import React, { useState, useEffect } from "react";
import {
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  TextField,
  Button,
  makeStyles,
  Checkbox,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(2),
    width: 200,
  },
  content: {
    marginTop: theme.spacing(3),
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  button: {
    margin: theme.spacing(1),
  },

  table: {
    minWidth: 650,
  },
  filterContainer: {
    marginBottom: theme.spacing(3),
  },

  csvTable: {
    width: "100%",
  },
  tableHeader: {
    backgroundColor: theme.palette.primary.main, // Use your primary color here
    '& .MuiTableCell-head': {
      color: 'white', // This will make the text white
    },
  },
}));

const LeftColumn = ({ onCreatorSelect }) => {
  const classes = useStyles();
  const [filter, setFilter] = useState("");
  const [region, setRegion] = useState(""); // Default to showing ALL Regions
  const [platform, setPlatform] = useState("TikTok"); // Default to TikTok Brand, and remove the option for None
  const [promotionType, setPromotionType] = useState("Brand"); // Assuming you want to start with TikTok Brand
  const [data, setData] = useState([]);
  const [race, setRace] = useState("");
  const [gender, setGender] = useState("");
  const [furtherLocation, setFurtherLocation] = useState("");
  const [selectedItems, setSelectedItems] = useState(new Set());
  const [avgViews, setAvgViews] = useState("");
  const [selectedCreatorsData, setSelectedCreatorsData] = useState([]);
;
  const [campaignSum, setCampaignSum] = useState(0);

  const handleResetFilters = () => {
    setRegion("");
    setPlatform("TikTok");
    setPromotionType("Brand");
    setRace("");
    setGender("");
    setFurtherLocation("");
    setFilter("");
    setAvgViews(""); // Reset this as well if you're including the AVG Views Filter
  };
  useEffect(() => {
    const sum = calculateTotalCampaignSum();
    setCampaignSum(sum);
    const fetchData = async () => {
    try {
      // Replace the URL with your server's endpoint that queries the PostgreSQL database
      const response = await fetch("http://localhost:5000/api/creators");
      if (!response.ok) throw new Error("Network response was not ok");
      const jsonData = await response.json();
      console.log(jsonData); // Add this line
      setData(jsonData);
          } catch (error) {
      console.error("Failed to fetch data:", error);
    }
  };
  fetchData();
}, [selectedCreatorsData]);

  const handleFilterChange = (e) => {
    setFilter(e.target.value.toLowerCase());
  };

  const handleRegionChange = (e) => {
    setRegion(e.target.value);
  };

  const platformToKey = {
    "TikTok": ["TikTok", "Tiktok Sound", "Tiktok Brand"],
    "Instagram": ["instagram", "IG Feed Post", "IG Reels Sound", "IG Reels Brand"],
    "Youtube": ["youtube"], // Add relevant keys for Youtube
  };

  const handlePlatformChange = (e) => {
    setPlatform(e.target.value);
    // Reset promotionType based on platform, defaulting to first available option or empty if none
    setPromotionType(promotionTypeOptions[e.target.value]?.[0] || '');
  };
  // Assuming each row in your data includes a unique identifier at index 0
  // Assuming the structure of your `data` array and the unique identifier is at index 0
  const handleSelectItem = (creatorId) => {
  // Toggle the selection state of the item.
  const isSelected = selectedItems.has(creatorId);
  if (isSelected) {
    selectedItems.delete(creatorId);
  } else {
    selectedItems.add(creatorId);
  }

  // Update the state with the new set of selected items.
  setSelectedItems(new Set(selectedItems));

  // Find the selected creator from the data array.
  const selectedCreator = data.find(creator => creator.creator === creatorId);
  if (!selectedCreator) {
    console.warn(`Item with ID ${creatorId} not found.`);
    return; // Early exit if the item isn't found.
  }

  // Prepare the data object for the selected item.
  // Dynamically determine the price based on the selected promotion type.
  const promotionPriceKey = promotionType + "Price"; // Assuming your data has keys like "SoundPrice", "BrandPrice" for prices
  const price = selectedCreator[promotionPriceKey] || "Price Unavailable";
  
  onCreatorSelect({
    id: selectedCreator.creatorId, // Ensure you have a unique ID for each creator
    name: selectedCreator.name, // Example property
    price: selectedCreator.price, // Example property
    // Add any other relevant properties you need
  });

  const relevantData = {
    id: creatorId,
    name: selectedCreator.creator, // Assuming the creator's name is stored under the "creator" key
    price: price, // Use the determined price for the selected promotion type
    // Include any other relevant data you might need from the selectedCreator object
  };

  // If item is newly selected, update the list of selected creator details.
  if (!isSelected) {
    setSelectedCreatorsData(prev => [...prev, relevantData]);
  } else {
    // If item is deselected, remove it from the list of selected creator details.
    setSelectedCreatorsData(prev => prev.filter(item => item.id !== creatorId));
  }
};

  // Utility function to update the state based on the item selection or deselection.
  const updateSelections = (uniqueId, isSelected) => {
    setSelectedItems(prev => {
      const updated = new Set(prev);
      if (isSelected) {
        updated.add(uniqueId);
      } else {
        updated.delete(uniqueId);
      }
      return updated;
    });

    setSelectedCreatorsData((prev) => {
      return isCurrentlySelected
        ? prev.filter((item) => item.id !== uniqueId)
        : [...prev, itemData];
    });

    // Invoke the callback with the item data if the item is newly selected.
    if (!isCurrentlySelected && onCreatorSelect) {
      onCreatorSelect(itemData);
    }
  };

  const calculateTotalCampaignSum = () => {
    const sum = selectedCreatorsData.reduce((accumulator, creator) => {
      // Remove any non-numeric characters (including the dollar sign) before conversion
      const cleanedPriceString = creator.price.replace(/[^0-9.-]+/g, "");
      const price = parseFloat(cleanedPriceString);
      // Check if the conversion was successful (i.e., the result is a finite number)
      if (isFinite(price)) {
        return accumulator + price;
      } else {
        console.error(`Invalid price format detected: ${creator.price}`);
        return accumulator;
      }
    }, 0);
    return sum;
  };

  const handlePromotionTypeChange = (e) => {
    setPromotionType(e.target.value);
  };
  const headerToKeyMap = {
    "Creator": "creator",
    "TikTok": "tiktok",
    "Instagram": "instagram",
    "Youtube": "youtube",
    "Geolocation/Gender/Ethnicity": "geolocationgenderethnicity",
    "Primary Market": "Primary Market",
    "Content Style": "NotesContent Style",
    "TikTok Sound": "Tiktok Sound",
    "TikTok Brand": "Tiktok Brand",
    "IG Feed Post": "IG Feed Post",
    "IG Reels Sound": "IG Reels Sound",
    "IG Reels Brand": "IG Reels Brand",
    "Instagram Link": "Instagram Link",
    "TikTok Link": "TikTok Link",
    "Youtube Link": "Youtube Link",
    "AVG Views": "AVG Views",
    "60s Integration": "60s Integration",
    "90s Integration": "90s Integration",
    // Add mappings for any other specific headers you have
};
  const promotionTypeOptions = {
    TikTok: ["Sound", "Brand", "Livestream"],
    Instagram: ["Sound", "Brand", "Story Set"],
    Youtube: ["30s Integration", "60s Integration", "Shorts"],
  };

  const headers = {
    TikTok: ["Creator", "TikTok", "Geolocation/Gender/Ethnicity", "Primary Market", "Content Style", "TikTok Sound", "TikTok Brand", "AVG Views"],
    Instagram: ["Creator", "Instagram", "Geolocation/Gender/Ethnicity", "Primary Market", "Content Style", "IG Feed Post", "IG Reels Sound", "IG Reels Brand", "AVG Views"],
    Youtube: ["Creator", "Youtube", "Geolocation/Gender/Ethnicity", "Primary Market", "Content Style", "60s Integration ", "90s Integration", "Shorts"],
  };
 
  let platformHeaders = headers[platform];
  if (promotionType === "Brand") {
    platformHeaders = platformHeaders.filter(header => !header.includes("Sound"));
  } else if (promotionType === "Sound") {
    platformHeaders = platformHeaders.filter(header => !header.includes("Brand"));
  }
  const filteredData = data.filter(creator => {
    // Exclude rows where platform or promotion type data is "N/A" or not present
    const platformKeys = platformToKey[platform];
    const isPlatformDataPresent = platformKeys.some(key => creator[headerToKeyMap[key]] && creator[headerToKeyMap[key]] !== "N/A");
    if (!isPlatformDataPresent) return false;

    // Exclude rows based on region and search filter
    const regionMatch = region ? creator.region?.toLowerCase().includes(region.toLowerCase()) : true;
    const raceGenderLocationMatch = creator.geolocationgenderethnicity?.toLowerCase().includes(filter.toLowerCase());

    return regionMatch && raceGenderLocationMatch;
  });
  const generateCellContent = (creator, header) => {
    const key = headerToKeyMap[header];
    return creator[key] ?? "N/A";
  };
  return (
    <div>
      <Grid container spacing={3}>
        <div className={classes.content}>
          <Grid item xs={12} className={classes.filterContainer}>
            <FormControl variant="outlined" className={classes.formControl}>
              <InputLabel id="region-select-label">Select Region</InputLabel>
              <Select
                labelId="region-select-label"
                id="region-select"
                value={region}
                onChange={handleRegionChange}
                label="Select Region"
              >
                <MenuItem value="">
                  <em>ALL</em>
                </MenuItem>
                <MenuItem value="NACAUKAUS">USA, Canada, UK, Australia</MenuItem>
                <MenuItem value="EU">EU</MenuItem>
                <MenuItem value="LATAM">LATAM</MenuItem>
                <MenuItem value="Asia">Asia</MenuItem>

                {/* More regions */}
              </Select>
            </FormControl>
            {/* Platform Dropdown */}
            <FormControl variant="outlined" className={classes.formControl}>
              <InputLabel id="platform-select-label">Platform</InputLabel>
              <Select
                labelId="platform-select-label"
                id="platform-select"
                value={platform}
                onChange={handlePlatformChange}
                label="Platform"
              >
                <MenuItem value="TikTok">TikTok</MenuItem>
                <MenuItem value="Instagram">Instagram</MenuItem>
                <MenuItem value="Youtube">Youtube</MenuItem>
                <MenuItem value="Twitter">Twitter</MenuItem>

              </Select>
            </FormControl>
            {/* Promotion Type Dropdown */}
            <FormControl variant="outlined" className={classes.formControl}>
              <InputLabel id="promotion-type-label">Promotion Type</InputLabel>
              <Select
                labelId="promotion-type-label"
                id="promotion-type-select"
                value={promotionType}
                onChange={handlePromotionTypeChange}
                label="Promotion Type"
              >
                {platform &&
                  promotionTypeOptions[platform].map((type) => (
                    <MenuItem key={type} value={type}>
                      {type}
                    </MenuItem>
                  ))}
              </Select>
            </FormControl>
            {/* Race Dropdown */}
            <FormControl variant="outlined" className={classes.formControl}>
              <InputLabel id="race-select-label">Race</InputLabel>
              <Select
                labelId="race-select-label"
                id="race-select"
                value={race}
                onChange={(e) => setRace(e.target.value)}
                label="Race"
              >
                <MenuItem value="">
                  <em>None</em>
                </MenuItem>
                <MenuItem value="Asian">Asian</MenuItem>
                <MenuItem value="Black">Black</MenuItem>
                <MenuItem value="Hispanic">Hispanic</MenuItem>
                <MenuItem value="White">White</MenuItem>
                <MenuItem value="Other">Other</MenuItem>
              </Select>
            </FormControl>
            {/* Gender Dropdown */}
            <FormControl variant="outlined" className={classes.formControl}>
              <InputLabel id="gender-select-label">Gender</InputLabel>
              <Select
                labelId="gender-select-label"
                id="gender-select"
                value={gender}
                onChange={(e) => setGender(e.target.value)}
                label="Gender"
              >
                <MenuItem value="">
                  <em>None</em>
                </MenuItem>
                <MenuItem value="Male">Male</MenuItem>
                <MenuItem value="Female">Female</MenuItem>
                <MenuItem value="Other">Other</MenuItem>
              </Select>
            </FormControl>
            {/* Further Location Dropdown */}
            <FormControl variant="outlined" className={classes.formControl}>
              <InputLabel id="further-location-select-label">
                Location
              </InputLabel>
              <Select
                labelId="further-location-select-label"
                id="further-location-select"
                value={furtherLocation}
                onChange={(e) => setFurtherLocation(e.target.value)}
                label="Further Location"
              >
                <MenuItem value="">
                  <em>None</em>
                </MenuItem>
                <MenuItem value="US">US</MenuItem>
                <MenuItem value="UK">UK</MenuItem>
                <MenuItem value="Canada">Canada</MenuItem>
              </Select>
            </FormControl>
            {/* For the 'Reset Filters' and 'AVG Views Filter' */}
            <FormControl
              variant="outlined"
              className={classes.formControl}
              fullWidth
            >
              <InputLabel id="avg-views-select-label">AVG Views</InputLabel>
              <Select
                labelId="avg-views-select-label"
                id="avg-views-select"
                value={avgViews}
                onChange={(e) => setAvgViews(e.target.value)}
                label="AVG Views"
              >
                {/* AVG Views MenuItems */}
                <MenuItem value="">
                  <em>None</em>
                </MenuItem>
                <MenuItem value="10-50000">10-50,000</MenuItem>
                <MenuItem value="50000-100000">50,000-100,000</MenuItem>
                <MenuItem value="100000+">100,000+</MenuItem>
              </Select>
            </FormControl>
            <TextField
              id="search-field"
              label="Search"
              variant="outlined"
              size="small"
              fullWidth
              className={classes.formControl}
              value={filter}
              onChange={handleFilterChange}
            />
          </Grid>
        </div>

        <Button
          variant="contained"
          color="primary"
          onClick={handleResetFilters}
          className={classes.button}
          fullWidth
        >
          Reset Filters
        </Button>
      </Grid>

      <div className={classes.csvSection}>
        <Paper elevation={3}>
          <Table className={classes.csvTable}>
          <TableHead className={classes.tableHeader}>
          <TableRow>
                <TableCell padding="checkbox">Select</TableCell> {/* Added "Select" header */}
                {platformHeaders.map((header) => (
                  <TableCell key={header}>{header}</TableCell>
                ))}
              </TableRow>
            </TableHead>
            <TableBody>
  {filteredData.map((creator, idx) => (
  <TableRow
    key={idx}
    style={{
      backgroundColor: selectedItems.has(creator.creator) ? "lightgreen" : "transparent",
    }}
    // Removed onClick from here to prevent row from toggling selection when clicking on Checkbox
  >
                 <TableCell padding="checkbox">
        <Checkbox
          checked={selectedItems.has(creator.creator)}
          onChange={(e) => {
            e.stopPropagation(); // Prevent event from bubbling to the row's onClick
            handleSelectItem(creator.creator);
          }}
          color="primary"
        />
                  </TableCell>
                  {platformHeaders.map(header => (
                    <TableCell key={header}>{generateCellContent(creator, header)}</TableCell> // Adjusted to use generateCellContent
                  ))}
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Paper>
      </div>
    </div>
  );
};

export default LeftColumn;
