// CreatorDialog.js
import React, { useState } from 'react';
import { Dialog, DialogActions, DialogContent, DialogTitle, Button, TextField, Checkbox, FormControlLabel } from '@material-ui/core';

const CreatorDialog = ({ open, onClose, onSubmit }) => {
  const [contactEmail, setContactEmail] = useState('');
  const [paymentEmail, setPaymentEmail] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [emailSame, setEmailSame] = useState(false);

  const handleEmailSameChange = (event) => {
    setEmailSame(event.target.checked);
    if (event.target.checked) {
      setPaymentEmail(contactEmail);
    }
  };

  const handleSubmit = () => {
    // Here, you would typically pass the data back to the parent component or handle the form submission.
    onSubmit({ contactEmail, paymentEmail, phoneNumber });
    onClose(); // Close the dialog after submission
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>Add Creators</DialogTitle>
      <DialogContent>
        <TextField
          margin="dense"
          label="Contact Email"
          type="email"
          fullWidth
          value={contactEmail}
          onChange={(e) => setContactEmail(e.target.value)}
          variant="outlined"
        />
        <TextField
          margin="dense"
          label="Payment Email"
          type="email"
          fullWidth
          disabled={emailSame}
          value={paymentEmail}
          onChange={(e) => setPaymentEmail(e.target.value)}
          variant="outlined"
        />
        <TextField
          margin="dense"
          label="Phone Number"
          type="tel"
          fullWidth
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
          variant="outlined"
        />
        <FormControlLabel
          control={<Checkbox checked={emailSame} onChange={handleEmailSameChange} />}
          label="Payment and contact email are the same"
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancel</Button>
        <Button onClick={handleSubmit} color="primary">Submit</Button>
      </DialogActions>
    </Dialog>
  );
};

export default CreatorDialog;
