import React, { useState, useEffect } from 'react';
import {
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  TextField,
  Button,
  makeStyles,
  Checkbox,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    width: '100%',
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  button: {
    margin: theme.spacing(1),
  },
  searchField: {
    margin: theme.spacing(1),
    minWidth: 120,
    width: '100%',
  },
  table: {
    minWidth: 650,
  },
  filterContainer: {
    marginBottom: theme.spacing(3),
  },
  buttonContainer: {
    display: 'flex',
    justifyContent: 'space-between',
    padding: theme.spacing(2),
  },
  csvTable: {
    width: '100%',
  },
}));

const LeftColumn = () => {
  const classes = useStyles();
  const [filter, setFilter] = useState('');
  const [region, setRegion] = useState('');
  const [platform, setPlatform] = useState('TikTok');
  const [promotionType, setPromotionType] = useState('Brand');
  const [data, setData] = useState([]);
  const [race, setRace] = useState('');
  const [gender, setGender] = useState('');
  const [furtherLocation, setFurtherLocation] = useState('');
  const [selectedItems, setSelectedItems] = useState(new Set());
  const [avgViews, setAvgViews] = useState('');

  const handleResetFilters = () => {
    setRegion('');
    setPlatform('TikTok');
    setPromotionType('Brand');
    setRace('');
    setGender('');
    setFurtherLocation('');
    setFilter('');
    setAvgViews('');
  };

  useEffect(() => {
    // Place your data fetching logic here if necessary
  }, []);

  const handleSelectItem = (itemId) => {
    const newSelection = new Set(selectedItems);
    if (newSelection.has(itemId)) {
      newSelection.delete(itemId);
    } else {
      newSelection.add(itemId);
    }
    setSelectedItems(newSelection);
  };

  return (
    <div>
      <Grid container spacing={3}>
        <Grid item xs={12} className={classes.filterContainer}>
        <div className={classes.content}>
          <Grid container spacing={2}>
            <Grid item xs={12} md={6}>
              {/* Filters container */}
              <Grid container spacing={2} className={classes.filterContainer}>
                <Grid container spacing={2}>
                  {/* Adjust the spacing as needed */} {/* Region Dropdown */}
                  <FormControl
                    variant="outlined"
                    className={classes.formControl}
                  >
                    <InputLabel id="region-select-label">
                      Select Region
                    </InputLabel>
                    <Select
                      labelId="region-select-label"
                      id="region-select"
                      value={region}
                      onChange={handleRegionChange}
                      label="Select Region"
                    >
                      <MenuItem value="">
                        <em>ALL</em>
                      </MenuItem>
                      <MenuItem value="NACAUKAUS">NACAUKAUS</MenuItem>
                      <MenuItem value="EU">EU</MenuItem>
                      <MenuItem value="LATAM">LATAM</MenuItem>
                      <MenuItem value="Asia">Asia</MenuItem>

                      {/* More regions */}
                    </Select>
                  </FormControl>
                  {/* Platform Dropdown */}
                  <FormControl
                    variant="outlined"
                    className={classes.formControl}
                  >
                    <InputLabel id="platform-select-label">Platform</InputLabel>
                    <Select
                      labelId="platform-select-label"
                      id="platform-select"
                      value={platform}
                      onChange={handlePlatformChange}
                      label="Platform"
                    >
                      <MenuItem value="TikTok">TikTok</MenuItem>
                      <MenuItem value="Instagram">Instagram</MenuItem>
                      {/*  <MenuItem value="Youtube">Youtube</MenuItem> */}

                      {/* More platforms */}
                    </Select>
                  </FormControl>
                  {/* Promotion Type Dropdown */}
                  <FormControl
                    variant="outlined"
                    className={classes.formControl}
                  >
                    <InputLabel id="promotion-type-label">
                      Promotion Type
                    </InputLabel>
                    <Select
                      labelId="promotion-type-label"
                      id="promotion-type-select"
                      value={promotionType}
                      onChange={handlePromotionTypeChange}
                      label="Promotion Type"
                    >
                      {platform &&
                        promotionTypeOptions[platform].map((type) => (
                          <MenuItem key={type} value={type}>
                            {type}
                          </MenuItem>
                        ))}
                    </Select>
                  </FormControl>
                  {/* Race Dropdown */}
                  <FormControl
                    variant="outlined"
                    className={classes.formControl}
                  >
                    <InputLabel id="race-select-label">Race</InputLabel>
                    <Select
                      labelId="race-select-label"
                      id="race-select"
                      value={race}
                      onChange={(e) => setRace(e.target.value)}
                      label="Race"
                    >
                      <MenuItem value="">
                        <em>None</em>
                      </MenuItem>
                      <MenuItem value="Asian">Asian</MenuItem>
                      <MenuItem value="Black">Black</MenuItem>
                      <MenuItem value="Hispanic">Hispanic</MenuItem>
                      <MenuItem value="White">White</MenuItem>
                      <MenuItem value="Other">Other</MenuItem>
                    </Select>
                  </FormControl>
                  {/* Gender Dropdown */}
                  <FormControl
                    variant="outlined"
                    className={classes.formControl}
                  >
                    <InputLabel id="gender-select-label">Gender</InputLabel>
                    <Select
                      labelId="gender-select-label"
                      id="gender-select"
                      value={gender}
                      onChange={(e) => setGender(e.target.value)}
                      label="Gender"
                    >
                      <MenuItem value="">
                        <em>None</em>
                      </MenuItem>
                      <MenuItem value="Male">Male</MenuItem>
                      <MenuItem value="Female">Female</MenuItem>
                      <MenuItem value="Other">Other</MenuItem>
                    </Select>
                  </FormControl>
                  {/* Further Location Dropdown */}
                  <FormControl
                    variant="outlined"
                    className={classes.formControl}
                  >
                    <InputLabel id="further-location-select-label">
                      Location
                    </InputLabel>
                    <Select
                      labelId="further-location-select-label"
                      id="further-location-select"
                      value={furtherLocation}
                      onChange={(e) => setFurtherLocation(e.target.value)}
                      label="Further Location"
                    >
                      <MenuItem value="">
                        <em>None</em>
                      </MenuItem>
                      <MenuItem value="US">US</MenuItem>
                      <MenuItem value="UK">UK</MenuItem>
                      <MenuItem value="Canada">Canada</MenuItem>
                    </Select>
                  </FormControl>
                  {/* For the 'Reset Filters' and 'AVG Views Filter' */}
                  <Grid item xs={12} sm={5} md={3} container spacing={2}>
                    <FormControl
                      variant="outlined"
                      className={classes.formControl}
                      fullWidth
                    >
                      <InputLabel id="avg-views-select-label">
                        AVG Views
                      </InputLabel>
                      <Select
                        labelId="avg-views-select-label"
                        id="avg-views-select"
                        value={avgViews}
                        onChange={(e) => setAvgViews(e.target.value)}
                        label="AVG Views"
                      >
                        {/* AVG Views MenuItems */}
                        <MenuItem value="">
                          <em>None</em>
                        </MenuItem>
                        <MenuItem value="10-50000">10-50,000</MenuItem>
                        <MenuItem value="50000-100000">50,000-100,000</MenuItem>
                        <MenuItem value="100000+">100,000+</MenuItem>
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={6} md={7}>
                    <TextField
                      id="search-field"
                      label="Search"
                      variant="outlined"
                      size="small"
                      fullWidth
                      className={classes.searchField}
                      value={filter}
                      onChange={handleFilterChange}
                    />
                  </Grid>
                </Grid>
              </Grid>
            </Grid>

            <Grid item xs={12} md={6}>
              {" "}
              {/* Buttons container */}
              <div className={classes.buttonContainer}>
                <Button
                  className={classes.button}
                  variant="contained"
                  color="primary"
                  onClick={handleResetFilters}
                >
                  Reset Filters
                </Button>
                <Button
                  className={classes.button}
                  variant="contained"
                  color="secondary"
                  onClick={handleResetFilters}
                >
                  Add Creators
                </Button>
              </div>
              
            </Grid>
          </Grid>
        </div>
         {/* Filters */}
          <FormControl variant="outlined" className={classes.formControl}>
            <InputLabel id="region-label">Region</InputLabel>
            <Select
              labelId="region-label"
              id="region-select"
              value={region}
              onChange={(e) => setRegion(e.target.value)}
              label="Region"
            >
              <MenuItem value=""><em>None</em></MenuItem>
              {/* Add regions here */}
            </Select>
          </FormControl>

          {/* Similar FormControl blocks for platform, promotionType, race, gender, furtherLocation */}

          <TextField
            id="search-field"
            label="Search"
            variant="outlined"
            className={classes.searchField}
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
            fullWidth
          />

          <Button
            variant="contained"
            color="primary"
            onClick={handleResetFilters}
            className={classes.button}
            fullWidth
          >
            Reset Filters
          </Button>
        </Grid>

        <div className={classes.csvSection}>
          {/* CSV Section */}
          <Paper elevation={3}>
            <table className="csvTable">
              <thead>
                <tr>
                  <th>Select</th>

                  {headers.map(
                    (header, index) =>
                      columnsToShow.includes(index + 1) && (
                        <th
                          key={index}
                          style={{ width: getColumnWidth(index) }}
                        >
                          {header}
                        </th>
                      ), // Apply dynamic width based on column index
                  )}
                </tr>
              </thead>
              <tbody>
                {filteredData.map((row, idx) => {
                  if (!row) return null; // Ensure row is not undefined

                  const isSelected = selectedItems.has(row[0]); // Check if the row is selected
                  return (
                    <tr
                      key={idx}
                      style={{
                        backgroundColor: isSelected
                          ? "lightgreen"
                          : "transparent",
                      }} // Highlight selected rows
                      onClick={() => handleSelectItem(row[0])} // Add row click handler
                    >
                      <td>
                        <Checkbox
                          checked={isSelected}
                          onChange={(e) => {
                            e.stopPropagation(); // Prevent row click event from firing
                            handleSelectItem(row[0]);
                          }}
                          color="primary"
                        />
                      </td>
                      {row.map((value, index) =>
                        columnsToShow.includes(index + 1) ? (
                          <td
                            key={index}
                            style={{ width: getColumnWidth(index) }}
                          >
                            {index === 14 ? (
                              <a
                                href={value}
                                target="_blank"
                                rel="noopener noreferrer"
                              >
                                {value}
                              </a>
                            ) : (
                              value ?? "N/A"
                            )}
                          </td>
                        ) : null,
                      )}
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </Paper>
        </div>
      </Grid>
    </div>
  );
};

export default LeftColumn;
