import * as React from 'react';
import Button from '../client/Button';
import Typography from '../client/Typography';
import ProductHeroLayout from './ProductHeroLayout';

const backgroundImage =
  'https://images.unsplash.com/photo-1547032175-7fc8c7bd15b3?auto=format&fit=crop&w=1400';

export default function Hero() {
  return (
    <ProductHeroLayout
      sxBackground={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundColor: '#7fc7d9', // Average color of the background image.
        backgroundPosition: 'center',
      }}
    >
      {/* Increase the network loading priority of the background image. */}
      <img
        style={{ display: 'none' }}
        src={backgroundImage}
        alt="increase priority"
      />
      <Typography color="inherit" align="center" variant="h2" marked="center">
        <span className="red-text">Blitz</span> your way into
        <br />
        <span className="red-underline"> inspiring global culture</span>
      </Typography>
      <Typography
        color="inherit"
        align="center"
        variant="h5"
        sx={{ mb: 4, mt: { xs: 4, sm: 10 } }}
      >
        Break free from the platforms of the past. Power the future of
        influencer marketing today
      </Typography>
      <Button
        color="secondary"
        variant="contained"
        size="large"
        component="a"
        style={{ cursor: 'pointer' }}
        sx={{ minWidth: 200 }}
      >
        Request Demo
      </Button>
      <Typography variant="body2" color="inherit" sx={{ mt: 2 }}>
        Read More
      </Typography>
    </ProductHeroLayout>
  );
}
