import React, { useState, useEffect } from 'react';
import './dashboard.css';
import Navbar from '../../../Components/Navbar/NavBar';
import { Button, Table, TableBody, TableCell, TableHead, TableRow, Select, MenuItem } from '@mui/material';
import axios from 'axios';

const Dashboard = () => {
  const [dataView, setDataView] = useState('campaigns');
  const [campaigns, setCampaigns] = useState([]);
  const [payouts, setPayouts] = useState([]);
  const [invoices, setInvoices] = useState([]);
  const [campaignStatusFilter, setCampaignStatusFilter] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const { data: campaignsData } = await axios.get('http://localhost:5000/api/campaigns');
        setCampaigns(campaignsData);
        const { data: payoutsData } = await axios.get('http://localhost:5000/api/payouts');
        setPayouts(payoutsData);
        const { data: invoicesData } = await axios.get('http://localhost:5000/api/invoices');
        setInvoices(invoicesData);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const filteredCampaigns = campaigns.filter(campaign => 
    campaignStatusFilter ? campaign.campaignstatus === campaignStatusFilter : true
  );

  const renderTableData = () => {
    switch (dataView) {
      case 'campaigns':
        return filteredCampaigns.map((campaign) => (
          <TableRow key={campaign.id}>
            <TableCell>{campaign.name}</TableCell>
            <TableCell>${campaign.campaign_sum}</TableCell>
            <TableCell>{campaign.creators.map(creator => creator.name).join(', ')}</TableCell>
            <TableCell>{campaign.campaignstatus}</TableCell>
          </TableRow>
        ));
      case 'payouts':
        return payouts.map((payout) => (
          <TableRow key={payout.payout_id}>
            <TableCell>{payout.creator_id}</TableCell>
            <TableCell>{payout.amount}</TableCell>
            <TableCell>{payout.payout_date}</TableCell>
            <TableCell>{payout.blitzpay}</TableCell>

            <TableCell>{payout.status}</TableCell>
          </TableRow>
        ));
        case 'invoices':
          // Ensure invoices is an array before mapping
          return Array.isArray(invoices) ? invoices.map((invoice) => (
            <TableRow key={invoice.id}>
              <TableCell>{invoice.id}</TableCell>
              <TableCell>${invoice.amount_due}</TableCell>
              <TableCell>{invoice.status}</TableCell>
            </TableRow>
          )) : null;
      default:
        return null;
    }
  };

  return (
    <div className="dashboard">
      <Navbar />
      <div className="content" style={{ marginLeft: '250px' }}>
        <Select
          value={dataView}
          onChange={(e) => setDataView(e.target.value)}
          displayEmpty
          inputProps={{ 'aria-label': 'Without label' }}
        >
          <MenuItem value="campaigns">Campaigns</MenuItem>
          <MenuItem value="payouts">Payouts</MenuItem>
          <MenuItem value="invoices">Invoices</MenuItem>
        </Select>

        {dataView === 'campaigns' && (
          <Select
            value={campaignStatusFilter}
            onChange={(e) => setCampaignStatusFilter(e.target.value)}
            displayEmpty
            inputProps={{ 'aria-label': 'Without label' }}
          >
            <MenuItem value="">All</MenuItem>
            <MenuItem value="Launched">Launched</MenuItem>
            <MenuItem value="Pending">Pending</MenuItem>
            <MenuItem value="Completed">Completed</MenuItem>
          </Select>
        )}

        <Table>
          <TableHead>
            <TableRow>
              {dataView === 'campaigns' && <>
                <TableCell style={{ color: "white" }}>Campaign Name</TableCell>
                <TableCell style={{ color: "white" }}>Total Budget</TableCell>
                <TableCell style={{ color: "white" }}>Creators Involved</TableCell>
                <TableCell style={{ color: "white" }}>Status</TableCell>
              </>}
              {dataView === 'payouts' && <>
                <TableCell style={{ color: "white" }}>Creator</TableCell>
                <TableCell style={{ color: "white" }}>Amount</TableCell>
                <TableCell style={{ color: "white" }}>Status</TableCell>
              </>}
              {dataView === 'invoices' && <>
                <TableCell style={{ color: "white" }}>ID</TableCell>
                <TableCell style={{ color: "white" }}>Amount Due</TableCell>
                <TableCell style={{ color: "white" }}>Status</TableCell>
              </>}
            </TableRow>
          </TableHead>
          <TableBody>
            {renderTableData()}
          </TableBody>
        </Table>
      </div>
    </div>
  );
};

export default Dashboard;
