import React, { useState, useEffect } from "react";
import Navbar from "../../Components/Navbar/NavBar";
import "./blitzpay.css";
import PayCreatorDialog from "./payCreator"; // Assuming PayCreatorDialog is in the same directory
import { Snackbar, Checkbox, Button } from "@material-ui/core";

const BlitzPay = () => {
  const [activeTab, setActiveTab] = useState("pending");
  const [filterStatus, setFilterStatus] = useState(""); // Added for filtering
  const [isPayCreatorDialogOpen, setIsPayCreatorDialogOpen] = useState(false);
  const [payouts, setPayouts] = useState([]);
  const [filteredPayouts, setFilteredPayouts] = useState([]); // Added to store filtered payouts
  const [selectedPayouts, setSelectedPayouts] = useState([]);
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState("")

  const fetchPayouts = () => {
    fetch("http://localhost:5000/api/payouts")
      .then((response) => response.json())
      .then((data) => setPayouts(data))
      .catch((error) => console.error("Error fetching payouts:", error));
  };

  // useEffect hook to load data on component mount
  useEffect(() => {
    fetchPayouts();
  }, []);

  useEffect(() => {
    // Filter payouts whenever the payouts list or filterStatus changes
    setFilteredPayouts(payouts.filter(payout => filterStatus === "" || payout.status === filterStatus));
  }, [payouts, filterStatus]);

  const handleTabClick = (tab) => {
    setActiveTab(tab);
    if (tab === "pay") setIsPayCreatorDialogOpen(true);
  };

  const handleDialogClose = () => setIsPayCreatorDialogOpen(false);

  const handleDialogSubmit = (submissionData) => {
    fetch("http://localhost:5000/api/payouts", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(submissionData),
    })
      .then((response) => response.json())
      .then(() => {
        handleDialogClose();
        fetchPayouts(); // Refresh data after creating a payout
      })
      .catch((error) => console.error("Error:", error));
  };

  const handleCheckboxChange = (event, payoutId) => {
    const currentSelection = new Set(selectedPayouts);
    if (event.target.checked) {
      currentSelection.add(payoutId.toString());
    } else {
      currentSelection.delete(payoutId.toString());
    }
    setSelectedPayouts([...currentSelection]);
  };

  const handleDeleteSelected = () => {
    fetch("http://localhost:5000/api/payouts/delete", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ payoutIds: selectedPayouts }),
    })
      .then((response) => response.json())
      .then(() => {
        setSnackbarMessage("Selected payouts have been successfully deleted.");
        setSnackbarOpen(true);
        fetchPayouts(); // Refresh data after deleting payouts
      })
      .catch((error) => {
        setSnackbarMessage(`Error deleting payouts: ${error.message}`);
        setSnackbarOpen(true);
      });
  };

  const handleCloseSnackbar = (event, reason) => {
    if (reason === "clickaway") {
      return;
    }
    setSnackbarOpen(false);
  };

  // Calculators
  const creatorsPaid = payouts.length;
  const amountInEscrow = payouts.reduce((acc, curr) => {
    if (curr.status === "Pending") {
      // Remove any currency symbols and commas from the amount
      // then convert it to a number before adding it to the accumulator.
      const sanitizedAmount = Number(
        curr.amount.toString().replace(/[$,]/g, ""),
      );
      return acc + sanitizedAmount;
    } else {
      return acc;
    }
  }, 0);
  const blitzPaysUsed = payouts.filter((payout) => payout.blitzpay).length;

  return (
    <div className="dashboard">
      <Navbar />
      <div className="content flex-row">
        <div className="left-column">
          <h1 className="header">Blitz Pay</h1>
          <div className="button-container">
          <FormControl style={{ marginBottom: "20px" }}>
            <InputLabel>Status Filter</InputLabel>
            <Select
              value={filterStatus}
              onChange={(e) => setFilterStatus(e.target.value)}
              displayEmpty
              inputProps={{ 'aria-label': 'Without label' }}
            >
              <MenuItem value="">
                <em>All</em>
              </MenuItem>
              <MenuItem value="Pending">Pending</MenuItem>
              <MenuItem value="Completed">Completed</MenuItem>
              <MenuItem value="Failed">Failed</MenuItem>
            </Select>
          </FormControl>
            <button className="btn" onClick={() => handleTabClick("pay")}>
              Pay a Creator
            </button>
            <button className="btn" onClick={handleDeleteSelected}>
              Delete Selected
            </button>{" "}
            {/* Delete Selected button */}
          </div>

          <div className="table-container">
            <table className="styled-table">
              <thead>
                <tr>
                  <th>Check</th>{" "}
                  {/* Updated to have a checkbox column header */}
                  <th>Creator Name</th>
                  <th>Campaign Name</th>
                  <th>Payout Date</th>
                  <th>Payment Status</th>
                  <th>BlitzPay</th>
                  <th>Amount</th>
                </tr>
              </thead>
              <tbody>
                {payouts.map((payout) => (
                  <tr key={payout.payout_id.toString()}>
                    <td>
                      <Checkbox
                        checked={selectedPayouts.includes(
                          payout.payout_id.toString(),
                        )}
                        onChange={(event) =>
                          handleCheckboxChange(event, payout.payout_id)
                        }
                        color="primary"
                      />
                    </td>
                    <td>{payout.creator_id}</td>
                    <td>{payout.campaignname || "Creator Payout"}</td>
                    <td>{payout.payout_date}</td>
                    <td>{payout.status}</td>
                    <td>{payout.blitzpay ? "true" : "false"}</td>
                    <td>${payout.amount}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
        <div className="right-column">
          <h2>Summary</h2>
          <p>Creators Paid: {creatorsPaid}</p>
          <p>Amount in Escrow: ${amountInEscrow}</p>
          <p>BlitzPays Used: {blitzPaysUsed}</p>
        </div>
      </div>
      {isPayCreatorDialogOpen && (
        <PayCreatorDialog
          open={isPayCreatorDialogOpen}
          onClose={handleDialogClose}
          onSubmit={handleDialogSubmit}
        />
      )}
      <Snackbar
        open={snackbarOpen}
        autoHideDuration={6000}
        onClose={handleCloseSnackbar}
        message={snackbarMessage}
        action={
          <Button color="inherit" size="small" onClick={handleCloseSnackbar}>
            Close
          </Button>
        }
      />
    </div>
  );
};

export default BlitzPay;
