import React, { useState, useEffect } from "react";
import {
  Container,
  Box,
  Typography,
  Table,
  TableBody,
  TextField,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Checkbox,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Button,
  Card,
  Grid,
  CardContent,
} from "@mui/material";
import Navbar from "../../../Components/Navbar/NavBar";
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from "@mui/material";

const Campaigns = () => {
  const [campaigns, setCampaigns] = useState([]);
  const [selectedCampaigns, setSelectedCampaigns] = useState([]);
  const [selectedAction, setSelectedAction] = useState("");
  const [openDialog, setOpenDialog] = useState(false); // State to handle dialog visibility
  const [dialogContent, setDialogContent] = useState({}); // State to store the content for the dialog
  const [creatorsToRemove, setCreatorsToRemove] = useState([]);

  const fetchCampaigns = async () => {
    const response = await fetch("http://localhost:5000/api/campaigns");
    if (!response.ok) {
      console.error("Failed to fetch campaigns");
      return;
    }
    const data = await response.json();
    setCampaigns(data);
  };

  const handleOpenDialog = (campaignData) => {
    setDialogContent(campaignData); // Assuming campaignData is the object with details about the campaign
    setOpenDialog(true);
  };

  // Function to handle closing the dialog
  const handleCloseDialog = () => {
    setOpenDialog(false);
  };
  const formatCampaignSum = (sum) => {
    const numericSum = parseFloat(sum.replace(/[^\d.]/g, "")); // Remove any non-numeric characters
    return `$${numericSum.toFixed(2).replace(/\B(?=(\d{3})+(?!\d))/g, ",")}`; // Format with commas and prefix with $
  };

  // Utility function to format proposal date
  const formatProposalDate = (dateString) => {
    const date = new Date(dateString);
    // Format the date as mm/dd/yy
    return `${(date.getMonth() + 1).toString().padStart(2, "0")}/${date.getDate().toString().padStart(2, "0")}/${date.getFullYear().toString().substr(-2)}`;
  };

  useEffect(() => {
    // Define the asynchronous function inside the useEffect hook
    async function fetchAndInitializeCampaigns() {
      try {
        const response = await fetch("http://localhost:5000/api/campaigns");
        if (!response.ok) {
          throw new Error(`Failed to fetch campaigns: ${response.statusText}`);
        }
        const campaigns = await response.json();
        // Add a 'status' property to each campaign object
        const initializedCampaigns = campaigns.map((campaign) => ({
          ...campaign,
          status: "Pending", // Assuming you want to set all fetched campaigns to "Pending"
        }));
        setCampaigns(initializedCampaigns);
      } catch (error) {
        console.error("Error fetching campaigns:", error);
      }
    }

    // Call the function
    fetchAndInitializeCampaigns();
  }, []); // Empty dependency array means this effect runs once on mount

  const handleActionChange = (event) => {
    setSelectedAction(event.target.value);
  };

  const handleCheckboxChange = (campaignId) => {
    const selectedIndex = selectedCampaigns.indexOf(campaignId);
    let newSelected = [];

    if (selectedIndex === -1) {
      newSelected = newSelected.concat(selectedCampaigns, campaignId);
    } else if (selectedIndex === 0) {
      newSelected = newSelected.concat(selectedCampaigns.slice(1));
    } else if (selectedIndex === selectedCampaigns.length - 1) {
      newSelected = newSelected.concat(selectedCampaigns.slice(0, -1));
    } else if (selectedIndex > 0) {
      newSelected = newSelected.concat(
        selectedCampaigns.slice(0, selectedIndex),
        selectedCampaigns.slice(selectedIndex + 1),
      );
    }

    setSelectedCampaigns(newSelected);
  };
  const handleRowDoubleClick = (campaign) => {
    setDialogContent(campaign);
    setOpenDialog(true);
  };

  const applyAction = async () => {
    if (selectedAction === "delete") {
      await fetch("http://localhost:5000/api/campaigns/delete", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ id: selectedCampaigns }), // Changed from campaignIds to id
      });

      fetchCampaigns(); // Refresh campaigns after deletion
    } else if (selectedAction === "launch" || selectedAction === "pause") {
      const status = selectedAction === "launch" ? "Launched" : "Paused";
      await fetch("http://localhost:5000/api/campaigns/updateStatus", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ campaignIds: selectedCampaigns, status }),
      });
      fetchCampaigns(); // Refresh campaigns after status update
    }
    setSelectedCampaigns([]); // Clear selection after action
  };
  const CampaignDetailDialog = () => {
    // Calculate total sum and number of creators
    const [newPrices, setNewPrices] = useState({}); // Hold new prices for creators    const [selectedCreatorForAction, setSelectedCreatorForAction] = useState(null);
    useState(null);
    const [newCampaignSum, setNewCampaignSum] = useState("");

    // Function to handle the selection of a creator for a specific action
    const handleActionSelection = (event) => {
      const action = event.target.value;
      setSelectedAction(action);
      // Additional logic to prepare for the action (e.g., set up for removal or price update)
    };
    // Calculate total sum and number of creatorsr
    const totalCampaignSum = dialogContent.creators
      ? dialogContent.creators.reduce((acc, creator) => {
          // Remove any characters that are not digits or a dot, then parse
          const price = parseFloat(
            (newPrices[creator.id] || creator.price || "0").replace(
              /[^\d.]/g,
              "",
            ),
          );
          return acc + price;
        }, 0)
      : 0;
    const totalCreators = dialogContent.creators
      ? dialogContent.creators.length
      : 0;

    // Function to stage creator removals without immediate effect
    // Toggle creator removal
    const toggleCreatorRemoval = (creatorId) => {
      setCreatorsToRemove((prevCreators) => {
        if (prevCreators.includes(creatorId)) {
          return prevCreators.filter((id) => id !== creatorId);
        } else {
          return [...prevCreators, creatorId];
        }
      });
    };

    // Update price function
    const updatePrice = (creatorId, event) => {
      const price = event.target.value; // Assuming you want to capture the value directly
      setNewPrices((prevPrices) => ({
        ...prevPrices,
        [creatorId]: price, // Ensure this captures the correct value format
      }));
    };

    const applyChanges = async () => {
      // Early exit if no action is selected or no modifications are specified
      if (selectedAction === "remove" && creatorsToRemove.length > 0) {
        try {
          const response = await fetch(
            "http://localhost:5000/api/campaigns/removeCreators",
            {
              method: "POST",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify({
                campaignId: dialogContent.id,
                creatorIds: creatorsToRemove,
              }),
            },
          );
          if (!response.ok) {
            // Handle error response
            const errorData = await response.json();
            console.error(`Failed to remove creators: ${errorData.message}`);
            return; // Exit if there is an error
          }
          // Success - Optionally, you can refresh the campaign data or clear the state
          setCreatorsToRemove([]);
          updateCampaignSum();
          fetchCampaigns(); // Refresh the campaigns to reflect the removal
        } catch (error) {
          console.error("Error removing creators:", error);
        }
      }

      // Inside your applyChanges function for the "counter" action
      else if (
        selectedAction === "counter" &&
        Object.keys(newPrices).length > 0
      ) {
        try {
          const response = await fetch(
            "http://localhost:5000/api/campaigns/updateCreatorPrices",
            {
              method: "POST",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify({
                campaignId: dialogContent.id, // Ensure this is an integer
                newPrices, // This should be an object mapping creator IDs to their new prices
              }),
            },
          );
          if (response.ok) {
            // After successful removal
            updateCampaignSum();
            fetchCampaigns(); // Refresh the campaign data
          }
          if (!response.ok) {
            const errorData = await response.json();
            throw new Error(
              `Failed to update creator prices: ${errorData.message}`,
            );
          }

          // If successful, reset the newPrices
          setNewPrices({});
        } catch (error) {
          console.error("Error updating prices:", error);
          // Handle error (e.g., show a message to the user)
        }
      }
      // Close the dialog regardless of outcome
      updateCampaignSum();

      handleCloseDialog();
      fetchCampaigns(); // Refresh the campaign data

    };
    const updateCampaignSum = async () => {
      try {
        const campaignSumToUpdate = totalCampaignSum; // Use the calculated totalCampaignSum
        const response = await fetch(
          "http://localhost:5000/api/campaigns/updateCampaignSum",
          {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
              campaignId: dialogContent.id,
              campaignSum: campaignSumToUpdate.toString(), // Convert to string if needed
            }),
          },
        );
        if (!response.ok) {
          throw new Error("Failed to update campaign sum");
        }
        alert("Campaign sum updated successfully");
        fetchCampaigns(); // Refresh the campaign list to reflect the updated sum
      } catch (error) {
        console.error("Error updating campaign sum:", error);
      }
    };

    function formatIdealDueDate(dateString) {
      const date = new Date(dateString);
      if (isNaN(date.getTime())) {
        return 'Invalid Date'; // Or any placeholder you prefer
      } else {
        return date.toLocaleDateString("en-US");
      }
    }
    

    return (
      <Dialog
        open={openDialog}
        onClose={handleCloseDialog}
        maxWidth="xl"
        fullWidth
      >
        <DialogTitle>
          <Box
            display="flex"
            justifyContent="space-between"
            alignItems="center"
          >
            <Typography variant="h6">
              Campaign Name: {dialogContent?.name}
            </Typography>

            <Box>
              <Select
                value={selectedAction} // Tracks the current selected action within the dialog
                displayEmpty
                onChange={handleActionSelection}
                style={{ marginLeft: "10px" }}
              >
                <MenuItem value="" disabled>
                  Actions
                </MenuItem>
                <MenuItem value="remove">Remove Creator</MenuItem>
                <MenuItem value="counter">Counter Offer Price</MenuItem>
              </Select>
              <Button
                variant="contained"
                color="success"
                style={{ marginLeft: "10px" }}

                onClick={() => {
                  /* Logic to generate report */
                }}
              >
                Add Creators
              </Button>
              <Button
                variant="contained"
                color="primary"
                style={{ marginLeft: "10px" }}

                onClick={() => {
                  /* Logic to generate report */
                }}
              >
                Generate Report
              </Button>
              <Button
                variant="contained"
                color="secondary"
                style={{ marginLeft: "10px" }}
                onClick={applyChanges}
              >
                Approve Changes
              </Button>
            </Box>
          </Box>
        </DialogTitle>
        <DialogContent>
          {dialogContent?.creators ? (
            <>
              <TableContainer component={Paper}>
                <Table aria-label="creator details">
                  <TableHead>
                    <TableRow>
                      <TableCell style={{ color: "white" }}>Select</TableCell>
                      <TableCell style={{ color: "white" }}>Name/ID</TableCell>
                      <TableCell style={{ color: "white" }}>Price</TableCell>
                      <TableCell style={{ color: "white" }}>
                        Offer Sent
                      </TableCell>
                      <TableCell style={{ color: "white" }}>
                        Assets Sent
                      </TableCell>
                      <TableCell style={{ color: "white" }}>
                        Approve Assets
                      </TableCell>
                      <TableCell style={{ color: "white" }}>
                        Live Link
                      </TableCell>
                      <TableCell style={{ color: "white" }}>
                        Post Date
                      </TableCell>
                      <TableCell style={{ color: "white" }}>
                        Total Views
                      </TableCell>
                      <TableCell style={{ color: "white" }}>
                        Total Engagement
                      </TableCell>
                      <TableCell style={{ color: "white" }}>Report</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {dialogContent.creators.map((creator, index) => (
                      <TableRow key={index}>
                        <TableCell padding="checkbox">
                          <Checkbox
                            checked={creatorsToRemove.includes(creator.id)}
                            onChange={() => toggleCreatorRemoval(creator.id)}
                            color="primary"
                          />
                        </TableCell>
                        <TableCell>{creator.name}</TableCell>
                        <TableCell>
                          {/* Render editable price field if counter offer is selected */}
                          {selectedAction === "counter" ? (
                            <TextField
                              type="text"
                              value={
                                newPrices[creator.id] ||
                                creator.price.toString()
                              }
                              onChange={(e) => updatePrice(creator.id, e)}
                              fullWidth
                            />
                          ) : (
                            creator.price
                          )}
                        </TableCell>
                        <TableCell>
                          {creator.offerSent ? "Yes" : "No"}
                        </TableCell>
                        <TableCell>
                          {creator.deliverablesSent ? creator.AssetLink : "No"}
                        </TableCell>
                        <TableCell>
                          <Checkbox checked={creator.assetsApproved} />
                        </TableCell>
                        <TableCell>
                          <TextField
                            variant="outlined"
                            size="small"
                            defaultValue={creator.liveLink}
                          />
                        </TableCell>
                        <TableCell>{creator.postDate}</TableCell>
                        <TableCell>{creator.totalViews}</TableCell>
                        <TableCell>{creator.totalEngagement}</TableCell>
                        <TableCell>{creator.campaignRecap}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
              <Box display="flex" justifyContent="space-between" marginTop={2}>
                <Typography>
                  Total Campaign Sum: ${totalCampaignSum.toFixed(2)}
                </Typography>
                <Typography>Total Creators: {totalCreators}</Typography>
              </Box>
            </>
          ) : (
            <Typography>No creator data available.</Typography>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Close</Button>
        </DialogActions>
      </Dialog>
    );
  };

  return (
    <Container>
      <Navbar />
      <Box sx={{ my: 4 }}>
        <Typography
          variant="h4"
          gutterBottom
          component="div"
          style={{ color: "#1976d2" }}
        >
          Campaigns
        </Typography>
        <Grid container spacing={2}>
          <Grid item xs={12} md={9}>
            {" "}
            {/* Adjusted from md={8} to md={9} for the main content */}
            <Box
              sx={{
                display: "flex",
                justifyContent: "flex-start",
                alignItems: "center",
                gap: 2,
                mb: 4,
              }}
            >
              <FormControl
                variant="outlined"
                size="small"
                sx={{ minWidth: 240 }}
              >
                <InputLabel id="action-type-label">Action Type</InputLabel>
                <Select
                  labelId="action-type-label"
                  value={selectedAction}
                  onChange={handleActionChange}
                  label="Action Type"
                >
                  <MenuItem value="edit">Edit</MenuItem>
                  <MenuItem value="delete">Delete</MenuItem>
                  <MenuItem value="launch">Launch/Activate</MenuItem>
                  <MenuItem value="pause">Pause</MenuItem>
                </Select>
              </FormControl>
              <Button variant="contained" color="primary" onClick={applyAction}>
                Apply
              </Button>
            </Box>
            <TableContainer component={Paper}>
              <Table aria-label="campaigns table" sx={{ minWidth: 650 }}>
                <TableHead style={{ backgroundColor: "black", color: "white" }}>
                  <TableRow>
                    <TableCell style={{ color: "white" }}>Select</TableCell>
                    <TableCell style={{ color: "white" }}>
                      Campaign Name
                    </TableCell>
                    <TableCell style={{ color: "white" }}>Brief</TableCell>
                    <TableCell style={{ color: "white" }}>
                      Campaign Sum
                    </TableCell>
                    <TableCell style={{ color: "white" }}>
                      Proposal Date
                    </TableCell>
                    <TableCell style={{ color: "white" }}>
                      Product Type
                    </TableCell>
                    <TableCell style={{ color: "white" }}>Creators</TableCell>
                    <TableCell style={{ color: "white" }}>Video Amount</TableCell>
                    <TableCell style={{ color: "white" }}>Ideal Due Date</TableCell>

                    <TableCell style={{ color: "white" }}>Status</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {campaigns.length > 0 ? (
                    campaigns.map((campaign) => (
                      <TableRow
                        key={campaign.id}
                        onDoubleClick={() => handleRowDoubleClick(campaign)}
                        hover
                      >
                        {" "}
                        <TableCell padding="checkbox">
                          <Checkbox
                            checked={
                              selectedCampaigns.indexOf(campaign.id) !== -1
                            }
                            onChange={() => handleCheckboxChange(campaign.id)}
                          />
                        </TableCell>
                        <TableCell
                          onClick={() => handleOpenDialog(campaign)}
                          style={{
                            cursor: "pointer",
                            textDecoration: "underline",
                          }}
                        >
                          {campaign.name}
                        </TableCell>
                        <TableCell>{campaign.brief}</TableCell>
                        <TableCell>
                          {formatCampaignSum(campaign.campaign_sum)}
                        </TableCell>
                        <TableCell>
                          {formatProposalDate(campaign.proposaldate)}
                        </TableCell>

                        <TableCell>{campaign.campaigntype}</TableCell>
                        <TableCell>
                          {campaign.creators
                            .map((creator) => creator.name)
                            .join(", ")}
                        </TableCell>
                        <TableCell>{campaign.videoamount}</TableCell>
                        <TableCell>{formatIdealDueDate(campaign.idealduedate)}</TableCell>


                        <TableCell>{campaign.campaignstatus}</TableCell>
                      </TableRow>
                    ))
                  ) : (
                    <TableRow>
                      <TableCell colSpan="8" align="center">
                        No campaigns available
                      </TableCell>
                    </TableRow>
                  )}
                </TableBody>
              </Table>
            </TableContainer>
          </Grid>
          <Grid item xs={12} md={3}>
            {" "}
            {/* Adjusted from md={4} to md={3} for the summary */}
            <Card>
              <CardContent>
                <Typography variant="h5" component="div">
                  Summary
                </Typography>
                <Typography color="text.secondary">
                  Total Campaigns: {campaigns.length}
                </Typography>
                <Typography color="text.secondary">
                  Active Campaigns:{" "}
                  {
                    campaigns.filter(
                      (campaign) => campaign.campaignstatus === "Launched",
                    ).length
                  }
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      </Box>
      <CampaignDetailDialog />
    </Container>
  );
};

export default Campaigns;
