import React, { useState, useEffect } from "react";
import {
  Container,
  Box,
  Typography,
  Table,
  TableBody,
  TextField,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Checkbox,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Button,
  Card,
  Grid,
  CardContent,
} from "@mui/material";
import Navbar from "../../../Components/Navbar/NavBar";
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from "@mui/material";
import RecentCampaign from "./recentCampaigns";
import { useNavigate } from "react-router-dom"; // Use useNavigate instead of useHistory
import PoNumberDialog from './addPOPopup'; // Adjust the path as necessary

const Campaigns = () => {
  const [campaigns, setCampaigns] = useState([]);
  const [selectedCampaigns, setSelectedCampaigns] = useState([]);
  const [selectedAction, setSelectedAction] = useState("");
  const [openDialog, setOpenDialog] = useState(false);
  const [dialogContent, setDialogContent] = useState({});
  const [creatorsToRemove, setCreatorsToRemove] = useState([]);
  const [liveLinks, setLiveLinks] = useState({});
  const [boostCodes, setBoostCodes] = useState({});
  const [openPoDialog, setOpenPoDialog] = useState(false);
  const [poNumber, setPoNumber] = useState('');

  const navigate = useNavigate();

  const handleAddCreatorsClick = (campaignId) => {
    navigate(`/add-creators/${campaignId}`);
  };
  const fetchCampaigns = async () => {
    const response = await fetch("http://localhost:5000/api/campaigns");
    if (!response.ok) {
      console.error("Failed to fetch campaigns");
      return;
    }
    const data = await response.json();
    setCampaigns(data);
  };

  
 const handleCompleteCampaign = async () => {
  // Check if campaignId and poNumber are available
  if (!dialogContent.id) {
    alert("Campaign ID is missing");
    return;
  }
  const promptForPoNumber = () => {
    setOpenPoDialog(true);
  };
  // Assuming `poNumber` state exists and holds the PO number value
  if (!poNumber) {
    alert("PO Number is missing");
    return;
  }

  try {
    const response = await fetch(`http://localhost:5000/api/campaigns/completeCampaign/${dialogContent.id}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ poNumber }), // Include poNumber in the body of the POST request
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(`Failed to complete campaign: ${errorData.message || response.statusText}`);
    }

    // Assuming the response includes some JSON data you might want to check or log
    const responseData = await response.json();
    console.log(responseData);
    alert("Campaign completed successfully, PO number updated.");

    // Optionally, refresh your campaigns data or update UI accordingly
    fetchCampaigns();

  } catch (error) {
    console.error("Error completing campaign:", error);
    alert(`Error: ${error.message}`);
  }
};

  
  const handleGenerateReport = async () => {
    const campaignId = dialogContent.id; // Ensure you have the campaignId correctly obtained from your dialog content
    const creatorLinks = dialogContent.creators
      .map((creator) => creator.liveLink)
      .filter((link) => link);

    try {
      const response = await fetch(
        "http://localhost:5000/api/campaigns/generate_report",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ campaignId, creatorLinks }), // Ensure both campaignId and creatorLinks are included
        },
      );

      if (!response.ok) {
        throw new Error("Failed to generate report");
      }

      const reportData = await response.json();
      console.log(reportData); // For now, just logging the report data. You can update the UI based on this data.
      alert("Report generated successfully. Check the console for details.");
    } catch (error) {
      console.error("Error generating report:", error);
      alert("Failed to generate report.");
    }
  };

  const handleOpenDialog = (campaignData) => {
    setDialogContent(campaignData); // Assuming campaignData is the object with details about the campaign
    setOpenDialog(true);
  };

  // Function to handle closing the dialog
  const handleCloseDialog = () => {
    setOpenDialog(false);
  };
  const formatCampaignSum = (sum) => {
    const numericSum = parseFloat(sum.replace(/[^\d.]/g, "")); // Remove any non-numeric characters
    return `$${numericSum.toFixed(2).replace(/\B(?=(\d{3})+(?!\d))/g, ",")}`; // Format with commas and prefix with $
  };

  // Utility function to format proposal date
  const formatProposalDate = (dateString) => {
    const date = new Date(dateString);
    // Format the date as mm/dd/yy
    return `${(date.getMonth() + 1).toString().padStart(2, "0")}/${date.getDate().toString().padStart(2, "0")}/${date.getFullYear().toString().substr(-2)}`;
  };
  const promptForPoNumberAndCompleteCampaign = () => {
    if (!dialogContent.id) {
      alert("Campaign ID is missing");
      return;
    }
    // Open the PO Number Dialog
    setOpenPoDialog(true);
  };

  // Function to call after a PO number is provided in the dialog
  const completeCampaignWithPo = async (poNumber) => {
    try {
      const response = await fetch(`http://localhost:5000/api/campaigns/completeCampaign/${dialogContent.id}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ poNumber }), // Send the provided PO number in the request body
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`Failed to complete campaign: ${errorData.message || response.statusText}`);
      }

      // Handle successful campaign completion
      alert("Campaign completed successfully, PO number updated.");
      fetchCampaigns(); // Refresh the campaigns list or similar action
      setOpenPoDialog(false); // Close the PO Number Dialog
    } catch (error) {
      console.error("Error completing campaign:", error);
      alert(`Error: ${error.message}`);
      setOpenPoDialog(false); // Ensure the dialog is closed even if there's an error
    }
  };
  useEffect(() => {
    // Define the asynchronous function inside the useEffect hook
    async function fetchAndInitializeCampaigns() {
      try {
        const response = await fetch("http://localhost:5000/api/campaigns");
        if (!response.ok) {
          throw new Error(`Failed to fetch campaigns: ${response.statusText}`);
        }
        const campaigns = await response.json();
        // Add a 'status' property to each campaign object
        const initializedCampaigns = campaigns.map((campaign) => ({
          ...campaign,
          status: "Pending", // Assuming you want to set all fetched campaigns to "Pending"
        }));
        setCampaigns(initializedCampaigns);
      } catch (error) {
        console.error("Error fetching campaigns:", error);
      }
    }

    // Call the function
    fetchAndInitializeCampaigns();
  }, []); // Empty dependency array means this effect runs once on mount

  const handleActionChange = (event) => {
    setSelectedAction(event.target.value);
  };

  const handleCheckboxChange = (campaignId) => {
    const selectedIndex = selectedCampaigns.indexOf(campaignId);
    let newSelected = [];

    if (selectedIndex === -1) {
      newSelected = newSelected.concat(selectedCampaigns, campaignId);
    } else if (selectedIndex === 0) {
      newSelected = newSelected.concat(selectedCampaigns.slice(1));
    } else if (selectedIndex === selectedCampaigns.length - 1) {
      newSelected = newSelected.concat(selectedCampaigns.slice(0, -1));
    } else if (selectedIndex > 0) {
      newSelected = newSelected.concat(
        selectedCampaigns.slice(0, selectedIndex),
        selectedCampaigns.slice(selectedIndex + 1),
      );
    }

    setSelectedCampaigns(newSelected);
  };
  const handleRowDoubleClick = (campaign) => {
    setDialogContent(campaign);
    setOpenDialog(true);
  };

  const applyAction = async () => {
    if (selectedAction === "delete") {
      await fetch("http://localhost:5000/api/campaigns/delete", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ id: selectedCampaigns }), // Changed from campaignIds to id
      });

      fetchCampaigns(); // Refresh campaigns after deletion
    } else if (selectedAction === "launch" || selectedAction === "pause") {
      const status = selectedAction === "launch" ? "Launched" : "Paused";
      await fetch("http://localhost:5000/api/campaigns/updateStatus", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ campaignIds: selectedCampaigns, status }),
      });
      fetchCampaigns(); // Refresh campaigns after status update
    }
    setSelectedCampaigns([]); // Clear selection after action
  };
  function formatIdealDueDate(dateString) {
    const date = new Date(dateString);
    if (isNaN(date.getTime())) {
      return "Invalid Date"; // Or any placeholder you prefer
    } else {
      // Ensure timezone does not affect the output, consider formatting like so:
      return date.toISOString().substring(0, 10); // Returns "YYYY-MM-DD"
    }
  }

  const CampaignDetailDialog = () => {
    // Calculate total sum and number of creators
    const [newPrices, setNewPrices] = useState({}); // Hold new prices for creators    const [selectedCreatorForAction, setSelectedCreatorForAction] = useState(null);
    useState(null);
    
    // Function to handle the selection of a creator for a specific action
    const handleActionSelection = (event) => {
      const action = event.target.value;
      setSelectedAction(action);
      // Additional logic to prepare for the action (e.g., set up for removal or price update)
    };
    // Calculate total sum and number of creatorsr
    const totalCampaignSum = dialogContent.creators
      ? dialogContent.creators.reduce((acc, creator) => {
          // Remove any characters that are not digits or a dot, then parse
          const price = parseFloat(
            (newPrices[creator.id] || creator.price || "0").replace(
              /[^\d.]/g,
              "",
            ),
          );
          return acc + price;
        }, 0)
      : 0;
    const totalCreators = dialogContent.creators
      ? dialogContent.creators.length
      : 0;

    // Function to stage creator removals without immediate effect
    // Toggle creator removal
    const toggleCreatorRemoval = (creatorId) => {
      setCreatorsToRemove((prevCreators) => {
        if (prevCreators.includes(creatorId)) {
          return prevCreators.filter((id) => id !== creatorId);
        } else {
          return [...prevCreators, creatorId];
        }
      });
    };

    // Update price function
    const updatePrice = (creatorId, event) => {
      const price = event.target.value; // Assuming you want to capture the value directly
      setNewPrices((prevPrices) => ({
        ...prevPrices,
        [creatorId]: price, // Ensure this captures the correct value format
      }));
    };

    const applyChanges = async () => {
      let changesMade = false; // Flag to track if any changes have been made

      // Early exit if no action is selected or no modifications are specified
      if (selectedAction === "remove" && creatorsToRemove.length > 0) {
        try {
          const response = await fetch(
            "http://localhost:5000/api/campaigns/removeCreators",
            {
              method: "POST",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify({
                campaignId: dialogContent.id,
                creatorIds: creatorsToRemove,
              }),
            },
          );
          if (!response.ok) {
            // Handle error response
            const errorData = await response.json();
            console.error(`Failed to remove creators: ${errorData.message}`);
            return; // Exit if there is an error
          }
          // Success - Optionally, you can refresh the campaign data or clear the state
          setCreatorsToRemove([]);
          updateCampaignSum();
          fetchCampaigns(); // Refresh the campaigns to reflect the removal
        } catch (error) {
          console.error("Error removing creators:", error);
        }
      }

      // Inside your applyChanges function for the "counter" action
      if (selectedAction === "counter" && Object.keys(newPrices).length > 0) {
        try {
          const response = await fetch(
            "http://localhost:5000/api/campaigns/updateCreatorPrices",
            {
              method: "POST",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify({
                campaignId: dialogContent.id, // Ensure this is an integer
                newPrices, // This should be an object mapping creator IDs to their new prices
              }),
            },
          );
          if (response.ok) {
            // After successful removal
            updateCampaignSum();
            fetchCampaigns(); // Refresh the campaign data
          }
          if (!response.ok) {
            const errorData = await response.json();
            throw new Error(
              `Failed to update creator prices: ${errorData.message}`,
            );
          }

          // If successful, reset the newPrices
          setNewPrices({});
        } catch (error) {
          console.error("Error updating prices:", error);
          // Handle error (e.g., show a message to the user)
        }
      }
      // Close the dialog regardless of outcome
      updateCampaignSum();
      updateLiveLinksAndBoostCodes();
      handleCloseDialog();
      fetchCampaigns(); // Refresh the campaign data
    };

    const updateCampaignSum = async () => {
      try {
        const campaignSumToUpdate = totalCampaignSum; // Use the calculated totalCampaignSum
        const response = await fetch(
          "http://localhost:5000/api/campaigns/updateCampaignSum",
          {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
              campaignId: dialogContent.id,
              campaignSum: campaignSumToUpdate.toString(), // Convert to string if needed
            }),
          },
        );
        if (!response.ok) {
          throw new Error("Failed to update campaign sum");
        }
        alert("Campaign sum updated successfully");
        fetchCampaigns(); // Refresh the campaign list to reflect the updated sum
      } catch (error) {
        console.error("Error updating campaign sum:", error);
      }
    };

    const updateLiveLinksAndBoostCodes = async () => {
      // Constructing the updates payload
      const updates = dialogContent.creators
        .map((creator) => ({
          id: creator.id,
          liveLink: liveLinks[creator.id] || "",
          boostCode: boostCodes[creator.id] || "",
        }))
        .filter((update) => update.liveLink || update.boostCode); // Filter out entries without updates

      if (updates.length === 0) {
        alert("No updates to apply");
        return false; // Early exit if no updates
      }

      try {
        const response = await fetch(
          "http://localhost:5000/api/campaigns/updateLinksAndCodes",
          {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
              campaignId: dialogContent.id, // Assuming `dialogContent` holds the current campaign's data
              updates: updates,
            }),
          },
        );

        if (!response.ok)
          throw new Error("Failed to update live links and boost codes");

        // Success handling
        alert("Live links and boost codes updated successfully");
        setLiveLinks({});
        setBoostCodes({});
        // You might want to refresh or update the UI accordingly here

        return true; // Indicate success
      } catch (error) {
        console.error("Error updating live links and boost codes:", error);
        alert("Failed to update live links and boost codes");
        return false; // Indicate failure
      }
    };

    return (
      <Dialog
        open={openDialog}
        onClose={handleCloseDialog}
        maxWidth="xl"
        fullWidth
      >
        <DialogTitle>
          <Box
            display="flex"
            justifyContent="space-between"
            alignItems="center"
          >
            <Typography variant="h6">
              Campaign Name: {dialogContent?.name}
            </Typography>
            <Typography variant="h6">Ideal Due Date:</Typography>
            <Box>
              <Select
                value={selectedAction} // Tracks the current selected action within the dialog
                displayEmpty
                onChange={handleActionSelection}
                style={{ marginLeft: "10px" }}
              >
                <MenuItem value="" disabled>
                  Actions
                </MenuItem>
                <MenuItem value="remove">Remove Creator</MenuItem>
                <MenuItem value="counter">Counter Offer Price</MenuItem>
              </Select>
              <Button
                variant="contained"
                color="success"
                style={{ marginLeft: "10px" }}
                onClick={() => handleAddCreatorsClick(dialogContent?.id)} // Pass the current dialog's campaign ID
              >
                Add Creators
              </Button>
              <Button
                variant="contained"
                color="primary"
                style={{ marginLeft: "10px" }}
                onClick={handleGenerateReport}
              >
                Generate Report
              </Button>

              <Button
                variant="contained"
                color="secondary"
                style={{ marginLeft: "10px" }}
                onClick={applyChanges}
              >
                Approve Changes
              </Button>

              <Button
                variant="contained"
                color="warning"
                style={{ marginLeft: "10px" }}
                onClick={handleCompleteCampaign}
              >
                Complete Campaign
              </Button>
            </Box>
          </Box>
        </DialogTitle>
        <DialogContent>
          {dialogContent?.creators ? (
            <>
              <TableContainer component={Paper}>
                <Table aria-label="creator details">
                  <TableHead>
                    <TableRow>
                      <TableCell style={{ color: "white" }}>Select</TableCell>
                      <TableCell style={{ color: "white" }}>Name/ID</TableCell>
                      <TableCell style={{ color: "white" }}>Price</TableCell>
                      <TableCell style={{ color: "white" }}>
                        Offer Sent
                      </TableCell>
                      <TableCell style={{ color: "white" }}>
                        Assets Sent
                      </TableCell>
                      <TableCell style={{ color: "white" }}>
                        Approve Assets
                      </TableCell>
                      <TableCell style={{ color: "white" }}>
                        Live Link
                      </TableCell>
                      <TableCell style={{ color: "white" }}>
                        Usage or Boost Code
                      </TableCell>
                      <TableCell style={{ color: "white" }}>
                        Post Date
                      </TableCell>
                      <TableCell style={{ color: "white" }}>
                        Total Views
                      </TableCell>
                      <TableCell style={{ color: "white" }}>
                        Total Likes
                      </TableCell>
                      <TableCell style={{ color: "white" }}>
                        Total Comments
                      </TableCell>
                      <TableCell style={{ color: "white" }}>
                        Total Engagement
                      </TableCell>
                      <TableCell style={{ color: "white" }}>Report</TableCell>
                      <TableCell style={{ color: "white" }}>Status</TableCell>

                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {dialogContent.creators.map((creator, index) => (
                      <TableRow key={index}>
                        <TableCell padding="checkbox">
                          <Checkbox
                            checked={creatorsToRemove.includes(creator.id)}
                            onChange={() => toggleCreatorRemoval(creator.id)}
                            color="primary"
                          />
                        </TableCell>
                        <TableCell>{creator.name}</TableCell>
                        <TableCell>
                          {/* Render editable price field if counter offer is selected */}
                          {selectedAction === "counter" ? (
                            <TextField
                              type="text"
                              value={
                                newPrices[creator.id] ||
                                creator.price.toString()
                              }
                              onChange={(e) => updatePrice(creator.id, e)}
                              fullWidth
                            />
                          ) : (
                            creator.price
                          )}
                        </TableCell>
                        <TableCell>
                          {creator.offerSent ? "Yes" : "No"}
                        </TableCell>
                        <TableCell>
                          {creator.deliverablesSent ? creator.AssetLink : "No"}
                        </TableCell>
                        <TableCell>
                          <Checkbox checked={creator.assetsApproved} />
                        </TableCell>
                        <TableCell>
                          <TextField
                            variant="outlined"
                            size="small"
                            placeholder="Live Link"
                            value={
                              liveLinks[creator.id] !== undefined
                                ? liveLinks[creator.id]
                                : creator.liveLink || ""
                            }
                            onChange={(e) =>
                              setLiveLinks({
                                ...liveLinks,
                                [creator.id]: e.target.value,
                              })
                            }
                          />
                        </TableCell>
                        <TableCell>
                          <TextField
                            variant="outlined"
                            size="small"
                            placeholder="Boost Code"
                            value={
                              boostCodes[creator.id] !== undefined
                                ? boostCodes[creator.id]
                                : creator.boostCode || ""
                            }
                            onChange={(e) =>
                              setBoostCodes({
                                ...boostCodes,
                                [creator.id]: e.target.value,
                              })
                            }
                          />
                        </TableCell>
                        <TableCell>{creator.postDate}</TableCell>
                        <TableCell>{creator.totalViews}</TableCell>
                        <TableCell>{creator.Likes}</TableCell>
                        <TableCell>{creator.Comments}</TableCell>

                        <TableCell>{creator.totalEngagement}</TableCell>
                        <TableCell>{creator.campaignRecap}</TableCell>
                        <TableCell>{creator.status}</TableCell>

                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
              <Box display="flex" justifyContent="space-between" marginTop={2}>
                <Typography>
                  Total Campaign Sum: ${totalCampaignSum.toFixed(2)}
                </Typography>
                <Typography>Total Creators: {totalCreators}</Typography>
              </Box>
            </>
          ) : (
            <Typography>No creator data available.</Typography>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Close</Button>
        </DialogActions>
      </Dialog>
    );
  };
  const sortedCampaigns = campaigns.sort(
    (a, b) => new Date(b.proposaldate) - new Date(a.proposaldate),
  );
  const handleSelectCampaign = (campaign) => {
    setDialogContent(campaign); // Set the selected campaign as dialog content
    setOpenDialog(true); // Open the dialog
  };

  return (
    <Container>
      <Navbar />
      <Box sx={{ my: 4 }}>
        <Typography
          variant="h4"
          gutterBottom
          component="div"
          style={{ color: "#1976d2" }}
        >
          Campaigns
        </Typography>
        <Grid container spacing={2}>
          <Grid container spacing={2}></Grid>
          <Grid container spacing={2}>
  {sortedCampaigns.slice(0, 3).map((campaign) => (
    <Grid item xs={12} md={6} key={campaign.id}>
<RecentCampaign campaign={campaign} onSelectCampaign={handleSelectCampaign} />
    </Grid>
  ))}

            {/* Summary block and other content... */}

            {/* Summary Block */}
            <Grid item xs={12} md={6}>
              <Card elevation={3} sx={{ minWidth: 275, margin: 2 }}>
              <CardContent onDoubleClick={() => onSelectCampaign(campaign)}>
                  <Typography variant="h5" component="div" gutterBottom>
                    Summary
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Total Campaigns: {campaigns.length}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Active Campaigns:{" "}
                    {
                      campaigns.filter(
                        (campaign) => campaign.campaignstatus === "Launched",
                      ).length
                    }
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Total Budget: $
                    {campaigns
                      .reduce(
                        (sum, campaign) =>
                          sum + Number(campaign.campaign_sum || 0),
                        0,
                      )
                      .toLocaleString()}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Total Posts Up:{" "}
                    {campaigns.reduce(
                      (sum, campaign) =>
                        sum +
                        campaign.creators.filter((creator) => creator.liveLink)
                          .length,
                      0,
                    )}
                  </Typography>
                  {/* Assuming each campaign should have a fixed number of posts (e.g., 5) */}
                  <Typography variant="body2" color="text.secondary">
                    Total Posts Remaining:{" "}
                    {/* Calculation would depend on your data structure */}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Total Views:{" "}
                    {campaigns
                      .reduce(
                        (sum, campaign) =>
                          sum +
                          campaign.creators.reduce(
                            (cSum, creator) =>
                              cSum + Number(creator.totalViews || 0),
                            0,
                          ),
                        0,
                      )
                      .toLocaleString()}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Total Engagement:{" "}
                    {campaigns
                      .reduce(
                        (sum, campaign) =>
                          sum +
                          campaign.creators.reduce(
                            (cSum, creator) =>
                              cSum + Number(creator.totalEngagement || 0),
                            0,
                          ),
                        0,
                      )
                      .toLocaleString()}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
          <Grid item xs={12} md={12} sx={{ paddingRight: "2%" }}>
            {" "}
            {/* Allocate 80% width and add padding */}{" "}
            {/* Adjusted from md={8} to md={9} for the main content */}
            <Box
              sx={{
                display: "flex",
                justifyContent: "flex-start",
                alignItems: "center",
                gap: 2,
                mb: 4,
              }}
            >
              <FormControl
                variant="outlined"
                size="small"
                sx={{ minWidth: 240 }}
              >
                <InputLabel id="action-type-label">Action Type</InputLabel>
                <Select
                  labelId="action-type-label"
                  value={selectedAction}
                  onChange={handleActionChange}
                  label="Action Type"
                >
                  <MenuItem value="edit">Edit</MenuItem>
                  <MenuItem value="delete">Delete</MenuItem>
                  <MenuItem value="launch">Launch/Activate</MenuItem>
                  <MenuItem value="pause">Pause</MenuItem>
                </Select>
              </FormControl>
              <Button variant="contained" color="primary" onClick={applyAction}>
                Apply
              </Button>
            </Box>
            <TableContainer component={Paper}>
              <Table aria-label="campaigns table" sx={{ minWidth: 650 }}>
                <TableHead style={{ backgroundColor: "black", color: "white" }}>
                  <TableRow>
                    <TableCell style={{ color: "white" }}>Select</TableCell>
                    <TableCell style={{ color: "white" }}>
                      Campaign Name
                    </TableCell>
                    <TableCell style={{ color: "white" }}>Brief</TableCell>
                    <TableCell style={{ color: "white" }}>
                      Campaign Sum
                    </TableCell>
                    <TableCell style={{ color: "white" }}>
                      Proposal Date
                    </TableCell>
                    <TableCell style={{ color: "white" }}>
                      Product Type
                    </TableCell>
                    <TableCell style={{ color: "white" }}>Creators</TableCell>
                    <TableCell style={{ color: "white" }}>
                      Video Amount
                    </TableCell>
                    <TableCell style={{ color: "white" }}>
                      Ideal Due Date
                    </TableCell>

                    <TableCell style={{ color: "white" }}>Status</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {campaigns.length > 0 ? (
                    campaigns.map((campaign) => (
                      <TableRow
                        key={campaign.id}
                        onDoubleClick={() => handleRowDoubleClick(campaign)}
                        hover
                      >
                        {" "}
                        <TableCell padding="checkbox">
                          <Checkbox
                            checked={
                              selectedCampaigns.indexOf(campaign.id) !== -1
                            }
                            onChange={() => handleCheckboxChange(campaign.id)}
                          />
                        </TableCell>
                        <TableCell
                          onClick={() => handleOpenDialog(campaign)}
                          style={{
                            cursor: "pointer",
                            textDecoration: "underline",
                          }}
                        >
                          {campaign.name}
                        </TableCell>
                        <TableCell>{campaign.brief}</TableCell>
                        <TableCell>
                          {formatCampaignSum(campaign.campaign_sum)}
                        </TableCell>
                        <TableCell>
                          {formatProposalDate(campaign.proposaldate)}
                        </TableCell>
                        <TableCell>{campaign.campaigntype}</TableCell>
                        <TableCell>
                          {campaign.creators
                            .map((creator) => creator.name)
                            .join(", ")}
                        </TableCell>
                        <TableCell>{campaign.videoamount}</TableCell>
                        <TableCell>
                          {formatIdealDueDate(campaign.idealduedate)}
                        </TableCell>
                        <TableCell>{campaign.campaignstatus}</TableCell>
                      </TableRow>
                    ))
                  ) : (
                    <TableRow>
                      <TableCell colSpan="8" align="center">
                        No campaigns available
                      </TableCell>
                    </TableRow>
                  )}
                </TableBody>
              </Table>
            </TableContainer>
          </Grid>
        </Grid>
      </Box>
    
    </Container>
  );
};

export default Campaigns;
