import React, { useState } from 'react';
import { Dialog, DialogActions, DialogContent, DialogTitle, Button, TextField, Checkbox, FormControlLabel, Autocomplete } from '@mui/material';
import Navbar from '../../Components/Navbar/NavBar';
import './blitzpay.css';

const PayCreatorDialog = ({ open, onClose, onSubmit }) => {
  const [paymentAmount, setPaymentAmount] = useState('');
  const [paymentTerm, setPaymentTerm] = useState('');
  const [blitzPay, setBlitzPay] = useState(false);
  const [poNumber, setPoNumber] = useState('');
  const [notes, setNotes] = useState('');
  const [selectedCreator, setSelectedCreator] = useState(null);

  // Dummy data for creator selection
  const creators = [
    { name: 'John Doe', id: '1' },
    { name: 'Jane Smith', id: '2' },
    // Add more dummy creators as needed
  ];

  const paymentTerms = [
    { name: 'Net 5', id: '1' },
    { name: 'Net 30', id: '2' },
    { name: 'Net 45', id: '3' },
    { name: 'Net 60', id: '4' },

    // Add more dummy creators as needed
  ];

  const handleBlitzPayChange = (event) => {
    setBlitzPay(event.target.checked);
  };

  const handleSubmit = () => {
    onSubmit({
      selectedCreator: selectedCreator?.id,
      paymentAmount,
      paymentTerm,
      blitzPay,
      poNumber,
      notes,
    });
    onClose();
  };

  return (
    <Dialog open={open} onClose={onClose} aria-labelledby="form-dialog-title">
      <DialogTitle id="form-dialog-title">Pay a Creator</DialogTitle>
      <DialogContent>
        <Autocomplete
          options={creators}
          getOptionLabel={(option) => option.name}
          fullWidth
          margin="dense"
          onChange={(event, newValue) => {
            setSelectedCreator(newValue);
          }}
          renderInput={(params) => <TextField {...params} label="Select a Creator" variant="outlined" />}
        />
        <TextField
          margin="dense"
          label="Payment Amount"
          type="number"
          fullWidth
          value={paymentAmount}
          onChange={(e) => setPaymentAmount(e.target.value)}
          variant="outlined"
        />
        <TextField
          margin="dense"
          label="Payment Term"
          fullWidth
          value={paymentTerm}
          onChange={(e) => setPaymentTerm(e.target.value)}
          variant="outlined"
        />
        <FormControlLabel
          control={<Checkbox checked={blitzPay} onChange={handleBlitzPayChange} />}
          label="BlitzPay"
        />
        <TextField
          margin="dense"
          label="PO #"
          fullWidth
          value={poNumber}
          onChange={(e) => setPoNumber(e.target.value)}
          variant="outlined"
        />
        <TextField
          margin="dense"
          label="Notes"
          fullWidth
          multiline
          rows={4}
          value={notes}
          onChange={(e) => setNotes(e.target.value)}
          variant="outlined"
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose} color="primary">Cancel</Button>
        <Button onClick={handleSubmit} color="primary">Submit</Button>
      </DialogActions>
    </Dialog>
  );
};

export default PayCreatorDialog;
