import React, { useState } from 'react';
import {
  Button,
  TextField,
  Box,
  Snackbar,
  Alert,
  CircularProgress,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import AuthFormLayout from '../../Components/containers/auth-form-layout';
import { Link } from 'react-router-dom';
import withRoot from '../../hocs/with-root';
import { useMutation } from 'react-query';
import client from '../../API';
import useAuth from '../../Hooks/use-auth';
import { validateEmail, validatePassword } from '../../Utils';
import routes from '../../Config/routes';
import './Login.css';
import Typography from '../../Components/client/Typography';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [openSnackbar, setOpenSnackbar] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');
  const [snackbarSeverity, setSnackbarSeverity] = useState('info');
  const { authorize } = useAuth();

  const { mutate: login, isLoading } = useMutation(client.users.login, {
    onSuccess: (data) => {
      authorize(data.token);
      navigate('/dashboard');
    },
    onError: (error) => {
      console.error('An error occurred during login: ', error);
      let errorMessage = 'An error occurred, please try again.';
      if (error.response && error.response.data) {
        errorMessage = error.response.data.message || errorMessage;
      }
      setSnackbarMessage(errorMessage);
      setSnackbarSeverity('error');
      setOpenSnackbar(true);
    },
  });

  const handleCloseSnackbar = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    setOpenSnackbar(false);
  };

  const handleLogin = async () => {
    if (email.length === 0) {
      setSnackbarMessage('Email field is required!');
      setSnackbarSeverity('error');
      setOpenSnackbar(true);

      return;
    }

    if (!validateEmail(email)) {
      setSnackbarMessage('Invalid email address!');
      setSnackbarSeverity('error');
      setOpenSnackbar(true);

      return;
    }

    if (password.length === 0) {
      setSnackbarMessage('Email field is required!');
      setSnackbarSeverity('error');
      setOpenSnackbar(true);

      return;
    }

    if (!validatePassword(password)) {
      setSnackbarMessage('Password should have 6 more characters!');
      setSnackbarSeverity('error');
      setOpenSnackbar(true);

      return;
    }

    login({
      email,
      password,
    });
  };

  const handleRegister = () => {
    navigate('/register');
  };

  return (
    <AuthFormLayout>
      <div>
        <div>
          <Box textAlign="center">
            <h2>Welcome back</h2>
            <p>Sign in to continue with Blitz</p>
          </Box>

          <TextField
            disabled={isLoading}
            label="Email or Username"
            variant="outlined"
            fullWidth
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            margin="normal"
          />
          <TextField
            disabled={isLoading}
            label="Password"
            variant="outlined"
            type="password"
            fullWidth
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            margin="normal"
          />

          <Box className="remember-me-container" margin="normal">
            <input type="checkbox" id="remember-me" name="remember-me" />
            <label htmlFor="remember-me">Remember me</label>
          </Box>

          <Button
            variant="contained"
            color="primary"
            size="large"
            startIcon={
              isLoading && <CircularProgress size={20} color="inherit" />
            }
            onClick={handleLogin}
          >
            Login
          </Button>

          <p>
            Don't have an account?
            <Button color="primary">
              <Link to={routes.contactUs}>
                <Typography color="black">Contact Us</Typography>
              </Link>
            </Button>
          </p>

          <p>
            <Button color="primary">
              <Link to={routes.forgotPassword}>
                <Typography color="black">Forgot Password</Typography>
              </Link>
            </Button>
          </p>
        </div>

        <Snackbar
          open={openSnackbar}
          autoHideDuration={6000}
          onClose={handleCloseSnackbar}
        >
          <Alert
            onClose={handleCloseSnackbar}
            severity={snackbarSeverity}
            sx={{ width: '100%' }}
          >
            {snackbarMessage}
          </Alert>
        </Snackbar>
      </div>
    </AuthFormLayout>
  );
};

export default withRoot(Login);
