import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Container, Grid, Button, Divider, List, ListItem, ListItemText } from '@mui/material';
import Navbar from '../../../Components/Navbar/NavBar';
import { makeStyles } from '@material-ui/core/styles';

import SearchFilterSection from '../Search/leftColumnSearch';
import CampaignDetailsUI from './campaigndetailsUI';
const drawerWidth = "14.5rem"; // Assuming your Navbar width is 240px

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexDirection: "row",
    height: "100vh",
    overflow: "hidden",
    paddingLeft: 240, // Set this to the width of your Navbar
    [theme.breakpoints.up('md')]: {
      paddingLeft: 0,
    },
  },
  navbar: {
    width: drawerWidth,
    flexShrink: 0,
    zIndex: 2,
  },
  content: {
    display: "flex",
    flexDirection: "row", // Changed to column to stack filter and csv sections vertically
    flexGrow: 1,
    paddingLeft: drawerWidth, // Add padding equal to the navbar width
    width: `calc(80% - ${drawerWidth}px)`, // Allocate 75% of the width minus the navbar width
    overflowY: "auto", // Allow vertical scrolling within the content area if necessary
  },
  filterSection: {
    backgroundColor: "#f0f0f0",
    padding: theme.spacing(3),
  },
  csvSection: {
    backgroundColor: "#e8e8e8",
    padding: theme.spacing(0.9),
    height: "100%", // Set a fixed height
    overflowY: "auto", // Enable vertical scrolling within the CSV section
    width: "100%", // Ensure the csvSection takes the full width of its parent
    overflowX: "auto", // Allow horizontal scrolling within the csvSection if necessary
  },
  searchSection: {
    padding: theme.spacing(2),
    
    // Remove marginLeft to use the grid's spacing for alignment
  },
  campaignDetails: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
}));

const AddCreators = () => {
  const classes = useStyles();
  const { campaignId } = useParams();
  const navigate = useNavigate();
  const [campaignDetails, setCampaignDetails] = useState(null);
  const [selectedCreatorsData, setSelectedCreatorsData] = useState([]);

  useEffect(() => {
    const fetchCampaignDetails = async () => {
      const url = `http://localhost:5000/api/campaigns/${campaignId}`;
      try {
        const response = await fetch(url);
        if (!response.ok) {
          throw new Error(`Failed to fetch campaign details, status: ${response.status}`);
        }
        const data = await response.json();
        setCampaignDetails(data);
      } catch (error) {
        console.error('Error fetching campaign details:', error);
      }
    };

    if (campaignId) {
      fetchCampaignDetails();
    }
  }, [campaignId]);

  
  const handleCreatorSelect = (creator) => {
    setSelectedCreatorsData((prevCreators) => [...prevCreators, creator]);
  };

  const handleConfirmCreatorChanges = async () => {
    // Step 1: Prepare the payload
    const payload = {
      campaignId: parseInt(campaignId), // Ensure campaignId is an integer
      newCreators: selectedCreatorsData.map(creator => creator.id), // Assuming each creator has an 'id' property
    };
  
    try {
      // Step 2: Send the request
      const response = await fetch('http://localhost:5000/api/campaigns/updateCreatorList', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload), // Convert payload to JSON string
      });
  
      // Step 3: Handle the response
      if (!response.ok) {
        throw new Error('Failed to update campaign creators');
      }
      const responseData = await response.json(); // Convert response to JSON
      console.log('Successfully updated campaign creators:', responseData);
  
      // Step 4: Post-request actions
      navigate('/campaigns'); // Navigate to the campaigns route
    } catch (error) {
      console.error('Error updating campaign creators:', error);
    }
  };

  return (
    <Container maxWidth={false}>
      <Navbar />
      <Grid container spacing={2} className={classes.root}>
        <div className={classes.content}>
          <Grid item xs={12} md={9} className={classes.searchSection}>
            <SearchFilterSection onCreatorSelect={handleCreatorSelect} />
          </Grid>
          <Grid item xs={12} md={3} className={classes.campaignDetails}>
            <CampaignDetailsUI campaignDetails={campaignDetails} newCreators={selectedCreatorsData} />
            <Divider style={{ margin: '20px 0' }} />
            <List>
              {selectedCreatorsData.map((creator, index) => (
                <ListItem key={index}>
                  <ListItemText primary={creator.name} secondary={`Price: ${creator.price}`} />
                </ListItem>
              ))}
            </List>
            <Button variant="contained" color="primary" fullWidth onClick={handleConfirmCreatorChanges}>
              Confirm Creator Changes
            </Button>
          </Grid>
        </div>
      </Grid>
    </Container>
  );
};

export default AddCreators;
