import React, { useState, useEffect } from 'react';
import { Dialog, DialogActions, DialogContent, DialogTitle, Button, TextField, Checkbox, FormControlLabel, Autocomplete, Box } from '@mui/material';
import './blitzpay.css';

const PayCreatorDialog = ({ open, onClose, onSubmit }) => {
  const [paymentAmount, setPaymentAmount] = useState('');
  const [paymentTerm, setPaymentTerm] = useState(null);
  const [blitzPay, setBlitzPay] = useState(false);
  const [poNumber, setPoNumber] = useState('');
  const [notes, setNotes] = useState('');
  const [selectedCreator, setSelectedCreator] = useState(null);
  const [creators, setCreators] = useState([]);

  const paymentTerms = [
    { name: 'Net 5', id: '1' },
    { name: 'Net 30', id: '2' },
    { name: 'Net 45', id: '3' },
    { name: 'Net 60', id: '4' },
  ];

  useEffect(() => {
    const fetchCreatorsData = async () => {
      try {
        const response = await fetch('http://localhost:5100/api/sheets/creatorPayoutInfo/read?sheetId=1H8nPNW3e25oNGFZ8eITm3Jf_q3PImyvug4BbPHNsIZ8');
        if (!response.ok) throw new Error('Network response was not ok');
        const data = await response.json();
        setCreators(data); // Assuming the data format aligns with what the frontend expects
      } catch (error) {
        console.error('There was a problem with your fetch operation:', error);
      }
    };

    fetchCreatorsData();
  }, []);

  const handleBlitzPayChange = (event) => {
    setBlitzPay(event.target.checked);
  };

  const handleGeneratePO = () => {
    setPoNumber(`PO-${Math.floor(Math.random() * 10000)}`);
  };

  const handleSubmit = () => {
    if (!selectedCreator || !paymentAmount || !paymentTerm || !poNumber) {
      alert('Please fill in all required fields.');
      return;
    }

    const submissionData = {
      selectedCreator: selectedCreator.id,
      paymentAmount,
      paymentTerm: paymentTerm.name,
      poNumber,
      blitzPay,
      notes,
    };

    onSubmit(submissionData);
  };
  

  return (
    <Dialog open={open} onClose={onClose} aria-labelledby="form-dialog-title">
      <DialogTitle id="form-dialog-title">Pay a Creator</DialogTitle>
      <DialogContent>
        <Autocomplete
          options={creators}
          getOptionLabel={(option) => option.name}
          value={selectedCreator}
          onChange={(event, newValue) => {
            setSelectedCreator(newValue);
          }}
          renderInput={(params) => (
            <TextField {...params} label="Select a Creator" variant="outlined" required />
          )}
        />
        <TextField
          margin="dense"
          label="Payment Amount"
          type="number"
          fullWidth
          value={paymentAmount}
          onChange={(e) => setPaymentAmount(e.target.value)}
          variant="outlined"
          required
        />
        <Autocomplete
          options={paymentTerms}
          getOptionLabel={(option) => option.name}
          value={paymentTerm}
          onChange={(event, newValue) => {
            setPaymentTerm(newValue);
          }}
          renderInput={(params) => (
            <TextField {...params} label="Select a Payment Term" variant="outlined" required />
          )}
        />
        <FormControlLabel
          control={<Checkbox checked={blitzPay} onChange={handleBlitzPayChange} />}
          label="BlitzPay"
        />
        <Box display="flex" alignItems="center" gap={2}>
          <TextField
            margin="dense"
            label="PO #"
            fullWidth
            value={poNumber}
            onChange={(e) => setPoNumber(e.target.value)}
            variant="outlined"
            required
          />
          <Button variant="outlined" onClick={handleGeneratePO}>Generate PO</Button>
        </Box>
        <TextField
          margin="dense"
          label="Notes"
          fullWidth
          multiline
          rows={4}
          value={notes}
          onChange={(e) => setNotes(e.target.value)}
          variant="outlined"
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose} color="primary">Cancel</Button>
        <Button onClick={handleSubmit} color="primary">Submit</Button>
      </DialogActions>
    </Dialog>
  );
};

export default PayCreatorDialog;
