import React, { useState, useEffect } from "react";
import Navbar from "../../../Components/Navbar/NavBar";
import "./dedInvoicing.css";
import { FormControl, InputLabel, Select, MenuItem } from "@material-ui/core";

const Invoicing = () => {
  const [activeTab, setActiveTab] = useState("all");
  const [statusFilter, setStatusFilter] = useState(""); // State to hold the current filter status
  const [invoices, setInvoices] = useState([]);
  const [filteredInvoices, setFilteredInvoices] = useState([]); // State to hold filtered invoices
  const [campaignNames, setCampaignNames] = useState({});

  useEffect(() => {
    // Fetch all invoices from the backend
    const fetchInvoices = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/invoices");
        if (!response.ok) {
          throw new Error("Could not fetch invoices");
        }
        const data = await response.json();
        setInvoices(data.invoices); // Adjust based on actual API response
        // Fetch campaign names for all invoices and store them in state
        const campaignIds = data.invoices.map((inv) => inv.campaign_id);
        const campaignsResponse = await Promise.all(
          campaignIds.map((id) =>
            fetch(`http://localhost:5000/api/campaigns/${id}`).then((res) =>
              res.json(),
            ),
          ),
        );
        const campaignNamesMap = {};
        campaignsResponse.forEach((campaign) => {
          campaignNamesMap[campaign.id] = campaign.name;
        });
        setCampaignNames(campaignNamesMap);
      } catch (error) {
        console.error(error.message);
      }
    };

    fetchInvoices();
  }, []);

  useEffect(() => {
    // Filter invoices whenever the invoices list or statusFilter changes
    setFilteredInvoices(
      invoices.filter(
        (invoice) => statusFilter === "" || invoice.status === statusFilter,
      ),
    );
  }, [invoices, statusFilter]);

  const handleStatusFilterChange = (event) => {
    setStatusFilter(event.target.value);
  };

  const formatAmount = (amount) => {
    // Format the number to a currency format
    return `$${parseFloat(amount)
      .toFixed(2)
      .replace(/\d(?=(\d{3})+\.)/g, "$&,")}`;
  };

  return (
    <div className="dashboard">
      <Navbar />
      <div className="invoice-container">
        <div className="left-column">
          <h1 className="header">Invoicing</h1>
          <div className="button-container" style={{ marginBottom: "10px" }}>
          <FormControl fullWidth>
              <InputLabel>Status Filter</InputLabel>
              <Select
                value={statusFilter}
                onChange={handleStatusFilterChange}
                displayEmpty
                inputProps={{ 'aria-label': 'Without label' }}
              >
                <MenuItem value="">
                  <em>All</em>
                </MenuItem>
                <MenuItem value="Sent">Sent</MenuItem>
                <MenuItem value="Paid">Paid</MenuItem>
                <MenuItem value="Failed">Failed</MenuItem>
                <MenuItem value="Advance">Advance</MenuItem>

              </Select>
            </FormControl>
          </div>
          <div className="table-container">
            <table>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Campaign Name</th>
                  <th>PO Number</th>
                  <th>Amount Due</th>
                  <th>Status</th>
                  <th>Created At</th>
                </tr>
              </thead>
              <tbody>
  {filteredInvoices.map((invoice) => ( // Use filteredInvoices here
    <tr key={invoice.id}>
      <td>{invoice.id}</td>
      <td>{campaignNames[invoice.campaign_id] || "Creator Payout"}</td> {/* Assuming you want to use the mapped campaign names */}
      <td>{invoice.po_number || "N/A"}</td>
      <td>{formatAmount(invoice.amount_due)}</td>
      <td>{invoice.status}</td>
      <td>{new Date(invoice.created_at).toLocaleDateString()}</td>
    </tr>
  ))}
</tbody>

            </table>
          </div>
        </div>
        <div className="right-column">
          {/* Components for the right column will go here */}
        </div>
      </div>
    </div>
  );
};

export default Invoicing;
