import React, { useState, useEffect } from "react";
import "./dashboard.css";
import Navbar from "../../../Components/Navbar/NavBar";
import {
  Button,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  Select,
  MenuItem,
} from "@mui/material";
import { useMutation } from "react-query";
import client from "../../../API";

const Dashboard = () => {
  const [dataView, setDataView] = useState("campaigns");
  const [campaigns, setCampaigns] = useState([]);
  const [payouts, setPayouts] = useState([]);
  const [invoices, setInvoices] = useState([]);
  const [campaignStatusFilter, setCampaignStatusFilter] = useState("");
  const [accountBalance, setAccountBalance] = useState("Loading...");
  const [creditline, setCreditline] = useState("Loading...");

  const { mutate: fetchCampaigns } = useMutation(client.campaigns.list, {
    onSuccess: (data) => {
      setCampaigns(data);
    },
    onError: (error) => {
      console.error("Error fetching campaigns:", error);
    },
  });

  const { mutate: fetchPayouts } = useMutation(client.payouts.list, {
    onSuccess: (data) => {
      setPayouts(data);
    },
    onError: (error) => {
      console.error("Error fetching payouts:", error);
    },
  });

  const { mutate: fetchInvoices } = useMutation(client.invoices.list, {
    onSuccess: (data) => {
      setInvoices(data.invoices || []);
    },
    onError: (error) => {
      console.error("Error fetching invoices:", error);
    },
  });
  const fetchCompanyData = async () => {
    try {
      // Adjusted call to match your updated client and method setup
      const response = await client.companies.listFetch(); // This needs to accurately hit the "/company/companydata" endpoint.
      
      // Directly using the response assuming HttpClient is setup similar to Axios
      // Adjust based on actual structure. It might be response.data directly if HttpClient wraps Axios.
      const data = response; 
  
      if (data && data.balance !== undefined && data.creditline !== undefined) {
        setAccountBalance(`$${parseFloat(data.balance).toFixed(2)}`);
        setCreditline(data.creditline ? `$${parseFloat(data.creditline).toFixed(2)}` : "No Credit Line Established");
      } else {
        // Handle case where data is undefined or not as expected
        console.error("Received data is not in the expected format:", data);
        setAccountBalance("Data format error");
        setCreditline("Data format error");
      }
    } catch (error) {
      console.error("Error fetching company data:", error);
      setAccountBalance("Error loading balance");
      setCreditline("Error loading credit line");
    }
  };  

  useEffect(() => {
    fetchCampaigns();
    fetchPayouts();
    fetchInvoices();
    fetchCompanyData();

  }, []);

  const filteredCampaigns = campaigns.filter((campaign) =>
    campaignStatusFilter
      ? campaign.campaignstatus === campaignStatusFilter
      : true
  );

  const renderTableData = () => {
    switch (dataView) {
      case "campaigns":
        return filteredCampaigns.map((campaign) => (
          <TableRow key={campaign.id}>
            <TableCell>{campaign.name}</TableCell>
            <TableCell>${campaign.campaign_sum}</TableCell>
            <TableCell>
              {campaign.creators.map((creator) => creator.name).join(", ")}
            </TableCell>
            <TableCell>{campaign.campaignstatus}</TableCell>
          </TableRow>
        ));
      case "payouts":
        return payouts.map((payout) => (
          <TableRow key={payout.payout_id}>
            <TableCell>{payout.creator_id}</TableCell>
            <TableCell>{payout.amount}</TableCell>
            <TableCell>{payout.payout_date}</TableCell>
            <TableCell>{payout.blitzpay}</TableCell>

            <TableCell>{payout.status}</TableCell>
          </TableRow>
        ));
      case "invoices":
        // Ensure invoices is an array before mapping
        return Array.isArray(invoices)
          ? invoices.map((invoice) => (
              <TableRow key={invoice.id}>
                <TableCell>{invoice.id}</TableCell>
                <TableCell>${invoice.amount_due}</TableCell>
                <TableCell>{invoice.status}</TableCell>
              </TableRow>
            ))
          : null;
      default:
        return null;
    }
  };

  return (
    <div className="dashboard">
      <Navbar />
      <div
        className="container"
        style={{ display: "flex", marginLeft: "250px" }}
      >
        {/* Left Column */}
        <div className="left-column" style={{ flex: "0 0 57%" }}>
          {/* Placeholder for Graph or other components */}
          <div
            className="graph-placeholder"
            style={{
              marginBottom: "20px",
              height: "200px",
              backgroundColor: "#f0f0f0",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            Graph Placeholder
          </div>

          {/* Data View and Filters */}
          <div style={{ marginBottom: "20px" }}>
            <Select
              value={dataView}
              onChange={(e) => setDataView(e.target.value)}
              displayEmpty
              inputProps={{ "aria-label": "Without label" }}
              style={{ marginRight: "10px" }}
            >
              <MenuItem value="campaigns">Campaigns</MenuItem>
              <MenuItem value="payouts">Payouts</MenuItem>
              <MenuItem value="invoices">Invoices</MenuItem>
            </Select>

            {dataView === "campaigns" && (
              <Select
                value={campaignStatusFilter}
                onChange={(e) => setCampaignStatusFilter(e.target.value)}
                displayEmpty
                inputProps={{ "aria-label": "Without label" }}
              >
                <MenuItem value="">All</MenuItem>
                <MenuItem value="Launched">Launched</MenuItem>
                <MenuItem value="Pending">Pending</MenuItem>
                <MenuItem value="Completed">Completed</MenuItem>
              </Select>
            )}
          </div>

          <Table>
            <TableHead>
              <TableRow>{/* Table Headers based on dataView */}</TableRow>
            </TableHead>
            <TableBody>{renderTableData()}</TableBody>
          </Table>
        </div>

        {/* Right Column */}
        <div
          className="right-column"
          style={{ flex: "0 0 25%", paddingLeft: "10px" }}
        >
          {/* Total Balance */}
          <div className="balance">
            <h2>Client Name Summary</h2>
          </div>
          <div className="balance" style={{ marginBottom: "10px" }}>
          <h2>Account Balance</h2>
          <p>{accountBalance}</p>
          </div>
          <div className="balance" style={{ marginBottom: "10px" }}>
            <h3>Total Spent</h3>
            <div style={{ fontSize: "18px", fontWeight: "bold" }}>$5,000</div>
          </div>
          {/* Add to Balance Button */}
          <Button
            variant="contained"
            color="primary"
            style={{ marginBottom: "20px" }}
          >
            Add to Balance
          </Button>

          {/* Placeholder for Transaction History */}
          <div
            className="transaction-history-placeholder"
            style={{
              height: "300px",
              backgroundColor: "#f0f0f0",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            Transaction History Placeholder
          </div>
          <div
            className="transaction-history-placeholder"
            style={{
              height: "300px",
              backgroundColor: "#f0f0f0",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            Campaign Recaps Summary
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
