import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Container, Grid, Button, Divider } from '@mui/material';
import Navbar from '../../../Components/Navbar/NavBar';
import { makeStyles } from '@material-ui/core/styles'; // Ensure this is consistent with your MUI version
import SearchFilterSection from '../Search/leftColumnSearch';
import CampaignDetailsUI from './campaigndetailsUI';

const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: theme.spacing(8), // Adjust this based on your Navbar height
  },
  searchSection: {
    padding: theme.spacing(2),
    marginLeft: 30,
  },
  campaignDetails: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
}));

const AddCreators = () => {
  const classes = useStyles();
  const { campaignId } = useParams();
  const navigate = useNavigate();
  const [campaignDetails, setCampaignDetails] = useState(null);

  useEffect(() => {
    const fetchCampaignDetails = async () => {
      const url = `http://localhost:5000/api/campaigns/${campaignId}`;
      try {
        const response = await fetch(url);
        if (!response.ok) {
          throw new Error(`Failed to fetch campaign details, status: ${response.status}`);
        }
        const data = await response.json();
        setCampaignDetails(data);
      } catch (error) {
        console.error('Error fetching campaign details:', error);
      }
    };

    if (campaignId) {
      fetchCampaignDetails();
    }
  }, [campaignId]);

  const handleConfirmCreatorChanges = () => {
    navigate('/campaigns');
  };

  return (
    <Container maxWidth={false}> {/* fullWidth container */}
      <Navbar />
      <Grid container spacing={3} className={classes.root}>
        <Grid item xs={10} md={8} className={classes.searchSection}> {/* Left column, larger screen */}
          <SearchFilterSection />
        </Grid>
        <Grid item xs={2} md={4} className={classes.campaignDetails}> {/* Right column, adjust for larger screen */}
          <CampaignDetailsUI campaignDetails={campaignDetails} />
          <Divider style={{ margin: '20px 0' }} />
          <Button variant="contained" color="primary" fullWidth onClick={handleConfirmCreatorChanges}>
            Confirm Creator Changes
          </Button>
        </Grid>
      </Grid>
    </Container>
  );
};

export default AddCreators;
