import React, { useState } from 'react';
import { Dialog, DialogActions, DialogContent, DialogTitle, Button, TextField, Checkbox, FormControlLabel, FormGroup } from '@material-ui/core';
import { FormControl, InputLabel, Select, MenuItem } from '@material-ui/core';

const CreatorDialog = ({ open, onClose, onSubmit }) => {
  const [contactEmail, setContactEmail] = useState('');
  const [paymentEmail, setPaymentEmail] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [emailSame, setEmailSame] = useState(false);
  const [creatorName, setCreatorName] = useState('');
  const [creatorRegion, setCreatorRegion] = useState('');
  const [platforms, setPlatforms] = useState({
    TikTok: false,
    Instagram: false,
    YouTube: false,
    Twitter: false,
  });
  const [race, setRace] = useState('');
  const [gender, setGender] = useState('');
  const [location, setLocation] = useState('');
   
  // Instagram
  const [instagramLink, setInstagramLink] = useState('');
  const [instagramBrandRate, setInstagramBrandRate] = useState('');
  const [instagramSongRate, setInstagramSongRate] = useState('');
  
  // TikTok
  const [tikTokLink, setTikTokLink] = useState('');
  const [tikTokBrandRate, setTikTokBrandRate] = useState('');
  const [tikTokSongRate, setTikTokSongRate] = useState('');
  
  // YouTube
  const [youtubeLink, setYoutubeLink] = useState('');
  const [youtube30sBrandRate, setYoutube30sBrandRate] = useState('');
  const [youtube60sBrandRate, setYoutube60sBrandRate] = useState('');
  const [youtubeShortsBrandRate, setYoutubeShortsBrandRate] = useState('');
  const [youtubeSongRate, setYoutubeSongRate] = useState('');

  // Add other platform-specific rates and links as neede
  const handlePlatformChange = (event) => {
    setPlatforms({ ...platforms, [event.target.name]: event.target.checked });
  };

  const handleEmailSameChange = (event) => {
    setEmailSame(event.target.checked); // Update the state to reflect the checkbox's status
    if (event.target.checked) {
      setPaymentEmail(contactEmail); // If checked, set the payment email to the contact email
    } else {
      setPaymentEmail(''); // If unchecked, clear the payment email field for manual input
    }
  };
  
  const handleSaveCreator = async () => {
    const geolocationgenderethnicity = `${location} / ${gender} / ${race}`;
    const primaryMarket = "Your primary market here"; // This needs to be defined based on your form or business logic
    const notesContentStyle = "Your content style here"; // This needs to be defined similarly

    const payload = {
        creator: creatorName,
        email: contactEmail, // Assuming this maps to the 'creator' field in your database
        phone_number: phoneNumber,
        region: creatorRegion,
        geolocationgenderethnicity,
        instagram_link: instagramLink,
        tiktok_link: tikTokLink,
        youtube_link: youtubeLink,
        // Assuming IG and TikTok "brand_rate" and "song_rate" map to your schema's corresponding fields
        ig_feed_post: instagramBrandRate,
        ig_reels_sound: instagramSongRate,
        tiktok_brand: tikTokBrandRate,
        tiktok_sound: tikTokSongRate,
        // Additional fields as per your schema
        primary_market: primaryMarket,
        notescontent_style: notesContentStyle,
        ig_reels_brand: "Your IG reels brand rate", // Set appropriate values
        ig_story: "Your IG story rate",
        youtube_song_rate: youtubeSongRate, // Assuming this maps to 'shorts' in your schema
        // Ensure all other required fields are included and match the schema
    };

    try {
        const response = await fetch('http://localhost:5000/api/creators/add', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload),
        });

        if (!response.ok) {
            throw new Error('Failed to save the creator data');
        }
        const responseData = await response.json();
        console.log('Creator data saved:', responseData);
    } catch (error) {
        console.error('Error saving creator data:', error);

    }
  };
  
  
  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>Add Creators</DialogTitle>
      <DialogContent>
        <TextField
          margin="dense"
          label="Creator Name"
          type="text"
          fullWidth
          value={creatorName}
          onChange={(e) => setCreatorName(e.target.value)}
          variant="outlined"
        />
        <FormControl variant="outlined" fullWidth margin="dense">
          <InputLabel>Creator Region</InputLabel>
          <Select
            label="Creator Region"
            value={creatorRegion}
            onChange={(e) => setCreatorRegion(e.target.value)}
          >
            <MenuItem value=""><em>None</em></MenuItem>
            <MenuItem value="North America">North America</MenuItem>
            <MenuItem value="Europe">Europe</MenuItem>
            <MenuItem value="Asia">Asia</MenuItem>
            <MenuItem value="South America">South America</MenuItem>
            <MenuItem value="Australia">Australia</MenuItem>
            <MenuItem value="Africa">Africa</MenuItem>
          </Select>
        </FormControl>
        <FormGroup row>
          {Object.keys(platforms).map((platform) => (
            <FormControlLabel
              key={platform}
              control={<Checkbox checked={platforms[platform]} onChange={handlePlatformChange} name={platform} />}
              label={platform}
            />
          ))}
        </FormGroup>
        {platforms.Instagram && (
  <>
    <TextField
      margin="dense"
      label="Instagram Link"
      type="text"
      fullWidth
      value={instagramLink}
      onChange={(e) => setInstagramLink(e.target.value)}
      variant="outlined"
    />
    <TextField
      margin="dense"
      label="Instagram Brand Rate"
      type="text"
      fullWidth
      value={instagramBrandRate}
      onChange={(e) => setInstagramBrandRate(e.target.value)}
      variant="outlined"
    />
    <TextField
      margin="dense"
      label="Instagram Song Rate"
      type="text"
      fullWidth
      value={instagramSongRate}
      onChange={(e) => setInstagramSongRate(e.target.value)}
      variant="outlined"
    />
  </>
)}
{platforms.TikTok && (
  <>
    <TextField
      margin="dense"
      label="TikTok Link"
      type="text"
      fullWidth
      value={tikTokLink}
      onChange={(e) => setTikTokLink(e.target.value)}
      variant="outlined"
    />
    <TextField
      margin="dense"
      label="TikTok Brand Rate"
      type="text"
      fullWidth
      value={tikTokBrandRate}
      onChange={(e) => setTikTokBrandRate(e.target.value)}
      variant="outlined"
    />
    <TextField
      margin="dense"
      label="TikTok Song Rate"
      type="text"
      fullWidth
      value={tikTokSongRate}
      onChange={(e) => setTikTokSongRate(e.target.value)}
      variant="outlined"
    />
  </>
)}
{platforms.YouTube && (
  <>
    <TextField
      margin="dense"
      label="YouTube Link"
      type="text"
      fullWidth
      value={youtubeLink}
      onChange={(e) => setYoutubeLink(e.target.value)}
      variant="outlined"
    />
    <TextField
      margin="dense"
      label="YouTube Brand Rate - 30s"
      type="text"
      fullWidth
      value={youtube30sBrandRate}
      onChange={(e) => setYoutube30sBrandRate(e.target.value)}
      variant="outlined"
    />
    <TextField
      margin="dense"
      label="YouTube Brand Rate - 60s"
      type="text"
      fullWidth
      value={youtube60sBrandRate}
      onChange={(e) => setYoutube60sBrandRate(e.target.value)}
      variant="outlined"
    />
    <TextField
      margin="dense"
      label="YouTube Shorts Brand Rate"
      type="text"
      fullWidth
      value={youtubeShortsBrandRate}
      onChange={(e) => setYoutubeShortsBrandRate(e.target.value)}
      variant="outlined"
    />
    <TextField
      margin="dense"
      label="YouTube Song Rate"
      type="text"
      fullWidth
      value={youtubeSongRate}
      onChange={(e) => setYoutubeSongRate(e.target.value)}
      variant="outlined"
    />
  </>
)}

        <TextField
          margin="dense"
          label="Race (optional)"
          type="text"
          fullWidth
          value={race}
          onChange={(e) => setRace(e.target.value)}
          variant="outlined"
        />
        <TextField
          margin="dense"
          label="Gender (optional)"
          type="text"
          fullWidth
          value={gender}
          onChange={(e) => setGender(e.target.value)}
          variant="outlined"
        />
        <TextField
          margin="dense"
          label="Location (optional)"
          type="text"
          fullWidth
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          variant="outlined"
        />
        <TextField
          margin="dense"
          label="Contact Email"
          type="email"
          fullWidth
          value={contactEmail}
          onChange={(e) => setContactEmail(e.target.value)}
          variant="outlined"
        />
        <TextField
          margin="dense"
          label="Payment Email"
          type="email"
          fullWidth
          disabled={emailSame}
          value={paymentEmail}
          onChange={(e) => setPaymentEmail(e.target.value)}
          variant="outlined"
        />
        <TextField
          margin="dense"
          label="Phone Number"
          type="tel"
          fullWidth
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
          variant="outlined"
        />
        <FormControlLabel
          control={<Checkbox checked={emailSame} onChange={handleEmailSameChange} />}
          label="Payment and contact email are the same"
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancel</Button>
        <Button onClick={handleSaveCreator} color="primary">Submit</Button>
      </DialogActions>
    </Dialog>
  );
  
};

export default CreatorDialog;
