// LeftColumn.js
import React from 'react';
import {
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  TextField,
  Button,
  makeStyles
} from '@material-ui/core';

// You can move makeStyles here or to a separate file if needed.
const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    width: '100%',
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  button: {
    margin: theme.spacing(1),
  },
  searchField: {
    margin: theme.spacing(1),
    minWidth: 120,
    width: '100%',
  },
}));

const LeftColumn = ({
  region, setRegion, platform, setPlatform, promotionType, setPromotionType,
  race, setRace, gender, setGender, furtherLocation, setFurtherLocation,
  avgViews, setAvgViews, filter, setFilter, handleResetFilters
}) => {
  const classes = useStyles();

  return (
    <Grid container spacing={3}>
      <Grid item xs={12}>
        <FormControl variant="outlined" className={classes.formControl}>
          <InputLabel id="region-label">Region</InputLabel>
          <Select
            labelId="region-label"
            id="region"
            value={region}
            onChange={(e) => setRegion(e.target.value)}
            label="Region"
          >
            <MenuItem value=""><em>None</em></MenuItem>
            <MenuItem value="NA">North America</MenuItem>
            <MenuItem value="EU">Europe</MenuItem>
            {/* ...other regions */}
          </Select>
        </FormControl>
      </Grid>

      {/* ...Other form controls for platform, promotionType, race, etc... */}

      <Grid item xs={12}>
        <TextField
          id="search"
          label="Search"
          type="search"
          variant="outlined"
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          className={classes.searchField}
        />
      </Grid>

      <Grid item xs={12}>
        <Button
          variant="contained"
          color="primary"
          onClick={handleResetFilters}
          className={classes.button}
          fullWidth
        >
          Reset Filters
        </Button>
      </Grid>

      {/* ...any additional buttons or inputs needed for filtering */}
    </Grid>
  );
};

export default LeftColumn;
