import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Grid, makeStyles, Card, CardContent, Typography, CardMedia, Link } from '@material-ui/core';
import client from '../../../../API'
import NavBar from '../../../../Components/Navbar/NavBar'; // Assuming NavBar is your navigation component

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    padding: theme.spacing(2)
  },
  card: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
}));

const AgencyCreatorRoster = () => {
  const classes = useStyles();
  const { manager } = useParams(); // Captures the manager parameter from the URL
  const [creators, setCreators] = useState([]);

  useEffect(() => {
    const fetchCreators = async () => {
        try {
          const response = await client.post('/api/creators/manager', { manager });
          setCreators(response.creators);
        } catch (error) {
          console.error('Failed to fetch creators', error);
          console.log(error.config);  // This will log the request configuration and might give clues.
        }
      };
    fetchCreators();
  }, [manager]);

  return (
    <>
      <NavBar /> {/* Assuming NavBar contains links to other parts of the application */}
      <Grid container spacing={2} className={classes.root}>
        {creators.map((creator) => (
          <Grid item xs={12} sm={6} md={4} key={creator.id}>
            <Card className={classes.card}>
              <CardMedia
                className={classes.media}
                image={creator.profileImageUrl || "default_profile_image_url"} // Placeholder for profile image
                title="Profile Image"
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="h2">
                  {creator.name}
                </Typography>
                <Typography variant="body2" color="textSecondary" component="p">
                  Platform: {creator.platform}
                </Typography>
                <Typography variant="body2" color="textSecondary" component="p">
                  Followers: {creator.followers}
                </Typography>
                <Typography variant="body2" color="textSecondary" component="p">
                  Engagement Rate: {creator.engagementRate}%
                </Typography>
                <Link href={creator.profileLink} target="_blank">
                  View Profile
                </Link>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </>
  );
};

export default AgencyCreatorRoster;
