import React from 'react';
import PropTypes from 'prop-types';
import Container from '@mui/material/Container';
import Box from '@mui/material/Box';
import Paper from '../client/Paper';
import Typography from '../client/Typography';
import Header from './layouts/header';
import Footer from './layouts/footer';
import { Link } from 'react-router-dom';
import routes from '../../Config/routes';

function RegisterFormLayout(props) {
  const { children, isRegister } = props;

  return (
    <React.Fragment>
      <Header />

      <React.Fragment>
        <Typography
          variant="h3"
          gutterBottom
          marked="center"
          align="center"
          mt={6}
        >
          {isRegister ? 'Sign Up' : 'Sign In'}
        </Typography>
        <Typography variant="body2" align="center">
          {'Not a member yet? '}
          <Link to={isRegister ? routes.login : routes.register}>
            <Typography align="center" underline="always">
              {isRegister ? 'Already have an account?' : 'Sign Up here'}
            </Typography>
          </Link>
        </Typography>
      </React.Fragment>
      <Box
        sx={{
          display: 'flex',
          backgroundImage: 'url(/images/appCurvyLines.png)',
          backgroundRepeat: 'no-repeat',
        }}
      >
        <Container maxWidth="sm">
          <Box sx={{ mt: 7, mb: 12 }}>
            <Paper
              background="light"
              sx={{ py: { xs: 4, md: 8 }, px: { xs: 3, md: 6 } }}
            >
              {children}
            </Paper>
          </Box>
        </Container>
      </Box>

      <Footer />
    </React.Fragment>
  );
}

RegisterFormLayout.propTypes = {
  children: PropTypes.node,
};

export default RegisterFormLayout;
