import React, { useState, useEffect } from "react";
import {
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  TextField,
  Button,
  makeStyles,
  Checkbox,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(2),
    width: 200,
  },
  content: {
    marginTop: theme.spacing(3),
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  button: {
    margin: theme.spacing(1),
  },

  table: {
    minWidth: 650,
  },
  filterContainer: {
    marginBottom: theme.spacing(3),
  },

  csvTable: {
    width: "100%",
  },
}));

const LeftColumn = ({ onCreatorSelect }) => {
  const classes = useStyles();
  const [filter, setFilter] = useState("");
  const [region, setRegion] = useState(""); // Default to showing ALL Regions
  const [platform, setPlatform] = useState("TikTok"); // Default to TikTok Brand, and remove the option for None
  const [promotionType, setPromotionType] = useState("Brand"); // Assuming you want to start with TikTok Brand
  const [data, setData] = useState([]);
  const [race, setRace] = useState("");
  const [gender, setGender] = useState("");
  const [furtherLocation, setFurtherLocation] = useState("");
  const [selectedItems, setSelectedItems] = useState(new Set());
  const [avgViews, setAvgViews] = useState("");
  const [selectedCreatorsData, setSelectedCreatorsData] = useState([]);
  const [campaignName, setCampaignName] = useState("");
  const [dialogMessage, setDialogMessage] = useState("");
  const [campaignSum, setCampaignSum] = useState(0);

  const handleResetFilters = () => {
    setRegion("");
    setPlatform("TikTok");
    setPromotionType("Brand");
    setRace("");
    setGender("");
    setFurtherLocation("");
    setFilter("");
    setAvgViews(""); // Reset this as well if you're including the AVG Views Filter
  };
  useEffect(() => {
    const sum = calculateTotalCampaignSum();
    setCampaignSum(sum);
    const fetchData = async () => {
      try {
        const response = await fetch(
          "http://localhost:5100/api/sheets/creatorData/read"
        );
        if (!response.ok) throw new Error("Network response was not ok");
        const jsonData = await response.json();
        setData(jsonData); // Assuming jsonData is an array of arrays
      } catch (error) {
        console.error("Failed to fetch data:", error);
      }
    };
    fetchData();
  }, [selectedCreatorsData]);
  const platformIndexMap = {
    TikTok: 2, // Column 3 in human-readable form, index 2 in zero-based indexing
    Instagram: 3, // Column 4 in human-readable form, index 3 in zero-based indexing
    Youtube: 4, // Column 5 in human-readable form, index 4 in zero-based indexing
  };

  const priceColumnMap = {
    TikTok: {
      Sound: 8, // Column index for TikTok Sound price
      Brand: 9, // Column index for TikTok Brand price
    },
    Instagram: {
      Sound: 11, // Column index for Instagram Sound price
      Brand: 12, // Column index for Instagram Brand price
    },
  };
  const handleFilterChange = (e) => {
    setFilter(e.target.value.toLowerCase());
  };

  const handleRegionChange = (e) => {
    setRegion(e.target.value);
  };

  const handlePlatformChange = (e) => {
    const newPlatform = e.target.value;
    setPlatform(newPlatform);
    // Reset promotionType based on the platform selected
    // Default to the first available promotion type for the new platform
    setPromotionType(promotionTypeOptions[newPlatform][0]);
  };
  // Assuming each row in your data includes a unique identifier at index 0
  // Assuming the structure of your `data` array and the unique identifier is at index 0
  const handleSelectItem = (uniqueId) => {
    // Attempt to find the item based on the provided unique ID.
    const selectedItem = filteredData.find((item) => item[0] === uniqueId);
    if (!selectedItem) {
      console.warn(`Item with ID ${uniqueId} not found.`);
      return; // Early exit if the item isn't found.
    }

    // Determine the pricing information based on the selected platform and promotion type.
    const priceColumnIndex = priceColumnMap[platform]?.[promotionType];
    const price = selectedItem[priceColumnIndex] || "Price Unavailable";

    // Prepare the data object for the selected item.
    const relevantData = {
      id: uniqueId,
      name: selectedItem[0], // Assuming the item's name is the first element.
      price, // Use the determined price or fallback.
      other: selectedItem[17], // Additional item information, assuming it's at index 17.
    };

    // Determine if the item is currently selected to add or remove it accordingly.
    const isCurrentlySelected = selectedItems.has(uniqueId);
    updateSelections(uniqueId, isCurrentlySelected, relevantData);
  };

  // Utility function to update the state based on the item selection or deselection.
  const updateSelections = (uniqueId, isCurrentlySelected, itemData) => {
    setSelectedItems((prev) => {
      const updated = new Set(prev);
      isCurrentlySelected ? updated.delete(uniqueId) : updated.add(uniqueId);
      return updated;
    });

    setSelectedCreatorsData((prev) => {
      return isCurrentlySelected
        ? prev.filter((item) => item.id !== uniqueId)
        : [...prev, itemData];
    });

    // Invoke the callback with the item data if the item is newly selected.
    if (!isCurrentlySelected && onCreatorSelect) {
      onCreatorSelect(itemData);
    }
  };

  const searchFilter = (row) => {
    // If there is no filter set, don't filter out this row
    if (!filter) return true;

    // Assuming columns at index 5, 6, and 7 are the "viability" columns
    const searchColumns = [0, 5, 6, 7];
    return searchColumns.some((index) => {
      // Ensure that undefined values are not causing an error
      const value = row[index] ? row[index].toString().toLowerCase() : "";
      return value.toString().toLowerCase().includes(filter);
    });
  };
  const calculateTotalCampaignSum = () => {
    const sum = selectedCreatorsData.reduce((accumulator, creator) => {
      // Remove any non-numeric characters (including the dollar sign) before conversion
      const cleanedPriceString = creator.price.replace(/[^0-9.-]+/g, "");
      const price = parseFloat(cleanedPriceString);
      // Check if the conversion was successful (i.e., the result is a finite number)
      if (isFinite(price)) {
        return accumulator + price;
      } else {
        console.error(`Invalid price format detected: ${creator.price}`);
        return accumulator;
      }
    }, 0);
    return sum;
  };

  const handlePromotionTypeChange = (e) => {
    setPromotionType(e.target.value);
  };
  const getColumnWidth = (index) => {
    // Define custom widths for specific columns by their index or header name
    const customWidths = {
      0: "50px", // Example: Column for checkboxes might be narrower
      1: "150px", // Adjust width for the second column
      // Add more custom widths as needed
      // You can also use headers[index] to set widths by header name if preferable
    };

    // Return the custom width if defined, otherwise return a default width
    return customWidths[index] || "125px"; // Default width for columns not explicitly set
  };
  const promotionTypeOptions = {
    TikTok: ["Sound", "Brand", "Livestream"],
    Instagram: ["Sound", "Brand", "Story Set"],
    Youtube: ["30 Second Integration", "60 Second Integration"],
  };
  const platformToColumnsMap = {
    TikTok: [2], // Column 3 in human-readable form
    Instagram: [3], // Column 4 in human-readable form
    Youtube: [4], // Column 5 in human-readable form
  };
  // ... inside your component where you define platformColumns
  const alwaysVisibleColumns = [1, 8, 15, 18]; // Columns 9, 16, and 18 in human-readable form

  // Determine which columns to display based on the selected platform and promotion type
  const columnsToShow = [
    ...alwaysVisibleColumns,
    ...(platformToColumnsMap[platform] || []),
    ...(promotionType && platform
      ? [priceColumnMap[platform][promotionType] + 1]
      : []),
  ];

  const headers = data[0] || []; // Fallback to an empty array if data[0] is undefined

  const filteredData = data.filter((row) => {
    const regionMatch = region
      ? row[6]?.toString().toLowerCase().includes(region.toLowerCase())
      : true;

    // Ensuring platform has a non-NULL and non-empty value
    const platformMatch = platform
      ? !isNaN(row[platformIndexMap[platform]]) &&
        row[platformIndexMap[platform]] !== null &&
        row[platformIndexMap[platform]] !== ""
      : true;

    let raceMatch = race
      ? row[4]?.toString().toLowerCase().includes(race.toLowerCase())
      : true;
    let genderMatch = gender
      ? row[4]
          ?.toString()
          .toLowerCase()
          .split(/[\s,]+/)
          .includes(gender.toLowerCase()) // Assuming gender is in column 6 (index 5)
      : true;

    const locationMatch = furtherLocation
      ? row[4]?.toString().toLowerCase().includes(furtherLocation.toLowerCase())
      : true;

    // Special handling for gender to ensure "Male" does not match "Female"
    if (gender === "male" && row[4]?.toLowerCase().includes("female")) {
      genderMatch = false;
    }

    // Updated to ensure 'Promotion Type' is not NULL or empty if selected
    const promotionTypeColumnIndex =
      promotionType && platform
        ? priceColumnMap[platform][promotionType]
        : null;
    const promotionTypeMatch =
      promotionTypeColumnIndex !== null
        ? row[promotionTypeColumnIndex] !== null &&
          row[promotionTypeColumnIndex] !== ""
        : true;

    return (
      regionMatch &&
      platformMatch &&
      raceMatch &&
      genderMatch &&
      locationMatch &&
      promotionTypeMatch &&
      searchFilter(row)
    );
  });

  return (
    <div>
      <Grid container spacing={3}>
        <div className={classes.content}>
          <Grid item xs={12} className={classes.filterContainer}>
            <FormControl variant="outlined" className={classes.formControl}>
              <InputLabel id="region-select-label">Select Region</InputLabel>
              <Select
                labelId="region-select-label"
                id="region-select"
                value={region}
                onChange={handleRegionChange}
                label="Select Region"
              >
                <MenuItem value="">
                  <em>ALL</em>
                </MenuItem>
                <MenuItem value="NACAUKAUS">NACAUKAUS</MenuItem>
                <MenuItem value="EU">EU</MenuItem>
                <MenuItem value="LATAM">LATAM</MenuItem>
                <MenuItem value="Asia">Asia</MenuItem>

                {/* More regions */}
              </Select>
            </FormControl>
            {/* Platform Dropdown */}
            <FormControl variant="outlined" className={classes.formControl}>
              <InputLabel id="platform-select-label">Platform</InputLabel>
              <Select
                labelId="platform-select-label"
                id="platform-select"
                value={platform}
                onChange={handlePlatformChange}
                label="Platform"
              >
                <MenuItem value="TikTok">TikTok</MenuItem>
                <MenuItem value="Instagram">Instagram</MenuItem>
              </Select>
            </FormControl>
            {/* Promotion Type Dropdown */}
            <FormControl variant="outlined" className={classes.formControl}>
              <InputLabel id="promotion-type-label">Promotion Type</InputLabel>
              <Select
                labelId="promotion-type-label"
                id="promotion-type-select"
                value={promotionType}
                onChange={handlePromotionTypeChange}
                label="Promotion Type"
              >
                {platform &&
                  promotionTypeOptions[platform].map((type) => (
                    <MenuItem key={type} value={type}>
                      {type}
                    </MenuItem>
                  ))}
              </Select>
            </FormControl>
            {/* Race Dropdown */}
            <FormControl variant="outlined" className={classes.formControl}>
              <InputLabel id="race-select-label">Race</InputLabel>
              <Select
                labelId="race-select-label"
                id="race-select"
                value={race}
                onChange={(e) => setRace(e.target.value)}
                label="Race"
              >
                <MenuItem value="">
                  <em>None</em>
                </MenuItem>
                <MenuItem value="Asian">Asian</MenuItem>
                <MenuItem value="Black">Black</MenuItem>
                <MenuItem value="Hispanic">Hispanic</MenuItem>
                <MenuItem value="White">White</MenuItem>
                <MenuItem value="Other">Other</MenuItem>
              </Select>
            </FormControl>
            {/* Gender Dropdown */}
            <FormControl variant="outlined" className={classes.formControl}>
              <InputLabel id="gender-select-label">Gender</InputLabel>
              <Select
                labelId="gender-select-label"
                id="gender-select"
                value={gender}
                onChange={(e) => setGender(e.target.value)}
                label="Gender"
              >
                <MenuItem value="">
                  <em>None</em>
                </MenuItem>
                <MenuItem value="Male">Male</MenuItem>
                <MenuItem value="Female">Female</MenuItem>
                <MenuItem value="Other">Other</MenuItem>
              </Select>
            </FormControl>
            {/* Further Location Dropdown */}
            <FormControl variant="outlined" className={classes.formControl}>
              <InputLabel id="further-location-select-label">
                Location
              </InputLabel>
              <Select
                labelId="further-location-select-label"
                id="further-location-select"
                value={furtherLocation}
                onChange={(e) => setFurtherLocation(e.target.value)}
                label="Further Location"
              >
                <MenuItem value="">
                  <em>None</em>
                </MenuItem>
                <MenuItem value="US">US</MenuItem>
                <MenuItem value="UK">UK</MenuItem>
                <MenuItem value="Canada">Canada</MenuItem>
              </Select>
            </FormControl>
            {/* For the 'Reset Filters' and 'AVG Views Filter' */}
            <FormControl
              variant="outlined"
              className={classes.formControl}
              fullWidth
            >
              <InputLabel id="avg-views-select-label">AVG Views</InputLabel>
              <Select
                labelId="avg-views-select-label"
                id="avg-views-select"
                value={avgViews}
                onChange={(e) => setAvgViews(e.target.value)}
                label="AVG Views"
              >
                {/* AVG Views MenuItems */}
                <MenuItem value="">
                  <em>None</em>
                </MenuItem>
                <MenuItem value="10-50000">10-50,000</MenuItem>
                <MenuItem value="50000-100000">50,000-100,000</MenuItem>
                <MenuItem value="100000+">100,000+</MenuItem>
              </Select>
            </FormControl>
            <TextField
              id="search-field"
              label="Search"
              variant="outlined"
              size="small"
              fullWidth
              className={classes.formControl}
              value={filter}
              onChange={handleFilterChange}
            />
          </Grid>
        </div>

        <Button
          variant="contained"
          color="primary"
          onClick={handleResetFilters}
          className={classes.button}
          fullWidth
        >
          Reset Filters
        </Button>
      </Grid>

      <div className={classes.csvSection}>
        {/* CSV Section */}
        <Paper elevation={3}>
          <table className="csvTable">
            <thead>
              <tr>
                <th>Select</th>

                {headers.map(
                  (header, index) =>
                    columnsToShow.includes(index + 1) && (
                      <th key={index} style={{ width: getColumnWidth(index) }}>
                        {header}
                      </th>
                    ) // Apply dynamic width based on column index
                )}
              </tr>
            </thead>
            <tbody>
              {filteredData.map((row, idx) => {
                if (!row) return null; // Ensure row is not undefined

                const isSelected = selectedItems.has(row[0]); // Check if the row is selected
                return (
                  <tr
                    key={idx}
                    style={{
                      backgroundColor: isSelected
                        ? "lightgreen"
                        : "transparent",
                    }} // Highlight selected rows
                    onClick={() => handleSelectItem(row[0])} // Add row click handler
                  >
                    <td>
                      <Checkbox
                        checked={isSelected}
                        onChange={(e) => {
                          e.stopPropagation(); // Prevent row click event from firing
                          handleSelectItem(row[0]);
                        }}
                        color="primary"
                      />
                    </td>
                    {row.map((value, index) =>
                      columnsToShow.includes(index + 1) ? (
                        <td
                          key={index}
                          style={{ width: getColumnWidth(index) }}
                        >
                          {index === 14 ? (
                            <a
                              href={value}
                              target="_blank"
                              rel="noopener noreferrer"
                            >
                              {value}
                            </a>
                          ) : (
                            value ?? "N/A"
                          )}
                        </td>
                      ) : null
                    )}
                  </tr>
                );
              })}
            </tbody>
          </table>
        </Paper>
      </div>
    </div>
  );
};

export default LeftColumn;
