import React, { useState } from 'react';
import Navbar from '../../Components/Navbar/NavBar';

const BlitzPay = () => {
  const [activeTab, setActiveTab] = useState('pending');

  const transactions = [
    { id: 1, campaignName: 'Lorem', payoutDate: '2022-01-01', paymentStatus: 'Completed', currencyType: 'USD', blitzPay: true, amount: 200 },
    { id: 2, campaignName: 'Ipsum', payoutDate: '2022-02-01', paymentStatus: 'Pending', currencyType: 'EUR', blitzPay: false, amount: 300 },
    { id: 3, campaignName: 'Dolor', payoutDate: '2022-03-01', paymentStatus: 'Completed', currencyType: 'GBP', blitzPay: true, amount: 100 },
    // More transactions...
  ];

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  // Calculators
  const creatorsPaid = transactions.length;
  const amountInEscrow = transactions.reduce((acc, curr) => curr.paymentStatus === 'Pending' ? acc + curr.amount : acc, 0);
  const blitzPaysUsed = transactions.filter(transaction => transaction.blitzPay).length;

  return (
    <div className="dashboard" style={{ fontFamily: 'Arial, sans-serif' }}>
      <Navbar />
      <div className="content" style={{ display: 'flex', flexDirection: 'row', padding: '20px' }}>
        <div className="left-column" style={{ width: '80%', paddingRight: '20px', borderRight: '2px solid #ddd' }}>
          <h1 className='header' style={{ borderBottom: '2px solid #888', paddingBottom: '10px', color: '#444' }}>Invoicing</h1>
          <div className="button-container" style={{ marginBottom: '20px', display: 'flex', justifyContent: 'space-between', maxWidth: '500px' }}>
            <button style={{ background: '#007bff', color: '#fff', border: 'none', padding: '10px 15px', cursor: 'pointer' }} onClick={() => handleTabClick('pending')}>Pending Transactions</button>
            <button style={{ background: '#28a745', color: '#fff', border: 'none', padding: '10px 15px', cursor: 'pointer' }} onClick={() => handleTabClick('all')}>All Transactions</button>
            <button style={{ background: '#ffc107', color: '#fff', border: 'none', padding: '10px 15px', cursor: 'pointer' }} onClick={() => handleTabClick('recent')}>Recent Payments</button>
          </div>
          <div className='table-container'>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead>
                <tr style={{ background: '#f8f9fa' }}>
                  <th style={{ border: '1px solid #dee2e6', padding: '8px' }}>ID</th>
                  <th style={{ border: '1px solid #dee2e6', padding: '8px' }}>Campaign Name</th>
                  <th style={{ border: '1px solid #dee2e6', padding: '8px' }}>Payout Date</th>
                  <th style={{ border: '1px solid #dee2e6', padding: '8px' }}>Payment Status</th>
                  <th style={{ border: '1px solid #dee2e6', padding: '8px' }}>Currency Type</th>
                  <th style={{ border: '1px solid #dee2e6', padding: '8px' }}>BlitzPay</th>
                  <th style={{ border: '1px solid #dee2e6', padding: '8px' }}>Amount</th>
                </tr>
              </thead>
              <tbody>
                {transactions.map((transaction, index) => (
                  <tr key={transaction.id} style={{ background: index % 2 === 0 ? '#fff' : '#f2f2f2' }}>
                    <td style={{ border: '1px solid #dee2e6', padding: '8px' }}>{transaction.id}</td>
                    <td style={{ border: '1px solid #dee2e6', padding: '8px' }}>{transaction.campaignName}</td>
                    <td style={{ border: '1px solid #dee2e6', padding: '8px' }}>{transaction.payoutDate}</td>
                    <td style={{ border: '1px solid #dee2e6', padding: '8px' }}>{transaction.paymentStatus}</td>
                    <td style={{ border: '1px solid #dee2e6', padding: '8px' }}>{transaction.currencyType}</td>
                    <td style={{ border: '1px solid #dee2e6', padding: '8px' }}>{transaction.blitzPay ? 'Yes' : 'No'}</td>
                    <td style={{ border: '1px solid #dee2e6', padding: '8px' }}>${transaction.amount}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
        <div className="right-column" style={{ width: '20%', paddingLeft: '10px' }}>
          <h2 style={{ borderBottom: '2px solid #888', paddingBottom: '10px', color: '#444' }}>Summary</h2>
          <p style={{ margin: '10px 0', fontWeight: 'bold' }}>Creators Paid: {creatorsPaid}</p>
          <p style={{ margin: '10px 0', fontWeight: 'bold' }}>Amount in Escrow: ${amountInEscrow}</p>
          <p style={{ margin: '10px 0', fontWeight: 'bold' }}>BlitzPays Used: {blitzPaysUsed}</p>
        </div>
      </div>
    </div>
  );
};

export default BlitzPay;
