import React, { useState, useEffect } from 'react';
import './dashboard.css'; // Ensure your CSS file has appropriate styles defined
import Navbar from '../../../Components/Navbar/NavBar';
import { Button, TextField, Dialog, DialogActions, DialogContent, DialogTitle, Table, TableBody, TableCell, TableHead, TableRow } from '@mui/material';
import axios from 'axios'; // Assuming Axios is used for HTTP requests

const Dashboard = () => {
  const [selectedTime, setSelectedTime] = useState('day');
  const [open, setOpen] = useState(false);
  const [campaigns, setCampaigns] = useState([]);
  const [totalBudget, setTotalBudget] = useState(0);
  const [totalSpentThisMonth, setTotalSpentThisMonth] = useState(0);
  const [recaps, setRecaps] = useState([]);

  useEffect(() => {
    fetchCampaigns();
    // Add more fetch calls as needed
  }, []);

  const fetchCampaigns = async () => {
    try {
      const response = await axios.get('http://localhost:5000/campaigns');
      setCampaigns(response.data.campaigns);
      calculateTotalBudget(response.data.campaigns);
    } catch (error) {
      console.error("Failed to fetch campaigns:", error);
    }
  };

  const calculateTotalBudget = (campaigns) => {
    const total = campaigns.reduce((acc, campaign) => acc + campaign.campaign_sum, 0);
    setTotalBudget(total);
  };

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const handleTimeSelection = (e) => setSelectedTime(e.target.value);

  // Placeholder component
  const PhotoChart = ({ timePeriod }) => <div>Chart Placeholder for {timePeriod}</div>;

  return (
    <div className="dashboard">
      <Navbar />
      <div className="content">
        <div className="column-left">
          <h2>Active Campaigns</h2>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Campaign Name</TableCell>
                <TableCell>Total Budget</TableCell>
                <TableCell>Creators Involved</TableCell>
                {/* Add more columns as needed */}
              </TableRow>
            </TableHead>
            <TableBody>
              {campaigns.map((campaign) => (
                <TableRow key={campaign.id}>
                  <TableCell>{campaign.name}</TableCell>
                  <TableCell>${campaign.campaign_sum}</TableCell>
                  <TableCell>{campaign.creator_count}</TableCell>
                  {/* Add more cell data as needed */}
                </TableRow>
              ))}
            </TableBody>
          </Table>
          <div className="total-budget">Total Budget Across Campaigns: ${totalBudget}</div>
          
          {/* Additional views or components as requested */}
          <div className="total-spent-this-month">
            <h3>Total Spent This Month</h3>
            <div>${totalSpentThisMonth}</div>
          </div>
  
          <div className="recaps-view">
            <h3>All Recaps</h3>
            {/* Assuming recaps is an array of recap information */}
            {recaps.map((recap, index) => (
              <div key={index}>
                <h4>{recap.title}</h4>
                <p>{recap.summary}</p>
                {/* More detailed recap information as needed */}
              </div>
            ))}
          </div>
        </div>
        <div className="column-right">
          <h2>Payment Statistics</h2>
          <div className="time-selection">
            <label htmlFor="time-select">Select time:</label>
            <select id="time-select" value={selectedTime} onChange={handleTimeSelection}>
              <option value="day">Day</option>
              <option value="week">Week</option>
              <option value="month">Month</option>
            </select>
          </div>
          <div className="chart">
            <PhotoChart timePeriod={selectedTime} />
          </div>
        </div>
      </div>
      
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Request Payment</DialogTitle>
        <DialogContent>
          <Button variant="contained" color="primary" size="large">Connect Stripe</Button>
          <Button variant="contained" color="primary" size="large">Connect Paypal</Button>
          <TextField label="Bill To" fullWidth margin="dense" />
          <TextField label="Invoice Title" fullWidth margin="dense" />
          <TextField label="Bank Name" fullWidth margin="dense" />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">Cancel</Button>
          <Button onClick={handleClose} color="primary">Submit</Button>
        </DialogActions>
      </Dialog>
      <div className="content">
        <div className="column-left">
          <h2>Macro Financials</h2>
          <div className="balance-section">
            <h3>Total Balance</h3>
            <div className="balance-number">$12,000</div>
            <div className="buttons">
              <Button variant="contained" size="large" color="primary" onClick={handleOpen}>Request</Button>
              <Button variant="contained" size="large" color="primary" onClick={handleOpen}>Pay</Button>
