import React, { useState, useEffect } from 'react';
import { Dialog, DialogActions, DialogContent, DialogTitle, Button, TextField, Checkbox, FormControlLabel, Autocomplete, Box } from '@mui/material';
import './blitzpay.css';

const PayCreatorDialog = ({ open, onClose, onSubmit }) => {
  const [paymentAmount, setPaymentAmount] = useState('');
  const [poNumber, setPoNumber] = useState('');
  const [notes, setNotes] = useState('');
  const [selectedCreator, setSelectedCreator] = useState(null);
  const [creators, setCreators] = useState([]);
  const [blitzPay] = useState([]);

  useEffect(() => {
    const fetchCreatorsData = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/creators');
            if (!response.ok) throw new Error('Network response was not ok');
            const data = await response.json();
            const formattedData = data.map(creator => ({
                ...creator,
                label: creator.creator // Assuming 'creator' field is the name of the creator
            }));
            setCreators(formattedData);
        } catch (error) {
            console.error('There was a problem with your fetch operation:', error);
        }
    };

    fetchCreatorsData();
}, []);


  const handleGeneratePO = () => {
    setPoNumber(`PO-${Math.floor(Math.random() * 10000)}`);
  };

  const handleSubmit = () => {
    if (!selectedCreator || !paymentAmount || !poNumber) {
        alert('Please fill in all required fields.');
        return;
    }

    // Structure the form data according to backend expectations
    const submissionData = {
        creator_id: selectedCreator.creator, // Assuming id is the correct identifier
        amount: parseFloat(paymentAmount), // Ensure amount is a float
        po_number: poNumber,
        blitzPay: false,
    };

    // onSubmit should be a prop passed from parent component
    onSubmit(submissionData).then(() => {
        onClose(); // Close the dialog on successful submission
    }).catch(error => {
        console.error('Submission failed', error);
    });
};

  return (
    <Dialog open={open} onClose={onClose} aria-labelledby="form-dialog-title">
      <DialogTitle id="form-dialog-title">Pay a Creator</DialogTitle>
      <DialogContent>
      <Autocomplete
    options={creators}
    getOptionLabel={(option) => option.label || ''} // Use the label property
    value={selectedCreator}
    onChange={(event, newValue) => {
        setSelectedCreator(newValue);
    }}
    renderInput={(params) => (
        <TextField {...params} label="Select a Creator" variant="outlined" required />
    )}
/>

        <TextField
          margin="dense"
          label="Payment Amount"
          type="number"
          fullWidth
          value={paymentAmount}
          onChange={(e) => setPaymentAmount(e.target.value)}
          variant="outlined"
          required
        />
        
        <Box display="flex" alignItems="center" gap={2}>
          <TextField
            margin="dense"
            label="PO #"
            fullWidth
            value={poNumber}
            onChange={(e) => setPoNumber(e.target.value)}
            variant="outlined"
            required
          />
          <Button variant="outlined" onClick={handleGeneratePO}>Generate PO</Button>
        </Box>
        <TextField
          margin="dense"
          label="Notes"
          fullWidth
          multiline
          rows={4}
          value={notes}
          onChange={(e) => setNotes(e.target.value)}
          variant="outlined"
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose} color="primary">Cancel</Button>
        <Button onClick={handleSubmit} color="primary">Submit</Button>
      </DialogActions>
    </Dialog>
  );
};

export default PayCreatorDialog;
