import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom'; // Replace useHistory with useNavigate
import {
  Container,
  Grid,
  Paper,
  Typography,
  Button,
} from '@mui/material';
import Navbar from '../../../Components/Navbar/NavBar';

// Assuming these components or similar functionality exist
import SearchFilterSection from '../Search/search'; // Represents the left side search functionality
import CampaignDetailsSection from './campaigns'; // Represents the right side campaign details

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
}));

const AddCreators = () => {
    const { campaignId } = useParams(); // Get campaignId from URL parameters
    const navigate = useNavigate(); // useNavigate for navigation
  
    // State hooks and effects to load campaign details and creators
    const [campaignDetails, setCampaignDetails] = useState(null);
    const [selectedCreators, setSelectedCreators] = useState([]);
  
    useEffect(() => {
      // Fetch campaign details and set state
    }, [campaignId]);
  
    const handleConfirmCreatorChanges = () => {
      // Implement the logic to update campaign with new creators
      console.log('Confirmed changes for', campaignId, 'with creators', selectedCreators);
      // Redirect or show success message
      navigate('/campaigns'); // Use navigate to go back to campaigns overview
    };

  return (
    <Container>
      <Navbar />
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Typography variant="h4" gutterBottom>
            Add Creators to Campaign
          </Typography>
        </Grid>
        <Grid item xs={12} md={6}>
          {/* Left side - Search functionality */}
          <SearchFilterSection setSelectedCreators={setSelectedCreators} /> {/* Make sure to pass the correct props */}
        </Grid>
        <Grid item xs={12} md={6}>
          {/* Right side - Campaign details */}
          <CampaignDetailsSection campaignDetails={campaignDetails} /> {/* Adjust prop names as necessary */}
          <Divider style={{ margin: '20px 0' }} />
          <Button
            variant="contained"
            color="primary"
            fullWidth
            onClick={handleConfirmCreatorChanges}
          >
            Confirm Creator Changes
          </Button>
        </Grid>
      </Grid>
    </Container>
  );
};

export default AddCreators;
