import React, { useState, useEffect } from 'react';
import {
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  TextField,
  Button,
  makeStyles,
  Checkbox,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    width: '100%',
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  button: {
    margin: theme.spacing(1),
  },
  searchField: {
    margin: theme.spacing(1),
    minWidth: 120,
    width: '100%',
  },
  table: {
    minWidth: 650,
  },
  filterContainer: {
    marginBottom: theme.spacing(3),
  },
  buttonContainer: {
    display: 'flex',
    justifyContent: 'space-between',
    padding: theme.spacing(2),
  },
  csvTable: {
    width: '100%',
  },
}));

const LeftColumn = () => {
  const classes = useStyles();
  const [filter, setFilter] = useState('');
  const [region, setRegion] = useState('');
  const [platform, setPlatform] = useState('TikTok');
  const [promotionType, setPromotionType] = useState('Brand');
  const [data, setData] = useState([]);
  const [race, setRace] = useState('');
  const [gender, setGender] = useState('');
  const [furtherLocation, setFurtherLocation] = useState('');
  const [selectedItems, setSelectedItems] = useState(new Set());
  const [avgViews, setAvgViews] = useState('');

  const handleResetFilters = () => {
    setRegion('');
    setPlatform('TikTok');
    setPromotionType('Brand');
    setRace('');
    setGender('');
    setFurtherLocation('');
    setFilter('');
    setAvgViews('');
  };

  useEffect(() => {
    // Place your data fetching logic here if necessary
  }, []);

  const handleSelectItem = (itemId) => {
    const newSelection = new Set(selectedItems);
    if (newSelection.has(itemId)) {
      newSelection.delete(itemId);
    } else {
      newSelection.add(itemId);
    }
    setSelectedItems(newSelection);
  };

  return (
    <div>
      <Grid container spacing={3}>
        <Grid item xs={12} className={classes.filterContainer}>
          {/* Filters */}
          <FormControl variant="outlined" className={classes.formControl}>
            <InputLabel id="region-label">Region</InputLabel>
            <Select
              labelId="region-label"
              id="region-select"
              value={region}
              onChange={(e) => setRegion(e.target.value)}
              label="Region"
            >
              <MenuItem value=""><em>None</em></MenuItem>
              {/* Add regions here */}
            </Select>
          </FormControl>

          {/* Similar FormControl blocks for platform, promotionType, race, gender, furtherLocation */}

          <TextField
            id="search-field"
            label="Search"
            variant="outlined"
            className={classes.searchField}
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
            fullWidth
          />

          <Button
            variant="contained"
            color="primary"
            onClick={handleResetFilters}
            className={classes.button}
            fullWidth
          >
            Reset Filters
          </Button>
        </Grid>

        <div className={classes.csvSection}>
          {/* CSV Section */}
          <Paper elevation={3}>
            <table className="csvTable">
              <thead>
                <tr>
                  <th>Select</th>

                  {headers.map(
                    (header, index) =>
                      columnsToShow.includes(index + 1) && (
                        <th
                          key={index}
                          style={{ width: getColumnWidth(index) }}
                        >
                          {header}
                        </th>
                      ), // Apply dynamic width based on column index
                  )}
                </tr>
              </thead>
              <tbody>
                {filteredData.map((row, idx) => {
                  if (!row) return null; // Ensure row is not undefined

                  const isSelected = selectedItems.has(row[0]); // Check if the row is selected
                  return (
                    <tr
                      key={idx}
                      style={{
                        backgroundColor: isSelected
                          ? "lightgreen"
                          : "transparent",
                      }} // Highlight selected rows
                      onClick={() => handleSelectItem(row[0])} // Add row click handler
                    >
                      <td>
                        <Checkbox
                          checked={isSelected}
                          onChange={(e) => {
                            e.stopPropagation(); // Prevent row click event from firing
                            handleSelectItem(row[0]);
                          }}
                          color="primary"
                        />
                      </td>
                      {row.map((value, index) =>
                        columnsToShow.includes(index + 1) ? (
                          <td
                            key={index}
                            style={{ width: getColumnWidth(index) }}
                          >
                            {index === 14 ? (
                              <a
                                href={value}
                                target="_blank"
                                rel="noopener noreferrer"
                              >
                                {value}
                              </a>
                            ) : (
                              value ?? "N/A"
                            )}
                          </td>
                        ) : null,
                      )}
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </Paper>
        </div>
      </Grid>
    </div>
  );
};

export default LeftColumn;
