import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Container, Grid, Typography, Button, Divider } from "@mui/material";
import Navbar from "../../../Components/Navbar/NavBar";
import { makeStyles } from "@material-ui/core/styles";
import SearchFilterSection from "../Search/search";
import CampaignDetailsUI from "./campaigndetailsUI";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "center",
    color: theme.palette.text.secondary,
  },
}));

const AddCreators = () => {
  const classes = useStyles();
  const { campaignId } = useParams();
  const navigate = useNavigate();
  const [campaignDetails, setCampaignDetails] = useState(null);
  const [selectedCreators, setSelectedCreators] = useState([]);

  useEffect(() => {
    const fetchCampaignDetails = async () => {
      // Replace this URL with the correct endpoint from your backend
      const url = `"http://localhost:5000/api/campaigns/${campaignId}");
      `;
      try {
        const response = await fetch(url);
        if (!response.ok) {
          throw new Error('Failed to fetch campaign details');
        }
        const data = await response.json();
        setCampaignDetails(data);
      } catch (error) {
        console.error("Error fetching campaign details:", error);
        // Optionally handle error state here
      }
    };

    if (campaignId) {
      fetchCampaignDetails();
    }
  }, [campaignId]);

  const handleConfirmCreatorChanges = () => {
    navigate("/campaigns");
  };

  return (
    <Container>
      <Navbar />
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Typography variant="h4" gutterBottom>
            Add Creators to Campaign
          </Typography>
        </Grid>
        <Grid item xs={12} md={6}>
          <SearchFilterSection setSelectedCreators={setSelectedCreators} />
        </Grid>
        <Grid item xs={12} md={6}>
          <CampaignDetailsUI campaignDetails={campaignDetails} />
          <Divider style={{ margin: "20px 0" }} />
          <Button variant="contained" color="primary" fullWidth onClick={handleConfirmCreatorChanges}>
            Confirm Creator Changes
          </Button>
        </Grid>
      </Grid>
    </Container>
  );
};

export default AddCreators;
