import React, { useState } from 'react';
import { Dialog, DialogActions, DialogContent, DialogTitle, Button, TextField, Checkbox, FormControlLabel, Select, MenuItem, FormControl, InputLabel } from '@material-ui/core';
import Navbar from '../../Components/Navbar/NavBar';
import './blitzpay.css';

const PayCreatorDialog = ({ open, onClose, onSubmit }) => {
  const [paymentAmount, setPaymentAmount] = useState('');
  const [paymentTerm, setPaymentTerm] = useState('');
  const [blitzPay, setBlitzPay] = useState(false);
  const [poNumber, setPoNumber] = useState('');
  const [notes, setNotes] = useState('');
  const [selectedCreator, setSelectedCreator] = useState('');

  // Dummy data for creator selection
  const creators = [
    { name: 'John Doe', id: '1' },
    { name: 'Jane Smith', id: '2' },
    // Add more dummy creators as needed
  ];

  const handleCreatorChange = (event) => {
    setSelectedCreator(event.target.value);
  };

  const handleBlitzPayChange = (event) => {
    setBlitzPay(event.target.checked);
  };

  const handleSubmit = () => {
    onSubmit({
      selectedCreator,
      paymentAmount,
      paymentTerm,
      blitzPay,
      poNumber,
      notes,
    });
    onClose();
  };

  return (
    <Dialog open={open} onClose={onClose} aria-labelledby="form-dialog-title">
      <DialogTitle id="form-dialog-title">Pay a Creator</DialogTitle>
      <DialogContent>
        <FormControl fullWidth margin="dense" variant="outlined">
          <InputLabel id="creator-select-label">Select a Creator</InputLabel>
          <Select
            labelId="creator-select-label"
            id="creator-select"
            value={selectedCreator}
            onChange={handleCreatorChange}
            label="Select a Creator"
          >
            {creators.map((creator) => (
              <MenuItem key={creator.id} value={creator.id}>{creator.name}</MenuItem>
            ))}
          </Select>
        </FormControl>
        <TextField
          margin="dense"
          label="Payment Amount"
          type="number"
          fullWidth
          value={paymentAmount}
          onChange={(e) => setPaymentAmount(e.target.value)}
          variant="outlined"
        />
        <TextField
          margin="dense"
          label="Payment Term"
          fullWidth
          value={paymentTerm}
          onChange={(e) => setPaymentTerm(e.target.value)}
          variant="outlined"
        />
        <FormControlLabel
          control={<Checkbox checked={blitzPay} onChange={handleBlitzPayChange} />}
          label="BlitzPay"
        />
        <TextField
          margin="dense"
          label="PO #"
          fullWidth
          value={poNumber}
          onChange={(e) => setPoNumber(e.target.value)}
          variant="outlined"
        />
        <TextField
          margin="dense"
          label="Notes"
          fullWidth
          multiline
          rows={4}
          value={notes}
          onChange={(e) => setNotes(e.target.value)}
          variant="outlined"
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose} color="primary">Cancel</Button>
        <Button onClick={handleSubmit} color="primary">Submit</Button>
      </DialogActions>
    </Dialog>
  );
};

// Ensure the rest of your BlitzPay component remains the same.
