const SearchPage = (onCreatorSelect) => {
  const classes = useStyles();
  const [filter, setFilter] = useState("");
  const [region, setRegion] = useState(""); // Default to showing ALL Regions
  const [platform, setPlatform] = useState("TikTok"); // Default to TikTok Brand, and remove the option for None
  const [promotionType, setPromotionType] = useState("Brand"); // Assuming you want to start with TikTok Brand
  const [data, setData] = useState([]);
  const [race, setRace] = useState("");
  const [gender, setGender] = useState("");
  const [furtherLocation, setFurtherLocation] = useState("");
  const [selectedItems, setSelectedItems] = useState(new Set());
  const [avgViews, setAvgViews] = useState("");
  const [selectedCreatorsData, setSelectedCreatorsData] = useState([]);
  const [campaignName, setCampaignName] = useState("");
  const [campaignBrief, setCampaignBrief] = useState("");
  const [videoAmount, setVideoAmount] = useState("1");
  const [campaignType, setCampaignType] = useState("");
  const [openDialog, setOpenDialog] = useState(false);
  const [dialogMessage, setDialogMessage] = useState("");
  const [campaignSum, setCampaignSum] = useState(0);
  const [idealDueDate, setIdealDueDate] = useState(new Date());
  const [isCreatorDialogOpen, setIsCreatorDialogOpen] = useState(false);
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);
  const [tableHeight, setTableHeight] = useState(0);

  const filterRef = useRef(null);

  const { mutate: createDataRead } = useMutation(client.creators.data, {
    onSuccess: (data) => {
      setData(data); // Assuming jsonData is an array of arrays
    },
    onError: (error) => {
      console.error("Failed to fetch data:", error);
    },
  });

  useEffect(() => {
    const sum = calculateTotalCampaignSum();
    setCampaignSum(sum);
    createDataRead();
  }, [selectedCreatorsData]);

  useEffect(() => {
    filterRef.current &&
      setTableHeight(
        document.documentElement.offsetHeight -
          filterRef.current.clientHeight -
          82,
      );
  }, [filterRef, document.documentElement.offsetHeight]);

  const handleSelectItem = (creatorId) => {
    // Toggle the selection state of the item.
    const isSelected = selectedItems.has(creatorId);
    if (isSelected) {
      selectedItems.delete(creatorId);
    } else {
      selectedItems.add(creatorId);
    }

    // Update the state with the new set of selected items.
    setSelectedItems(new Set(selectedItems));

    // Find the selected creator from the data array.
    const selectedCreator = data.find(
      (creator) => creator.creator === creatorId,
    );
    if (!selectedCreator) {
      console.warn(`Item with ID ${creatorId} not found.`);
      return; // Early exit if the item isn't found.
    }

    // Prepare the data object for the selected item.
    // Dynamically determine the price based on the selected promotion type.
    let priceKey = "";
    if (platform === "TikTok" && promotionType === "Sound") {
      priceKey = "Tiktok Sound";
    } else if (platform === "TikTok" && promotionType === "Brand") {
      priceKey = "Tiktok Brand";
    } else if (platform === "Instagram" && promotionType === "Feed Post") {
      priceKey = "IG Feed Post";
    } else if (platform === "Instagram" && promotionType === "Sound") {
      priceKey = "IG Reels Sound";
    } else if (platform === "Instagram" && promotionType === "Brand") {
      priceKey = "IG Reels Brand";
    } // Continue for other combinations
    const price = selectedCreator[priceKey] || "Price Unavailable";

    const relevantData = {
      id: selectedCreator.creator, // Ensure you have a unique ID for each creator
      name: selectedCreator.creator, // Example property
      price: price, // Include price if necessary, though ensure backend expects this format
    };
    // Update selection logic accordingly
    if (!isSelected) {
      setSelectedCreatorsData((prev) => [...prev, relevantData]);
    } else {
      setSelectedCreatorsData((prev) =>
        prev.filter((item) => item.id !== creatorId),
      );
    }

    // Update onCreatorSelect logic if necessary
    if (onCreatorSelect) {
      onCreatorSelect(relevantData);
    }
  };
  const calculateTotalCampaignSum = () => {
    const sum = selectedCreatorsData.reduce((accumulator, creator) => {
      // Remove any non-numeric characters (including the dollar sign) before conversion
      const cleanedPriceString = creator.price.replace(/[^0-9.-]+/g, "");
      const price = parseFloat(cleanedPriceString);
      // Check if the conversion was successful (i.e., the result is a finite number)
      if (isFinite(price)) {
        return accumulator + price;
      } else {
        console.error(`Invalid price format detected: ${creator.price}`);
        return accumulator;
      }
    }, 0);
    return sum;
  };

  // Function to generate a unique campaign ID
  const generateCampaignID = () => new Date().getTime().toString();
  // Before creating the campaignData object
  const parsedVideoAmount = parseInt(videoAmount, 10); // Ensure videoAmount is an integer
  const today = new Date().toISOString().slice(0, 10);

  // Function to create campaign data object
  const createCampaignData = () => {
    const newCreators = selectedCreatorsData.map((creator) => {
      // Construct the object expected by the backend for each creator
      // This is an example, adjust according to the actual expected structure
      return {
        id: creator.id,
        name: creator.id,
        price: creator.price /* other properties as needed */,
      };
    });
    return {
      id: generateCampaignID(),
      name: campaignName,
      brief: campaignBrief,
      videoAmount: parsedVideoAmount,
      campaignType: campaignType,
      creators: newCreators,
      proposalDate: today,
      campaign_sum: campaignSum, // Add the campaign sum here
      idealDueDate: idealDueDate, // Format the date as YYYY-MM-DD
    };
  };

  // Function to handle creating a new campaign
  const { mutate: createCampaigns } = useMutation(client.campaigns.create, {
    onSuccess: (data) => {
      console.log("Campaign saved successfully:", data);

      setDialogMessage("Campaign created successfully!");
      setOpenDialog(true);

      setSelectedItems(new Set());
      setCampaignName("");
      setCampaignBrief("");
    },
    onError: (error) => {
      console.error("Error saving campaign:", error);
      setDialogMessage(`Error: ${error.message}`);
      setOpenDialog(true);
    },
  });
  const handleCreatorSelect = (creator) => {
    setSelectedCreatorsData((prevCreators) => [...prevCreators, creator]);
  };
  const handleCreateCampaign = async () => {
    console.log("Creating campaign..."); // Initial log to confirm the function is called
    const campaignData = createCampaignData();
    createCampaigns(campaignData);
  };
  const handleCreatorSubmit = (formData) => {
    console.log(formData); // For now, just log the form data. You can replace this with your actual submission logic.
  };

  <Dialog
    open={openDialog}
    onClose={() => setOpenDialog(false)}
    aria-labelledby="alert-dialog-title"
    aria-describedby="alert-dialog-description"
  >
    <DialogTitle id="alert-dialog-title">
      {"Campaign Creation Status"}
    </DialogTitle>
    <DialogContent>
      <DialogContentText id="alert-dialog-description">
        {dialogMessage}
      </DialogContentText>
    </DialogContent>
    <DialogActions>
      <Button onClick={() => setOpenDialog(false)} color="primary" autoFocus>
        Close
      </Button>
    </DialogActions>
  </Dialog>;

  return (
    <div className={classes.root}>
      <Navbar className={classes.navbar} />
      <div className={classes.content}>
        <SearchFilterSection onCreatorSelect={handleCreatorSelect} />
      </div>
      <CreatorDialog
        open={isCreatorDialogOpen}
        onClose={() => setIsCreatorDialogOpen(false)}
        onSubmit={handleCreatorSubmit}
      />
      <div className={classes.rightColumn}>
        <Paper className={classes.paper} elevation={3}>
          <Typography
            variant="h4"
            gutterBottom
            className={classes.header}
            align="center"
          >
            Campaign Builder
          </Typography>
          <Divider style={{ margin: "10px 0" }} />
          <Grid container spacing={2} alignItems="center">
            <Grid item xs={12}>
              <TextField
                label="Campaign Name"
                variant="outlined"
                fullWidth
                value={campaignName} // Set value from state
                onChange={(e) => setCampaignName(e.target.value)} // Update state on change
              />
            </Grid>

            <Grid item xs={12}>
              <TextField
                label="Campaign Brief"
                variant="outlined"
                fullWidth
                value={campaignBrief} // Set value from state
                onChange={(e) => setCampaignBrief(e.target.value)} // Update state on change
              />
            </Grid>
            <Grid item xs={12}>
              <Grid item xs={12}>
                <TextField
                  id="date-picker"
                  label="Due Date"
                  type="date"
                  fullWidth
                  variant="outlined"
                  value={idealDueDate}
                  onChange={(e) => setIdealDueDate(e.target.value)}
                  InputLabelProps={{
                    shrink: true, // This makes the label visible
                  }}
                />
              </Grid>
            </Grid>
            <Grid item xs={12} style={{}}>
              <Typography variant="h6" align="center">
                Creator Details
              </Typography>
            </Grid>

            <Grid item xs={12} style={{}}>
              {selectedCreatorsData.map((creator, index) => (
                <Typography key={index} variant="body1">
                  {creator.name} - {creator.price}
                </Typography>
              ))}
            </Grid>
            <Grid item xs={12}>
              <Typography variant="h6" align="center">
                Campaign Sum: ${campaignSum}
              </Typography>
            </Grid>

            {/* Split "Video Amount" and "Campaign Type" into two columns */}
            <Grid item xs={6}>
              <FormControl fullWidth>
                <InputLabel>Video Amount</InputLabel>
                <Select
                  value={videoAmount}
                  onChange={(e) => setVideoAmount(e.target.value)}
                  label="Video Amount"
                >
                  <MenuItem value={1}>1</MenuItem>
                  <MenuItem value={2}>2</MenuItem>
                  <MenuItem value={3}>3</MenuItem>
                  {/* Add more options as needed */}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={6}>
              <FormControl fullWidth>
                <InputLabel>Campaign Type</InputLabel>
                <Select
                  value={campaignType} // Set value from state
                  onChange={(e) => setCampaignType(e.target.value)} // Update state on change
                  label="Campaign Type"
                >
                  <MenuItem value="product">Product Promotion</MenuItem>
                  <MenuItem value="app">App Promotion</MenuItem>
                  <MenuItem value="website">Website Promotion</MenuItem>
                  <MenuItem value="song">Song Promotion</MenuItem>
                  <MenuItem value="other">Other</MenuItem>
                </Select>
              </FormControl>
            </Grid>

            <Grid item xs={12}>
  <Button
    variant="contained"
    color="secondary"
    fullWidth
    onClick={() => {
      setSelectedItems(new Set()); // Clears the selection set
      setSelectedCreatorsData([]); // Clears the selected creators data
    }}
  >
    Remove Creators
  </Button>
</Grid>

            <Grid item xs={12}>
              <Button
                variant="contained"
                color="primary"
                fullWidth
                onClick={handleCreateCampaign} // Make sure this directly calls the function
              >
                Create Campaign!
              </Button>
            </Grid>
            <Dialog
              open={openDialog}
              onClose={() => setOpenDialog(false)}
              aria-labelledby="alert-dialog-title"
              aria-describedby="alert-dialog-description"
            >
              <DialogTitle id="alert-dialog-title">
                {"Campaign Creation Status"}
              </DialogTitle>
              <DialogContent>
                <DialogContentText id="alert-dialog-description">
                  {dialogMessage}
                </DialogContentText>
              </DialogContent>
              <DialogActions>
                <Button
                  onClick={() => setOpenDialog(false)}
                  color="primary"
                  autoFocus
                >
                  Close
                </Button>
              </DialogActions>
            </Dialog>
          </Grid>
        </Paper>
      </div>
    </div>
  );
};

export default SearchPage;
