import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Container,
  Grid,
  Typography,
  Button,
  Divider,
} from '@mui/material';
import Navbar from '../../../Components/Navbar/NavBar';
import { makeStyles } from "@material-ui/core/styles";

// Assuming these components or similar functionality exist
// Ensure that you have correctly imported these components
import SearchFilterSection from '../Search/search';
import CampaignDetailsSection from './campaigns';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
}));

const AddCreators = () => {
    const classes = useStyles(); // Use useStyles hook here
    const { campaignId } = useParams();
    const navigate = useNavigate();
  
    // State hooks and effects
    const [campaignDetails, setCampaignDetails] = useState(null);
    const [selectedCreators, setSelectedCreators] = useState([]);
  
    useEffect(() => {
      // Logic to fetch campaign details
    }, [campaignId]);
  
    const handleConfirmCreatorChanges = () => {
      // Logic to handle confirmation of creator changes
      navigate('/campaigns');
    };

  return (
    <Container>
      <Navbar />
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Typography variant="h4" gutterBottom>
            Add Creators to Campaign
          </Typography>
        </Grid>
        <Grid item xs={12} md={6}>
          <SearchFilterSection setSelectedCreators={setSelectedCreators} />
        </Grid>
        <Grid item xs={12} md={6}>
          <CampaignDetailsSection campaignDetails={campaignDetails} />
          <Divider style={{ margin: '20px 0' }} />
          <Button variant="contained" color="primary" fullWidth onClick={handleConfirmCreatorChanges}>
            Confirm Creator Changes
          </Button>
        </Grid>
      </Grid>
    </Container>
  );
};

export default AddCreators;
