import React, { useState, useEffect } from 'react';
import Navbar from '../../Components/Navbar/NavBar';
import './blitzpay.css';
import PayCreatorDialog from './payCreator'; // Assuming PayCreatorDialog is in the same directory

const BlitzPay = () => {
  const [activeTab, setActiveTab] = useState('pending');
  const [isPayCreatorDialogOpen, setIsPayCreatorDialogOpen] = useState(false);
  // New state for storing payouts fetched from the backend
  const [payouts, setPayouts] = useState([]);

  // Fetch payouts data from the backend when the component mounts
  useEffect(() => {
    fetch('/api/financial/payouts')
      .then((response) => response.json())
      .then((data) => {
        setPayouts(data); // Assume the data is an array of payout objects
      })
      .catch((error) => console.error('Error fetching payouts:', error));
  }, []);

  const handleTabClick = (tab) => {
    setActiveTab(tab);
    if (tab === 'pay') {
      setIsPayCreatorDialogOpen(true); // Open the dialog when the "Pay a Creator" tab is clicked
    }
  };

  const handleDialogClose = () => {
    setIsPayCreatorDialogOpen(false);
  };

  // Handle form submission to send payout information to the backend
  const handleDialogSubmit = (formData) => {
    fetch('/api/financial/payouts', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Payout submitted:', data);
        // Optionally refresh the payouts list or handle success
        setPayouts([...payouts, data]); // Assuming 'data' is the newly created payout
      })
      .catch((error) => {
        console.error('Error submitting payout:', error);
      });

    setIsPayCreatorDialogOpen(false);
  };

  return (
    <div className="dashboard">
      <Navbar />
      <div className="content flex-row">
        <div className="left-column">
          <h1 className='header'>Blitz Pay</h1>
          <div className="button-container">
            <button className="btn" onClick={() => handleTabClick('pending')}>Pending Transactions</button>
            <button className="btn" onClick={() => handleTabClick('all')}>All Transactions</button>
            <button className="btn" onClick={() => handleTabClick('recent')}>Recent Payments</button>
            <button className="btn" onClick={() => handleTabClick('pay')}>Pay a Creator</button>
          </div>
          {/* Assuming you want to integrate fetched payouts data */}
          <div className='table-container'>
            {/* Your table or another component to display payouts */}
          </div>
        </div>
        <div className="right-column">
          <h2>Summary</h2>
          {/* Summary information */}
        </div>
      </div>
      <PayCreatorDialog
        open={isPayCreatorDialogOpen}
        onClose={handleDialogClose}
        onSubmit={handleDialogSubmit}
      />
    </div>
  );
};

export default BlitzPay;
