import React, { useState, useEffect } from "react";
import "./search.css";
import Navbar from "../../../Components/Navbar/NavBar";
import {
  TextField,
  Button,
  Select,
  MenuItem,
  InputLabel,
  FormControl,
  Grid,
  Paper,
  Typography,
  Checkbox,
  Divider,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
} from "@material-ui/core";

import { makeStyles } from "@material-ui/core/styles";
import { KeyboardDatePicker } from "@material-ui/pickers";

const drawerWidth = "14.5rem"; // Assuming your Navbar width is 240px

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexDirection: "row",
    height: "100vh",
    overflow: "hidden",
  },
  navbar: {
    width: drawerWidth,
    flexShrink: 0,
    zIndex: 2,
  },
  content: {
    display: "flex",
    flexDirection: "column", // Changed to column to stack filter and csv sections vertically
    flexGrow: 1,
    paddingLeft: drawerWidth, // Add padding equal to the navbar width
    width: `calc(80% - ${drawerWidth}px)`, // Allocate 75% of the width minus the navbar width
    overflowY: "auto", // Allow vertical scrolling within the content area if necessary
  },
  filterSection: {
    backgroundColor: "#f0f0f0",
    padding: theme.spacing(3),
  },
  csvSection: {
    backgroundColor: "#e8e8e8",
    padding: theme.spacing(0.9),
    height: "100%", // Set a fixed height
    overflowY: "auto", // Enable vertical scrolling within the CSV section
    width: "100%", // Ensure the csvSection takes the full width of its parent
    overflowX: "auto", // Allow horizontal scrolling within the csvSection if necessary
  },

  rightColumn: {
    width: "25%", // Set the right column to take 25% of the screen
    overflowY: "auto", // Allow vertical scrolling within the right column if necessary
  },

  formControl: {
    margin: theme.spacing(1.5),
    minWidth: 150,
  },

  searchField: {
    margin: theme.spacing(1.5), // This adds margin all around the TextField
    marginLeft: theme.spacing(6), // This adds additional margin to the left
    minWidth: 150,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  button: {
    margin: theme.spacing(1), // Uniform margin for buttons
    padding: theme.spacing(1), // Adjust padding to ensure buttons are not too large or too small
    width: "100%", // Make buttons take full width of their container for consistency
  },
  filterContainer: {
    padding: theme.spacing(2), // Add padding around the filter container for spacing
  },
  buttonContainer: {
    padding: theme.spacing(2), // Ensure buttons have their own padding
    display: "flex",
    flexDirection: "column",
    gap: theme.spacing(2), // Use gap property to space out buttons
  },
  paper: {
    padding: theme.spacing(2),
    marginBottom: theme.spacing(2),
  },
  header: {
    margin: theme.spacing(1),
    fontWeight: "bold",
  },
}));

const SearchPage = () => {
  const classes = useStyles();
  const [filter, setFilter] = useState("");
  const [region, setRegion] = useState(""); // Default to showing ALL Regions
  const [platform, setPlatform] = useState("TikTok"); // Default to TikTok Brand, and remove the option for None
  const [promotionType, setPromotionType] = useState("Brand"); // Assuming you want to start with TikTok Brand
  const [data, setData] = useState([]);
  const [race, setRace] = useState("");
  const [gender, setGender] = useState("");
  const [furtherLocation, setFurtherLocation] = useState("");
  const [selectedItems, setSelectedItems] = useState(new Set());
  const [avgViews, setAvgViews] = useState("");
  const [selectedCreatorsData, setSelectedCreatorsData] = useState([]);
  const [campaignName, setCampaignName] = useState("");
  const [campaignBrief, setCampaignBrief] = useState("");
  const [videoAmount, setVideoAmount] = useState("1");
  const [campaignType, setCampaignType] = useState("");
  const [openDialog, setOpenDialog] = useState(false);
  const [dialogMessage, setDialogMessage] = useState("");
  const [campaignSum, setCampaignSum] = useState(0);
  const [idealDueDate, setIdealDueDate] = useState("");

  const handleResetFilters = () => {
    setRegion("");
    setPlatform("TikTok");
    setPromotionType("Brand");
    setRace("");
    setGender("");
    setFurtherLocation("");
    setFilter("");
    setAvgViews(""); // Reset this as well if you're including the AVG Views Filter
  };
  useEffect(() => {
    const sum = calculateTotalCampaignSum();
    setCampaignSum(sum);
    const fetchData = async () => {
      try {
        const response = await fetch(
          "http://localhost:5100/api/sheets/creatorData/read",
        );
        if (!response.ok) throw new Error("Network response was not ok");
        const jsonData = await response.json();
        setData(jsonData); // Assuming jsonData is an array of arrays
      } catch (error) {
        console.error("Failed to fetch data:", error);
      }
    };
    fetchData();
  }, [selectedCreatorsData]);
  const platformIndexMap = {
    TikTok: 2, // Column 3 in human-readable form, index 2 in zero-based indexing
    Instagram: 3, // Column 4 in human-readable form, index 3 in zero-based indexing
    Youtube: 4, // Column 5 in human-readable form, index 4 in zero-based indexing
  };

  const priceColumnMap = {
    TikTok: {
      Sound: 8, // Column index for TikTok Sound price
      Brand: 9, // Column index for TikTok Brand price
    },
    Instagram: {
      Sound: 11, // Column index for Instagram Sound price
      Brand: 12, // Column index for Instagram Brand price
    },
  };
  const handleFilterChange = (e) => {
    setFilter(e.target.value.toLowerCase());
  };

  const handleRegionChange = (e) => {
    setRegion(e.target.value);
  };

  const handlePlatformChange = (e) => {
    const newPlatform = e.target.value;
    setPlatform(newPlatform);
    // Reset promotionType based on the platform selected
    // Default to the first available promotion type for the new platform
    setPromotionType(promotionTypeOptions[newPlatform][0]);
  };
  // Assuming each row in your data includes a unique identifier at index 0
  // Assuming the structure of your `data` array and the unique identifier is at index 0
  const handleSelectItem = (uniqueId) => {
    const newSelection = new Set(selectedItems);
    const item = filteredData.find((item) => item[0] === uniqueId);
    if (!item) return;

    // Determine the correct column for pricing
    const priceIndex = priceColumnMap[platform]?.[promotionType] || null;
    const price = priceIndex ? item[priceIndex] : "Unknown";

    const relevantData = {
      id: uniqueId,
      name: item[0], // Assuming name is at index 1
      price: price,
      other: item[17], // Assuming some other data is at index 17
    };

    // Toggle selection logic remains unchanged
    if (newSelection.has(uniqueId)) {
      newSelection.delete(uniqueId);
      setSelectedCreatorsData((prev) =>
        prev.filter((creator) => creator.id !== uniqueId),
      );
    } else {
      newSelection.add(uniqueId);
      setSelectedCreatorsData((prev) => [...prev, relevantData]);
    }

    setSelectedItems(newSelection);
  };
  const searchFilter = (row) => {
    // If there is no filter set, don't filter out this row
    if (!filter) return true;

    // Assuming columns at index 5, 6, and 7 are the "viability" columns
    const searchColumns = [0, 5, 6, 7];
    return searchColumns.some((index) => {
      // Ensure that undefined values are not causing an error
      const value = row[index] ? row[index].toString().toLowerCase() : "";
      return value.toString().toLowerCase().includes(filter);
    });
  };
  const calculateTotalCampaignSum = () => {
    const sum = selectedCreatorsData.reduce((accumulator, creator) => {
      // Remove any non-numeric characters (including the dollar sign) before conversion
      const cleanedPriceString = creator.price.replace(/[^0-9.-]+/g, "");
      const price = parseFloat(cleanedPriceString);
      // Check if the conversion was successful (i.e., the result is a finite number)
      if (isFinite(price)) {
        return accumulator + price;
      } else {
        console.error(`Invalid price format detected: ${creator.price}`);
        return accumulator;
      }
    }, 0);
    return sum;
  };
  // Function to generate a unique campaign ID
  const generateCampaignID = () => new Date().getTime().toString();
  // Before creating the campaignData object
  const parsedVideoAmount = parseInt(videoAmount, 10); // Ensure videoAmount is an integer
  const today = new Date().toISOString().slice(0, 10);

  // Function to create campaign data object
  const createCampaignData = () => {
    return {
      id: generateCampaignID(),
      name: campaignName,
      brief: campaignBrief,
      videoAmount: parsedVideoAmount,
      campaignType: campaignType,
      creators: Array.from(selectedCreatorsData),
      userID: "your-mock-user-id",
      proposalDate: today,
      campaign_sum: campaignSum, // Add the campaign sum here
    };
  };

  // Function to handle creating a new campaign
  const handleCreateCampaign = async () => {
    console.log("Creating campaign..."); // Initial log to confirm the function is called
    const campaignData = createCampaignData();
    try {
      console.log("Sending data to server:", campaignData); // Log the data being sent
      const response = await fetch("http://localhost:5000/api/campaigns", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(campaignData),
      });

      if (!response.ok) {
        throw new Error(`Failed to save campaign: ${response.status}`);
      }

      const responseData = await response.json();
      console.log("Campaign saved successfully:", responseData);

      setDialogMessage("Campaign created successfully!");
      setOpenDialog(true);

      setSelectedItems(new Set());
      setCampaignName("");
      setCampaignBrief("");
    } catch (error) {
      console.error("Error saving campaign:", error);
      setDialogMessage(`Error: ${error.message}`);
      setOpenDialog(true);
    }
  };

  const handlePromotionTypeChange = (e) => {
    setPromotionType(e.target.value);
  };
  const getColumnWidth = (index) => {
    // Define custom widths for specific columns by their index or header name
    const customWidths = {
      0: "50px", // Example: Column for checkboxes might be narrower
      1: "150px", // Adjust width for the second column
      // Add more custom widths as needed
      // You can also use headers[index] to set widths by header name if preferable
    };

    // Return the custom width if defined, otherwise return a default width
    return customWidths[index] || "125px"; // Default width for columns not explicitly set
  };
  const promotionTypeOptions = {
    TikTok: ["Sound", "Brand", "Livestream"],
    Instagram: ["Sound", "Brand", "Story Set"],
    Youtube: ["30 Second Integration", "60 Second Integration"],
  };
  const platformToColumnsMap = {
    TikTok: [2], // Column 3 in human-readable form
    Instagram: [3], // Column 4 in human-readable form
    Youtube: [4], // Column 5 in human-readable form
  };
  // ... inside your component where you define platformColumns
  const alwaysVisibleColumns = [1, 8, 15, 18]; // Columns 9, 16, and 18 in human-readable form

  // Determine which columns to display based on the selected platform and promotion type
  const columnsToShow = [
    ...alwaysVisibleColumns,
    ...(platformToColumnsMap[platform] || []),
    ...(promotionType && platform
      ? [priceColumnMap[platform][promotionType] + 1]
      : []),
  ];

  const headers = data[0] || []; // Fallback to an empty array if data[0] is undefined

  const filteredData = data.filter((row) => {
    const regionMatch = region
      ? row[6]?.toString().toLowerCase().includes(region.toLowerCase())
      : true;

    // Ensuring platform has a non-NULL and non-empty value
    const platformMatch = platform
      ? !isNaN(row[platformIndexMap[platform]]) &&
        row[platformIndexMap[platform]] !== null &&
        row[platformIndexMap[platform]] !== ""
      : true;

    let raceMatch = race
      ? row[4]?.toString().toLowerCase().includes(race.toLowerCase())
      : true;
    let genderMatch = gender
      ? row[4]
          ?.toString()
          .toLowerCase()
          .split(/[\s,]+/)
          .includes(gender.toLowerCase()) // Assuming gender is in column 6 (index 5)
      : true;

    const locationMatch = furtherLocation
      ? row[4]?.toString().toLowerCase().includes(furtherLocation.toLowerCase())
      : true;

    // Special handling for gender to ensure "Male" does not match "Female"
    if (gender === "male" && row[4]?.toLowerCase().includes("female")) {
      genderMatch = false;
    }

    // Updated to ensure 'Promotion Type' is not NULL or empty if selected
    const promotionTypeColumnIndex =
      promotionType && platform
        ? priceColumnMap[platform][promotionType]
        : null;
    const promotionTypeMatch =
      promotionTypeColumnIndex !== null
        ? row[promotionTypeColumnIndex] !== null &&
          row[promotionTypeColumnIndex] !== ""
        : true;

    return (
      regionMatch &&
      platformMatch &&
      raceMatch &&
      genderMatch &&
      locationMatch &&
      promotionTypeMatch &&
      searchFilter(row)
    );
  });
  <Dialog
    open={openDialog}
    onClose={() => setOpenDialog(false)}
    aria-labelledby="alert-dialog-title"
    aria-describedby="alert-dialog-description"
  >
    <DialogTitle id="alert-dialog-title">
      {"Campaign Creation Status"}
    </DialogTitle>
    <DialogContent>
      <DialogContentText id="alert-dialog-description">
        {dialogMessage}
      </DialogContentText>
    </DialogContent>
    <DialogActions>
      <Button onClick={() => setOpenDialog(false)} color="primary" autoFocus>
        Close
      </Button>
    </DialogActions>
  </Dialog>;

  return (
    <div className={classes.root}>
      <Navbar className={classes.navbar} />
      <div className={classes.content}>
        <div className={classes.filterSection}>
          <Grid container spacing={2}>
            <Grid item xs={12} md={6}>
              {/* Filters container */}
              <Grid container spacing={2} className={classes.filterContainer}>
                <Grid container spacing={2}>
                  {/* Adjust the spacing as needed */} {/* Region Dropdown */}
                  <FormControl
                    variant="outlined"
                    className={classes.formControl}
                  >
                    <InputLabel id="region-select-label">
                      Select Region
                    </InputLabel>
                    <Select
                      labelId="region-select-label"
                      id="region-select"
                      value={region}
                      onChange={handleRegionChange}
                      label="Select Region"
                    >
                      <MenuItem value="">
                        <em>ALL</em>
                      </MenuItem>
                      <MenuItem value="NACAUKAUS">NACAUKAUS</MenuItem>
                      <MenuItem value="EU">EU</MenuItem>
                      <MenuItem value="LATAM">LATAM</MenuItem>
                      <MenuItem value="Asia">Asia</MenuItem>

                      {/* More regions */}
                    </Select>
                  </FormControl>
                  {/* Platform Dropdown */}
                  <FormControl
                    variant="outlined"
                    className={classes.formControl}
                  >
                    <InputLabel id="platform-select-label">Platform</InputLabel>
                    <Select
                      labelId="platform-select-label"
                      id="platform-select"
                      value={platform}
                      onChange={handlePlatformChange}
                      label="Platform"
                    >
                      <MenuItem value="TikTok">TikTok</MenuItem>
                      <MenuItem value="Instagram">Instagram</MenuItem>
                      {/*  <MenuItem value="Youtube">Youtube</MenuItem> */}

                      {/* More platforms */}
                    </Select>
                  </FormControl>
                  {/* Promotion Type Dropdown */}
                  <FormControl
                    variant="outlined"
                    className={classes.formControl}
                  >
                    <InputLabel id="promotion-type-label">
                      Promotion Type
                    </InputLabel>
                    <Select
                      labelId="promotion-type-label"
                      id="promotion-type-select"
                      value={promotionType}
                      onChange={handlePromotionTypeChange}
                      label="Promotion Type"
                    >
                      {platform &&
                        promotionTypeOptions[platform].map((type) => (
                          <MenuItem key={type} value={type}>
                            {type}
                          </MenuItem>
                        ))}
                    </Select>
                  </FormControl>
                  {/* Race Dropdown */}
                  <FormControl
                    variant="outlined"
                    className={classes.formControl}
                  >
                    <InputLabel id="race-select-label">Race</InputLabel>
                    <Select
                      labelId="race-select-label"
                      id="race-select"
                      value={race}
                      onChange={(e) => setRace(e.target.value)}
                      label="Race"
                    >
                      <MenuItem value="">
                        <em>None</em>
                      </MenuItem>
                      <MenuItem value="Asian">Asian</MenuItem>
                      <MenuItem value="Black">Black</MenuItem>
                      <MenuItem value="Hispanic">Hispanic</MenuItem>
                      <MenuItem value="White">White</MenuItem>
                      <MenuItem value="Other">Other</MenuItem>
                    </Select>
                  </FormControl>
                  {/* Gender Dropdown */}
                  <FormControl
                    variant="outlined"
                    className={classes.formControl}
                  >
                    <InputLabel id="gender-select-label">Gender</InputLabel>
                    <Select
                      labelId="gender-select-label"
                      id="gender-select"
                      value={gender}
                      onChange={(e) => setGender(e.target.value)}
                      label="Gender"
                    >
                      <MenuItem value="">
                        <em>None</em>
                      </MenuItem>
                      <MenuItem value="Male">Male</MenuItem>
                      <MenuItem value="Female">Female</MenuItem>
                      <MenuItem value="Other">Other</MenuItem>
                    </Select>
                  </FormControl>
                  {/* Further Location Dropdown */}
                  <FormControl
                    variant="outlined"
                    className={classes.formControl}
                  >
                    <InputLabel id="further-location-select-label">
                      Location
                    </InputLabel>
                    <Select
                      labelId="further-location-select-label"
                      id="further-location-select"
                      value={furtherLocation}
                      onChange={(e) => setFurtherLocation(e.target.value)}
                      label="Further Location"
                    >
                      <MenuItem value="">
                        <em>None</em>
                      </MenuItem>
                      <MenuItem value="US">US</MenuItem>
                      <MenuItem value="UK">UK</MenuItem>
                      <MenuItem value="Canada">Canada</MenuItem>
                    </Select>
                  </FormControl>
                  {/* For the 'Reset Filters' and 'AVG Views Filter' */}
                  <Grid item xs={12} sm={5} md={3} container spacing={2}>
                    <FormControl
                      variant="outlined"
                      className={classes.formControl}
                      fullWidth
                    >
                      <InputLabel id="avg-views-select-label">
                        AVG Views
                      </InputLabel>
                      <Select
                        labelId="avg-views-select-label"
                        id="avg-views-select"
                        value={avgViews}
                        onChange={(e) => setAvgViews(e.target.value)}
                        label="AVG Views"
                      >
                        {/* AVG Views MenuItems */}
                        <MenuItem value="">
                          <em>None</em>
                        </MenuItem>
                        <MenuItem value="10-50000">10-50,000</MenuItem>
                        <MenuItem value="50000-100000">50,000-100,000</MenuItem>
                        <MenuItem value="100000+">100,000+</MenuItem>
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={6} md={7}>
                    <TextField
                      id="search-field"
                      label="Search"
                      variant="outlined"
                      size="small"
                      fullWidth
                      className={classes.searchField}
                      value={filter}
                      onChange={handleFilterChange}
                    />
                  </Grid>
                </Grid>
              </Grid>
            </Grid>

            <Grid item xs={12} md={6}>
              {" "}
              {/* Buttons container */}
              <div className={classes.buttonContainer}>
                <Button
                  className={classes.button}
                  variant="contained"
                  color="primary"
                  onClick={handleResetFilters}
                >
                  Reset Filters
                </Button>
              </div>
            </Grid>
          </Grid>
        </div>

        <div className={classes.csvSection}>
          {/* CSV Section */}
          <Paper elevation={3}>
            <table className="csvTable">
              <thead>
                <tr>
                  <th>Select</th>

                  {headers.map(
                    (header, index) =>
                      columnsToShow.includes(index + 1) && (
                        <th
                          key={index}
                          style={{ width: getColumnWidth(index) }}
                        >
                          {header}
                        </th>
                      ), // Apply dynamic width based on column index
                  )}
                </tr>
              </thead>
              <tbody>
                {filteredData.map((row, idx) => {
                  if (!row) return null; // Ensure row is not undefined

                  const isSelected = selectedItems.has(row[0]); // Check if the row is selected
                  return (
                    <tr
                      key={idx}
                      style={{
                        backgroundColor: isSelected
                          ? "lightgreen"
                          : "transparent",
                      }} // Highlight selected rows
                      onClick={() => handleSelectItem(row[0])} // Add row click handler
                    >
                      <td>
                        <Checkbox
                          checked={isSelected}
                          onChange={(e) => {
                            e.stopPropagation(); // Prevent row click event from firing
                            handleSelectItem(row[0]);
                          }}
                          color="primary"
                        />
                      </td>
                      {row.map((value, index) =>
                        columnsToShow.includes(index + 1) ? (
                          <td
                            key={index}
                            style={{ width: getColumnWidth(index) }}
                          >
                            {index === 14 ? (
                              <a
                                href={value}
                                target="_blank"
                                rel="noopener noreferrer"
                              >
                                {value}
                              </a>
                            ) : (
                              value ?? "N/A"
                            )}
                          </td>
                        ) : null,
                      )}
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </Paper>
        </div>
      </div>

      <div className={classes.rightColumn}>
        <Paper className={classes.paper} elevation={3}>
          <Typography
            variant="h4"
            gutterBottom
            className={classes.header}
            align="center"
          >
            Campaign Builder
          </Typography>
          <Divider style={{ margin: "10px 0" }} />
          <Grid container spacing={2} alignItems="center">
            <Grid item xs={12}>
              <TextField
                label="Campaign Name"
                variant="outlined"
                fullWidth
                value={campaignName} // Set value from state
                onChange={(e) => setCampaignName(e.target.value)} // Update state on change
              />
            </Grid>

            <Grid item xs={12}>
              <TextField
                label="Campaign Brief"
                variant="outlined"
                fullWidth
                value={campaignBrief} // Set value from state
                onChange={(e) => setCampaignBrief(e.target.value)} // Update state on change
              />
            </Grid>
            <Grid item xs={12}>
            <KeyboardDatePicker
              margin="normal"
              id="date-picker-dialog"
              label="Due Date"
              format="MM/dd/yyyy"
              value={idealDueDate}
              onChange={setIdealDueDate}
              KeyboardButtonProps={{
                'aria-label': 'change date',
              }}
            />
          </Grid>
            <Grid item xs={12} style={{ marginBottom: 20 }}>
              <Typography variant="h6" align="center">
                Creator Details
              </Typography>
            </Grid>

            {Array.from(selectedItems).map((creatorId, index) => {
              const creatorData = data.find((d) => d[0] === creatorId);
              if (!creatorData) return null; // Skip if data is not found

              const priceIndex = priceColumnMap[platform]?.[promotionType];
              const price = priceIndex ? creatorData[priceIndex] : "Unknown";
              const promotionLabel = `${platform} ${promotionType}`;

              return (
                <>
                  <Grid
                    container
                    item
                    xs={12}
                    key={creatorId}
                    alignItems="center"
                    spacing={2}
                  >
                    <Grid item xs={1}>
                      <Checkbox
                        checked={selectedItems.has(creatorId)}
                        onChange={() => handleSelectItem(creatorId)}
                        color="primary"
                      />
                    </Grid>
                    <Grid item xs={4}>
                      <Typography variant="body2">{creatorData[0]}</Typography>
                    </Grid>
                    <Grid item xs={4}>
                      <Typography variant="body2">{promotionLabel}</Typography>
                    </Grid>
                    <Grid item xs={3}>
                      <Typography variant="body2">{`${price}`}</Typography>
                    </Grid>
                  </Grid>
                  {index < selectedItems.size - 1 && (
                    <Divider variant="middle" />
                  )}
                </>
              );
            })}
            <Grid item xs={12}>
              <Typography variant="h6" align="center">
                Campaign Sum: ${campaignSum}
              </Typography>
            </Grid>

            {/* Split "Video Amount" and "Campaign Type" into two columns */}
            <Grid item xs={6}>
              <FormControl fullWidth>
                <InputLabel>Video Amount</InputLabel>
                <Select
                  value={videoAmount}
                  onChange={(e) => setVideoAmount(e.target.value)}
                  label="Video Amount"
                >
                  <MenuItem value={1}>1</MenuItem>
                  <MenuItem value={2}>2</MenuItem>
                  <MenuItem value={3}>3</MenuItem>
                  {/* Add more options as needed */}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={6}>
              <FormControl fullWidth>
                <InputLabel>Campaign Type</InputLabel>
                <Select
                  value={campaignType} // Set value from state
                  onChange={(e) => setCampaignType(e.target.value)} // Update state on change
                  label="Campaign Type"
                >
                  <MenuItem value="product">Product Promotion</MenuItem>
                  <MenuItem value="app">App Promotion</MenuItem>
                  <MenuItem value="website">Website Promotion</MenuItem>
                  <MenuItem value="song">Song Promotion</MenuItem>
                  <MenuItem value="other">Other</MenuItem>
                </Select>
              </FormControl>
            </Grid>

            <Grid item xs={12}>
              <Button
                variant="contained"
                color="secondary"
                fullWidth
                onClick={() => setSelectedItems(new Set())}
              >
                Remove Creators
              </Button>
            </Grid>
            <Grid item xs={12}>
              <Button
                variant="contained"
                color="primary"
                fullWidth
                onClick={handleCreateCampaign} // Make sure this directly calls the function
              >
                Create Campaign!
              </Button>
            </Grid>
            <Dialog
              open={openDialog}
              onClose={() => setOpenDialog(false)}
              aria-labelledby="alert-dialog-title"
              aria-describedby="alert-dialog-description"
            >
              <DialogTitle id="alert-dialog-title">
                {"Campaign Creation Status"}
              </DialogTitle>
              <DialogContent>
                <DialogContentText id="alert-dialog-description">
                  {dialogMessage}
                </DialogContentText>
              </DialogContent>
              <DialogActions>
                <Button
                  onClick={() => setOpenDialog(false)}
                  color="primary"
                  autoFocus
                >
                  Close
                </Button>
              </DialogActions>
            </Dialog>
          </Grid>
        </Paper>
      </div>
    </div>
  );
};

export default SearchPage;
