import React, { useState } from 'react';
import {
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
} from '@mui/material';

function PoNumberDialog({ open, onClose, onComplete }) {
  const [poNumber, setPoNumber] = useState('');

  const handleGeneratePO = () => {
    setPoNumber(`PO-${Math.floor(Math.random() * 10000)}`);
  };

  const handleComplete = () => {
    onComplete(poNumber);
    onClose(); // Close the dialog
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>Add PO Number</DialogTitle>
      <DialogContent>
        <TextField
          autoFocus
          margin="dense"
          id="poNumber"
          label="PO Number"
          type="text"
          fullWidth
          variant="outlined"
          value={poNumber}
          onChange={(e) => setPoNumber(e.target.value)}
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancel</Button>
        <Button onClick={handleGeneratePO}>Generate</Button>
        <Button onClick={handleComplete}>Complete Campaign</Button>
      </DialogActions>
    </Dialog>
  );
}

export default PoNumberDialog;
