import React, { useState, useEffect } from 'react';
import {
  Button,
  Checkbox,
  TableContainer,
  Table,
  TableHead,
  TableRow,
  TableBody,
  Paper,
} from '@material-ui/core';
import { useMutation } from 'react-query';
import client from '../../../API'; // Ensure this path is correct
import { StyledTableRow, StyledTableCell } from '../../../Utils/styledcell'; // Ensure this path is correct
import CompanyDialog from './info'; // Adjust the path to your CompanyDialog component

const CompanyDetailsView = ({ classes }) => {
  const [companies, setCompanies] = useState([]);
  const [selectedIds, setSelectedIds] = useState([]);
  const [isCompanyDialogOpen, setCompanyDialogOpen] = useState(false);

  const fetchCompanies = async () => {
    try {
      const data = await client.companies.list();
      setCompanies(data);
      setSelectedIds([]);
    } catch (error) {
      console.error('Error fetching companies:', error);
    }
  };

  useEffect(() => {
    fetchCompanies();
  }, []);

  const onDeleteCompanies = () => {
    // Implement company deletion logic here
    console.log('Deleting companies:', selectedIds);
    // After deletion, you might want to fetch the companies list again
    fetchCompanies();
  };

  const onCompany = () => {
    setCompanyDialogOpen(true);
  };

  const handleSelectItem = (companyId) => {
    const newSelectedIds = selectedIds.includes(companyId)
      ? selectedIds.filter((id) => id !== companyId)
      : [...selectedIds, companyId];
    setSelectedIds(newSelectedIds);
  };

  const handleDialogClose = (refresh) => {
    setCompanyDialogOpen(false);
    if (refresh) fetchCompanies();
  };

  return (
    <div className={classes.content}>
      <div className={classes.buttonContainer}>
        <Button variant="contained" color="secondary" onClick={() => onCompany()}>
          Add Company
        </Button>
        <Button
          variant="contained"
          color="primary"
          disabled={selectedIds.length !== 1}
          onClick={() => onCompany()}
        >
          Edit Company
        </Button>
        <Button
          variant="contained"
          color="primary"
          disabled={!selectedIds.length}
          onClick={onDeleteCompanies}
        >
          Delete Company(s)
        </Button>
      </div>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 700 }} aria-label="customized table">
          <TableHead>
            <TableRow>
              {/* Define your table headers here */}
            </TableRow>
          </TableHead>
          <TableBody>
            {/* Map through companies to render rows */}
          </TableBody>
        </Table>
      </TableContainer>
      {isCompanyDialogOpen && (
        <CompanyDialog open={isCompanyDialogOpen} onClose={handleDialogClose} />
      )}
    </div>
  );
};

export default CompanyDetailsView;
