import React from 'react';
import { Card, CardContent, Typography } from '@mui/material';

// Functional Component Declaration
// Destructure the 'campaign' prop directly in the function parameter for direct access to the campaign properties.
const RecentCampaign = ({ campaign: { name, postsUp, status } }) => {
  // Use Material-UI components to structure the campaign information in a card format.
  // Directly inserting campaign properties in the render using JSX braces {}.
  return (
    <Card elevation={3} sx={{ minWidth: 275, margin: 2 }}> {/* Adjust styling as needed */}
      <CardContent>
        {/* Display the campaign's name as a heading */}
        <Typography variant="h5" component="div" gutterBottom>
          {name}
        </Typography>
        
        {/* Display total posts up with secondary text color for contrast */}
        <Typography variant="body2" color="text.secondary">
          Total Posts Up: {postsUp}
        </Typography>
        
        {/* Display the campaign status, could also add logic to color-code statuses */}
        <Typography variant="body2" color={status === "Launched" ? "success.main" : "error.main"}>
          Campaign Status: {status}
        </Typography>
      </CardContent>
    </Card>
  );
};

export default RecentCampaign;
