import React, { useState } from "react";
import {
  Button,
  TextField,
  Snackbar,
  Alert,
  CircularProgress,
} from "@mui/material";
import { useNavigate } from "react-router-dom";
import "./Login.css";
import blitzLogo from "../../Components/globalAssets/blitzLogo.png";
import axios from "axios";
import {
  validateEmail,
  validatePassword,
  capitalizeFirstLetter,
} from "../../Utils";

const Register = () => {
  const navigate = useNavigate();

  const [userInfo, setUserInfo] = useState({
    firstName: "",
    lastName: "",
    email: "",
    userName: "",
    password: "",
    confirmPassword: "",
    companyID: "",
    companyName: "",
    streetAddress: "",
    city: "",
    state: "",
    country: "",
    taxID: "",
  });
  const [openSnackbar, setOpenSnackbar] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState("");
  const [snackbarSeverity, setSnackbarSeverity] = useState("info");

  const [loading, setLoading] = useState(false);

  const handleCloseSnackbar = (event, reason) => {
    if (reason === "clickaway") {
      return;
    }
    setOpenSnackbar(false);
  };

  const handleRegister = async () => {
    let bValidation = true;

    Object.keys(userInfo).forEach((item) => {
      if (bValidation && userInfo[item].length === 0) {
        setSnackbarMessage(`${capitalizeFirstLetter(item)} field is required!`);
        setSnackbarSeverity("error");
        setOpenSnackbar(true);

        bValidation = false;
      }
    });

    if (bValidation) {
      if (!validateEmail(userInfo.email)) {
        setSnackbarMessage("Invalid email address!");
        setSnackbarSeverity("error");
        setOpenSnackbar(true);

        return;
      }

      if (!validatePassword(userInfo.password)) {
        setSnackbarMessage("Password should have 6 more characters!");
        setSnackbarSeverity("error");
        setOpenSnackbar(true);

        return;
      }

      if (userInfo.password != userInfo.confirmPassword) {
        setSnackbarMessage("Password should be confirmed correctly");
        setSnackbarSeverity("error");
        setOpenSnackbar(true);

        return;
      }
    }

    if (!bValidation) return;
    console.log(userInfo);
    const params = {
      username: userInfo.userName,
      first_name: userInfo.firstName,
      last_name: userInfo.lastName,
      email: userInfo.email,
      password: userInfo.password,
      company_id: userInfo.companyID,
      company_name: userInfo.companyName,
      address: `${userInfo.streetAddress}, ${userInfo.city}, ${userInfo.state}, ${userInfo.country}`,
      tax_info: userInfo.taxID,
    };

    setLoading(true);

    try {
      const response = await axios.post(
        "http://localhost:5000/api/register",
        params
      );

      setLoading(false);

      if (response.data.success) {
        setSnackbarMessage("You have created an account successfully!");
        setSnackbarSeverity("success");
        setOpenSnackbar(true);

        navigate("/login");
      } else {
        console.error("Register failed: ", response.data.message);
        setSnackbarMessage(
          response.data.message || "Login failed, please try again."
        );
        setSnackbarSeverity("error");
        setOpenSnackbar(true);
      }
    } catch (error) {
      setLoading(false);
      console.error("An error occurred during register: ", error);
      let errorMessage = "An error occurred, please try again.";
      if (error.response && error.response.data) {
        errorMessage = error.response.data.message || errorMessage;
      }
      setSnackbarMessage(errorMessage);
      setSnackbarSeverity("error");
      setOpenSnackbar(true);
    }
    //navigate("/dashboard");
  };

  return (
    <div className="login-container">
      {/* Left column */}
      <div className="logo-container">
        <img src={blitzLogo} alt="blitzLogo" />
      </div>

      {/* Right column */}
      <div className="form-container">
        <h2>Welcome to Blitz!</h2>
        <p>Enter your information to continue with Blitz</p>

        <div className="field-row">
          <TextField
            disabled={loading}
            className="field"
            label="First Name"
            variant="outlined"
            value={userInfo.firstName}
            onChange={(e) =>
              setUserInfo({ ...userInfo, firstName: e.target.value })
            }
          />
          <TextField
            disabled={loading}
            className="field"
            label="Last Name"
            variant="outlined"
            value={userInfo.lastName}
            onChange={(e) =>
              setUserInfo({ ...userInfo, lastName: e.target.value })
            }
          />
        </div>

        <div className="field-row">
          <TextField
            disabled={loading}
            className="field"
            label="Email"
            variant="outlined"
            value={userInfo.email}
            onChange={(e) =>
              setUserInfo({ ...userInfo, email: e.target.value })
            }
          />
          <TextField
            disabled={loading}
            className="field"
            label="Desired Username"
            variant="outlined"
            value={userInfo.userName}
            onChange={(e) =>
              setUserInfo({ ...userInfo, userName: e.target.value })
            }
          />
        </div>

        <div className="field-row">
          <TextField
            disabled={loading}
            className="field"
            label="Password"
            variant="outlined"
            type="password"
            value={userInfo.password}
            onChange={(e) =>
              setUserInfo({ ...userInfo, password: e.target.value })
            }
          />
          <TextField
            disabled={loading}
            className="field"
            label="Re-enter Password"
            variant="outlined"
            type="password"
            value={userInfo.confirmPassword}
            onChange={(e) =>
              setUserInfo({ ...userInfo, confirmPassword: e.target.value })
            }
          />
        </div>

        <div className="field-row">
          <TextField
            disabled={loading}
            className="field"
            label="Company ID"
            variant="outlined"
            value={userInfo.companyID}
            onChange={(e) =>
              setUserInfo({ ...userInfo, companyID: e.target.value })
            }
          />
          <TextField
            disabled={loading}
            className="field"
            label="Company Name"
            variant="outlined"
            value={userInfo.companyName}
            onChange={(e) =>
              setUserInfo({ ...userInfo, companyName: e.target.value })
            }
          />
        </div>

        <div className="field-row">
          <TextField
            disabled={loading}
            className="field"
            label="Street Address"
            variant="outlined"
            value={userInfo.streetAddress}
            onChange={(e) =>
              setUserInfo({ ...userInfo, streetAddress: e.target.value })
            }
          />
          <TextField
            disabled={loading}
            className="field"
            label="City"
            variant="outlined"
            value={userInfo.city}
            onChange={(e) => setUserInfo({ ...userInfo, city: e.target.value })}
          />
        </div>

        <div className="field-row">
          <TextField
            disabled={loading}
            className="field"
            label="State"
            variant="outlined"
            value={userInfo.state}
            onChange={(e) =>
              setUserInfo({ ...userInfo, state: e.target.value })
            }
          />
          <TextField
            disabled={loading}
            className="field"
            label="Country"
            variant="outlined"
            value={userInfo.country}
            onChange={(e) =>
              setUserInfo({ ...userInfo, country: e.target.value })
            }
          />
        </div>

        <div className="field-row">
          <TextField
            disabled={loading}
            className="field"
            label="SSN/Tax ID"
            variant="outlined"
            value={userInfo.taxID}
            onChange={(e) =>
              setUserInfo({ ...userInfo, taxID: e.target.value })
            }
          />
        </div>

        <Button
          variant="contained"
          color="primary"
          size="large"
          startIcon={loading && <CircularProgress size={20} color="inherit" />}
          onClick={handleRegister}
        >
          Create Account
        </Button>
      </div>

      <Snackbar
        open={openSnackbar}
        autoHideDuration={6000}
        onClose={handleCloseSnackbar}
      >
        <Alert
          onClose={handleCloseSnackbar}
          severity={snackbarSeverity}
          sx={{ width: "100%" }}
        >
          {snackbarMessage}
        </Alert>
      </Snackbar>
    </div>
  );
};

export default Register;
