// LeftColumn.js
import React from 'react';
import {
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  TextField,
  Button,
  makeStyles,
  Checkbox,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    width: '100%',
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  button: {
    margin: theme.spacing(1),
  },
  searchField: {
    margin: theme.spacing(1),
    minWidth: 120,
    width: '100%',
  },
  table: {
    minWidth: 650,
  },
}));

const LeftColumn = ({
  region, setRegion, platform, setPlatform, promotionType, setPromotionType,
  race, setRace, gender, setGender, furtherLocation, setFurtherLocation,
  avgViews, setAvgViews, filter, setFilter, handleResetFilters, data, selectedItems, handleSelectItem
}) => {
  const classes = useStyles();

  return (
    <Grid container spacing={3}>
      {/* ...all your other filter components like Region, Platform, etc... */}

      <Grid item xs={12}>
        <Button
          variant="contained"
          color="primary"
          onClick={handleResetFilters}
          className={classes.button}
          fullWidth
        >
          Reset Filters
        </Button>
      </Grid>

      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Checkbox</TableCell>
              {/* ...other table headers based on your CSV columns... */}
            </TableRow>
          </TableHead>
          <TableBody>
            {data.map((row, index) => (
              <TableRow key={index}>
                <TableCell padding="checkbox">
                  <Checkbox
                    checked={selectedItems.has(row.id)}
                    onChange={() => handleSelectItem(row.id)}
                    color="primary"
                  />
                </TableCell>
                {/* ...other table cells based on your CSV columns... */}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Grid>
  );
};

export default LeftColumn;
