import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './dashboard.css';
import Navbar from '../../../Components/Navbar/NavBar';
import { Button, Table, TableBody, TableCell, TableHead, TableRow } from '@mui/material';

const Dashboard = () => {
  const [campaigns, setCampaigns] = useState([]);
  const [payouts, setPayouts] = useState([]);
  const [invoices, setInvoices] = useState([]);
  const [balance, setBalance] = useState(0);

  useEffect(() => {
    const fetchCampaigns = async () => {
      const res = await axios.get('/api/campaigns');
      setCampaigns(res.data);
    };
    const fetchPayouts = async () => {
      const res = await axios.get('/api/payouts');
      setPayouts(res.data);
    };
    const fetchInvoices = async () => {
      const res = await axios.get('/api/invoices');
      setInvoices(res.data);
    };
    const fetchBalance = async () => {
      const res = await axios.get('/api/balance');
      setBalance(res.data.balance);
    };

    fetchCampaigns();
    fetchPayouts();
    fetchInvoices();
    fetchBalance();
  }, []);

  return (
    <div className="dashboard">
      <Navbar />
      <div className="dashboard-content">
        <div className="dashboard-column left-column">
          <div className="summary-section">
            <h2>Campaign Summary</h2>
            <Table aria-label="simple table">
              {/* ... */}
            </Table>
          </div>
          <div className="summary-section">
            <h2>Payout Summary</h2>
            <Table aria-label="simple table">
              {/* ... */}
            </Table>
          </div>
          <div className="summary-section">
            <h2>Invoice Summary</h2>
            <Table aria-label="simple table">
              {/* ... */}
            </Table>
          </div>
        </div>
        <div className="dashboard-column right-column">
          <div className="balance-section">
            <h2>Total Balance</h2>
            <div className="balance-value">${balance}</div>
          </div>
          <div className="action-buttons">
            <Button variant="contained" color="primary">Request Payout</Button>
            <Button variant="contained" color="secondary">Make Payment</Button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
