import React, { useState } from 'react';
import { Dialog, DialogActions, DialogContent, DialogTitle, Button, TextField, Checkbox, FormControlLabel } from '@material-ui/core';
import Navbar from '../../Components/Navbar/NavBar';

import './blitzpay.css'; // Ensure this path is correct

// Define the PayCreatorDialog component
const PayCreatorDialog = ({ open, onClose, onSubmit }) => {
  const [paymentAmount, setPaymentAmount] = useState('');
  const [paymentTerm, setPaymentTerm] = useState('');
  const [blitzPay, setBlitzPay] = useState(false);
  const [poNumber, setPoNumber] = useState('');
  const [notes, setNotes] = useState('');
  const [selectedCreator, setSelectedCreator] = useState(null);

  // Dummy data for creator search autofill
  const creators = [
    { name: 'John Doe', id: 1 },
    { name: 'Jane Smith', id: 2 },
    // Add more dummy creators as needed
  ];

  const handleCreatorChange = (_, newValue) => {
    setSelectedCreator(newValue);
  };

  const handleBlitzPayChange = (event) => {
    setBlitzPay(event.target.checked);
  };

  const handleSubmit = () => {
    onSubmit({
      selectedCreator,
      paymentAmount,
      paymentTerm,
      blitzPay,
      poNumber,
      notes,
    });
    onClose(); // Close the dialog
  };

  return (
    <Dialog open={open} onClose={onClose} aria-labelledby="form-dialog-title">
      <DialogTitle id="form-dialog-title">Pay a Creator</DialogTitle>
      <DialogContent>
        <FormControl
          options={creators}
          getOptionLabel={(option) => option.name}
          onChange={handleCreatorChange}
          renderInput={(params) => <TextField {...params} label="Select a Creator" variant="outlined" />}
        />
        <TextField
          margin="dense"
          label="Payment Amount"
          type="number"
          fullWidth
          value={paymentAmount}
          onChange={(e) => setPaymentAmount(e.target.value)}
          variant="outlined"
        />
        <TextField
          margin="dense"
          label="Payment Term"
          fullWidth
          value={paymentTerm}
          onChange={(e) => setPaymentTerm(e.target.value)}
          variant="outlined"
        />
        <FormControlLabel
          control={<Checkbox checked={blitzPay} onChange={handleBlitzPayChange} />}
          label="BlitzPay"
        />
        <TextField
          margin="dense"
          label="PO #"
          fullWidth
          value={poNumber}
          onChange={(e) => setPoNumber(e.target.value)}
          variant="outlined"
        />
        <TextField
          margin="dense"
          label="Notes"
          fullWidth
          multiline
          rows={4}
          value={notes}
          onChange={(e) => setNotes(e.target.value)}
          variant="outlined"
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose} color="primary">Cancel</Button>
        <Button onClick={handleSubmit} color="primary">Submit</Button>
      </DialogActions>
    </Dialog>
  );
};

// BlitzPay component with the PayCreatorDialog integrated
const BlitzPay = () => {
  const [activeTab, setActiveTab] = useState('pending');
  const [openPayCreatorDialog, setOpenPayCreatorDialog] = useState(false);

  const transactions = [
    { id: 1, creatorName: 'John', campaignName: 'Lorem', payoutDate: '2022-01-01', paymentStatus: 'Completed', currencyType: 'USD', blitzPay: true, amount: 200 },
    { id: 2, creatorName: 'Yoyo', campaignName: 'Ipsum', payoutDate: '2022-02-01', paymentStatus: 'Pending', currencyType: 'EUR', blitzPay: false, amount: 300 },
    { id: 3, creatorName: 'Jesse', campaignName: 'Dolor', payoutDate: '2022-03-01', paymentStatus: 'Completed', currencyType: 'GBP', blitzPay: true, amount: 100 },
    // More transactions...
  ];

  const handleTabClick = (tab) => {
    setActiveTab(tab);
    if (tab === 'pay') {
      setOpenPayCreatorDialog(true);
    }
  };

  const handlePayCreatorDialogClose = () => {
    setOpenPayCreatorDialog(false);
  };

  const handlePayCreatorSubmit = (data) => {
    console.log(data); // For now, just log the submitted data
    setOpenPayCreatorDialog(false);
  };

  // Calculators
  const creatorsPaid = transactions.length;
  const amountInEscrow = transactions.reduce((acc, curr) => curr.paymentStatus === 'Pending' ? acc + curr.amount : acc, 0);
  const blitzPaysUsed = transactions.filter(transaction => transaction.blitzPay).length;

  return (
    <div className="dashboard">
      <Navbar />
      <div className="content flex-row">
        <div className="left-column">
          <h1 className='header'>Blitz Pay</h1>
          <div className="button-container">
            <button className="btn" onClick={() => handleTabClick('pending')}>Pending Transactions</button>
            <button className="btn" onClick={() => handleTabClick('all')}>All Transactions</button>
            <button className="btn" onClick={() => handleTabClick('recent')}>Recent Payments</button>
            {/* Removed Pay a Creator button from here to prevent auto-opening */}
          </div>
          <div className='table-container'>
            {/* Table and other components */}
          </div>
        </div>
        <div className="right-column">
          {/* Summary and other components */}
        </div>
      </div>
      <PayCreatorDialog
        open={openPayCreatorDialog}
        onClose={handlePayCreatorDialogClose}
        onSubmit={handlePayCreatorSubmit}
      />
    </div>
  );
};

export default BlitzPay;
