import React, { useState, useEffect } from 'react';
import {
  Button,
  Checkbox,
  TableContainer,
  Table,
  TableHead,
  TableRow,
  TableBody,
  Paper,
} from '@material-ui/core';
import { useMutation } from 'react-query';
import client from '../../../API';
import { StyledTableRow, StyledTableCell } from '../../../Utils/styledcell';
import CompanyDialog from './info'; // Adjust the path according to your file structure

const CompanyDetailsView = ({ classes }) => {
  const [companies, setCompanies] = useState([]);
  const [selectedIds, setSelectedIds] = useState([]);
  const [isPayCreatorDialogOpen, setPayCreatorDialogOpen] = useState(false);

  const { mutate: fetchCompanies } = useMutation(client.companies.list, {
    onSuccess: async (data) => {
      setCompanies(data);
      setSelectedIds([]);
    },
    onError: (error) => {
      console.error('Error fetching companies:', error);
    },
  });

  useEffect(() => {
    fetchCompanies();
  }, []);

  const onDeleteCompanies = () => {
    // Assume deleteCompanies mutation setup similar to fetchCompanies
  };

  const onCompany = () => {
    setPayCreatorDialogOpen(true);
  };

  const handleSelectItem = (companyId) => {
    const newSelectedIds = selectedIds.includes(companyId)
      ? selectedIds.filter((id) => id !== companyId)
      : [...selectedIds, companyId];
    setSelectedIds(newSelectedIds);
  };

  const handleDialogClose = (refresh) => {
    setPayCreatorDialogOpen(false);
    if (refresh) fetchCompanies();
  };

  return (
    <>
      <Button variant="contained" color="secondary" onClick={onCompany}>
        Add Company
      </Button>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 700 }} aria-label="customized table">
          {/* TableHead and TableBody similar to the original component */}
        </Table>
      </TableContainer>
      {isPayCreatorDialogOpen && (
        <CompanyDialog open={isPayCreatorDialogOpen} onClose={handleDialogClose} />
      )}
    </>
  );
};

export default CompanyDetailsView;
