import React, { useState } from 'react';
import { Dialog, DialogActions, DialogContent, DialogTitle, Button, TextField, Checkbox, FormControlLabel, Autocomplete, Box } from '@mui/material';
import Navbar from '../../Components/Navbar/NavBar'; // Ensure Navbar is used or remove if not necessary
import './blitzpay.css';

const PayCreatorDialog = ({ open, onClose, onSubmit }) => {
  const [paymentAmount, setPaymentAmount] = useState('');
  const [paymentTerm, setPaymentTerm] = useState('');
  const [blitzPay, setBlitzPay] = useState(false);
  const [poNumber, setPoNumber] = useState('');
  const [notes, setNotes] = useState('');
  const [selectedCreator, setSelectedCreator] = useState(null);

  // Dummy data for creator selection and payment terms
  const creators = [
    { name: 'John Doe', id: '1' },
    { name: 'Jane Smith', id: '2' },
    // Add more dummy creators as needed
  ];

  const paymentTerms = [
    { name: 'Net 5', id: '1' },
    { name: 'Net 30', id: '2' },
    { name: 'Net 45', id: '3' },
    { name: 'Net 60', id: '4' },
  ];

  const handleGeneratePO = () => {
    // Implement PO generation logic here
    // Example: Set a mock PO number (in a real scenario, you might fetch this from a backend service)
    setPoNumber(`PO-${Math.floor(Math.random() * 10000)}`);
  };

  const isSubmitDisabled = !selectedCreator || !paymentAmount || !paymentTerm || !poNumber;

  const handleSubmit = () => {
    if (isSubmitDisabled) return; // Prevent form submission if validation fails

    onSubmit({
      selectedCreator: selectedCreator?.id,
      paymentAmount,
      paymentTerm: paymentTerm?.name, // Adjust based on your needs
      blitzPay,
      poNumber,
      notes,
    });
    onClose();
  };

  return (
    <Dialog open={open} onClose={onClose} aria-labelledby="form-dialog-title">
      <DialogTitle id="form-dialog-title">Pay a Creator</DialogTitle>
      <DialogContent>
        <Autocomplete
          options={creators}
          getOptionLabel={(option) => option.name}
          fullWidth
          margin="dense"
          onChange={(event, newValue) => {
            setSelectedCreator(newValue);
          }}
          renderInput={(params) => (
            <TextField {...params} label="Select a Creator" variant="outlined" required />
          )}
        />
        <TextField
          margin="dense"
          label="Payment Amount"
          type="number"
          fullWidth
          value={paymentAmount}
          onChange={(e) => setPaymentAmount(e.target.value)}
          variant="outlined"
          required
        />
        <Autocomplete
          options={paymentTerms}
          getOptionLabel={(option) => option.name}
          fullWidth
          margin="dense"
          onChange={(event, newValue) => {
            setPaymentTerm(newValue);
          }}
          renderInput={(params) => (
            <TextField {...params} label="Select a Payment Term" variant="outlined" required />
          )}
        />
        <FormControlLabel
          control={<Checkbox checked={blitzPay} onChange={handleBlitzPayChange} />}
          label="BlitzPay"
        />
        <Box display="flex" alignItems="center" justifyContent="space-between">
          <TextField
            margin="dense"
            label="PO #"
            fullWidth
            value={poNumber}
            onChange={(e) => setPoNumber(e.target.value)}
            variant="outlined"
            required
            sx={{ flex: 1, marginRight: '8px' }} // Adjust spacing based on your design
          />
          <Button variant="outlined" onClick={handleGeneratePO}>Generate PO</Button>
        </Box>
        <TextField
          margin="dense"
          label="Notes"
          fullWidth
          multiline
          rows={4}
          value={notes}
          onChange={(e) => setNotes(e.target.value)}
          variant="outlined"
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose} color="primary">Cancel</Button>
        <Button onClick={handleSubmit} color="primary" disabled={isSubmitDisabled}>Submit</Button>
      </DialogActions>
    </Dialog>
  );
};

export default PayCreatorDialog;
