// LeftColumn.js
import React from 'react';
import {
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  TextField,
  Button,
  makeStyles,
  Checkbox,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    width: '100%',
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  button: {
    margin: theme.spacing(1),
  },
  searchField: {
    margin: theme.spacing(1),
    minWidth: 120,
    width: '100%',
  },
  table: {
    minWidth: 650,
  },
}));

const LeftColumn = ({
  region, setRegion, platform, setPlatform, promotionType, setPromotionType,
  race, setRace, gender, setGender, furtherLocation, setFurtherLocation,
  avgViews, setAvgViews, filter, setFilter, handleResetFilters, data, selectedItems, handleSelectItem
}) => {
  const classes = useStyles();

  // Please ensure that the `data` prop is an array of objects with at least an `id` property
  // If `data` is not provided or not in the correct format, this component will not render correctly.

  return (
    <Grid container spacing={3}>
      {/* Filters Section */}
      <Grid item xs={12}>
        <FormControl variant="outlined" className={classes.formControl}>
          <InputLabel id="region-label">Region</InputLabel>
          <Select
            labelId="region-label"
            id="region-select"
            value={region}
            onChange={(e) => setRegion(e.target.value)}
            label="Region"
          >
            {/* Add your menu items here */}
          </Select>
        </FormControl>
        {/* Repeat for other filters like Platform, Promotion Type, Race, etc. */}

        <TextField
          id="search-field"
          label="Search"
          variant="outlined"
          className={classes.searchField}
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          fullWidth
        />

        <Button
          variant="contained"
          color="primary"
          onClick={handleResetFilters}
          className={classes.button}
          fullWidth
        >
          Reset Filters
        </Button>
      </Grid>

      {/* CSV Table Section */}
      <Grid item xs={12}>
        <TableContainer component={Paper}>
          <Table className={classes.table} aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell padding="checkbox">Select</TableCell>
                {/* ...other table headers... */}
              </TableRow>
            </TableHead>
            <TableBody>
              {data && data.map((row, index) => (
                <TableRow key={row.id || index}>
                  <TableCell padding="checkbox">
                    <Checkbox
                      checked={selectedItems.has(row.id)}
                      onChange={() => handleSelectItem(row.id)}
                      color="primary"
                    />
                  </TableCell>
                  {/* ...other table cells based on your CSV columns... */}
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Grid>
    </Grid>
  );
};

export default LeftColumn;
