import React from 'react';
import { Card, CardContent, Typography } from '@mui/material';

// Functional Component Declaration
const RecentCampaign = ({ campaign }) => {
  // Assuming the 'campaign' prop includes all necessary details
  const {
    name,
    budget,
    launchDate,
    creators, // Assuming this is an array of creator objects
    status,
  } = campaign;

  // Calculate total posts up (assuming creators have a 'liveLink' when the post is up)
  const postsUp = creators.filter((creator) => creator.liveLink).length;

  // Remaining posts are the total number of creators minus the posts up
  const remainingPosts = creators.length - postsUp;

  // Calculate total views and total engagement from all creators
  const totalViews = creators.reduce((acc, creator) => acc + (creator.totalViews || 0), 0);
  const totalEngagement = creators.reduce((acc, creator) => acc + (creator.totalEngagement || 0), 0);

  return (
    <Card elevation={3} sx={{ minWidth: 275, margin: 2 }}>
      <CardContent>
        {/* Display the campaign's name as a heading */}
        <Typography variant="h5" component="div" gutterBottom>
          {name}
        </Typography>
        
        {/* Display budget */}
        <Typography variant="body2" color="text.secondary">
          Budget: ${budget.toLocaleString()}
        </Typography>

        {/* Display launch date */}
        <Typography variant="body2" color="text.secondary">
          Launch Date: {new Date(launchDate).toLocaleDateString()}
        </Typography>

        {/* Display total posts up with secondary text color for contrast */}
        <Typography variant="body2" color="text.secondary">
          Total Posts Up: {postsUp}
        </Typography>

        {/* Display remaining posts */}
        <Typography variant="body2" color="text.secondary">
          Remaining Posts: {remainingPosts}
        </Typography>

        {/* Display total views */}
        <Typography variant="body2" color="text.secondary">
          Total Views: {totalViews.toLocaleString()}
        </Typography>

        {/* Display total engagement */}
        <Typography variant="body2" color="text.secondary">
          Total Engagement: {totalEngagement.toLocaleString()}
        </Typography>
        
        {/* Display the campaign status with conditional coloring */}
        <Typography variant="body2" color={status === "Launched" ? "success.main" : "error.main"}>
          Campaign Status: {status}
        </Typography>
      </CardContent>
    </Card>
  );
};

export default RecentCampaign;
