import React, { useState, useEffect } from 'react';
import Navbar from '../../Components/Navbar/NavBar';
import './blitzpay.css';
import PayCreatorDialog from './payCreator'; // Assuming PayCreatorDialog is in the same directory

const BlitzPay = () => {
  const [activeTab, setActiveTab] = useState('pending');
  const [isPayCreatorDialogOpen, setIsPayCreatorDialogOpen] = useState(false);

  const transactions = [
    { id: 1, creatorName: 'John', campaignName: 'Lorem', payoutDate: '2022-01-01', paymentStatus: 'Completed', currencyType: 'USD', blitzPay: true, amount: 200 },
    { id: 2, creatorName: 'Yoyo', campaignName: 'Ipsum', payoutDate: '2022-02-01', paymentStatus: 'Pending', currencyType: 'EUR', blitzPay: false, amount: 300 },
    { id: 3, creatorName: 'Jesse', campaignName: 'Dolor', payoutDate: '2022-03-01', paymentStatus: 'Completed', currencyType: 'GBP', blitzPay: true, amount: 100 },
    // More transactions...
  ];
 
  const [payouts, setPayouts] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/payouts')
    .then(response => response.json())
      .then(data => setPayouts(data))
      .catch(error => console.error('Error fetching payouts:', error));
  }, []);

  const handleTabClick = (tab) => {
    setActiveTab(tab);
    if (tab === 'pay') setIsPayCreatorDialogOpen(true);
  };

  const handleDialogClose = () => setIsPayCreatorDialogOpen(false);

  const handleDialogSubmit = ({
    selectedCreator,
    paymentAmount,
    paymentTerm,
    poNumber,
    blitzPay,
    onClose, // Assuming onClose is also passed as a prop or available in the same scope
    onSubmit // Make sure onSubmit function is defined and passed correctly as a prop
  }) => {
    // Validate required fields
    if (!selectedCreator || !paymentAmount || !paymentTerm || !poNumber) {
      alert('Please fill in all required fields.');
      return;
    }
  
    // Calculate the payout date
    const currentDate = new Date();
    let payoutDate = new Date();
    const interestRate = blitzPay ? 5 : 0; // 5% interest rate for BlitzPay, represented as a whole number for clarity
    let finalAmount = parseFloat(paymentAmount);
  
    if (blitzPay) {
      payoutDate.setDate(currentDate.getDate() + 1); // Next day if BlitzPay is true
      finalAmount *= 0.95; // Reduce amount by 5% for BlitzPay
    } else {
      // Adjust payout date based on payment term
      const paymentTermDays = parseInt(paymentTerm.name.replace('Net ', ''), 10);
      payoutDate.setDate(currentDate.getDate() + paymentTermDays);
    }
  
    // Prepare the data to be submitted
    const submissionData = {
      creator_id: selectedCreator.id,
      amount: finalAmount,
      po_number: poNumber,
      payout_date: payoutDate.toISOString().split('T')[0], // Format YYYY-MM-DD
      status: 'queued',
      blitzPay,
      interestRate
    };
  
    // Call the onSubmit function, passing the prepared data
    onSubmit(submissionData);
  
    // Close the dialog
    onClose();
  };
  


  // Calculators
  const creatorsPaid = transactions.length;
  const amountInEscrow = transactions.reduce((acc, curr) => curr.paymentStatus === 'Pending' ? acc + curr.amount : acc, 0);
  const blitzPaysUsed = transactions.filter(transaction => transaction.blitzPay).length;

  return (
    <div className="dashboard">
      <Navbar />
      <div className="content flex-row">
        <div className="left-column">
          <h1 className='header'>Blitz Pay</h1>
          <div className="button-container">
            <button className="btn" onClick={() => handleTabClick('pending')}>Pending Transactions</button>
            <button className="btn" onClick={() => handleTabClick('all')}>All Transactions</button>
            <button className="btn" onClick={() => handleTabClick('recent')}>Recent Payments</button>
            <button className="btn" onClick={() => handleTabClick('pay')}>Pay a Creator</button>
          </div>
          <div className='table-container'>
            <table className="styled-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Creator Name</th>
                  <th>Campaign Name</th>
                  <th>Payout Date</th>
                  <th>Payment Status</th>
                  <th>Currency Type</th>
                  <th>BlitzPay</th>
                  <th>Amount</th>
                </tr>
              </thead>
              <tbody>
                {transactions.map(transaction => (
                  <tr key={transaction.id}>
                    <td>{transaction.id}</td>
                    <td>{transaction.creatorName}</td>
                    <td>{transaction.campaignName}</td>
                    <td>{transaction.payoutDate}</td>
                    <td>{transaction.paymentStatus}</td>
                    <td>{transaction.currencyType}</td>
                    <td>{transaction.blitzPay ? 'Yes' : 'No'}</td>
                    <td>${transaction.amount}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
        <div className="right-column">
          <h2>Summary</h2>
          <p>Creators Paid: {creatorsPaid}</p>
          <p>Amount in Escrow: ${amountInEscrow}</p>
          <p>BlitzPays Used: {blitzPaysUsed}</p>
        </div>
      </div>
      <PayCreatorDialog
  open={isPayCreatorDialogOpen}
  onClose={() => setIsPayCreatorDialogOpen(false)}
  onSubmit={handleDialogSubmit}
/>

    </div>
  );
};

export default BlitzPay;
