import React, { useState, useEffect } from 'react';
import Navbar from '../../../Components/Navbar/NavBar';
import './dedInvoicing.css';

const Invoicing = () => {
  const [activeTab, setActiveTab] = useState('pending');
  const [invoices, setInvoices] = useState([]);

  useEffect(() => {
    // Fetch all invoices from the backend
    const fetchInvoices = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/invoices');
        if (!response.ok) {
          throw new Error('Could not fetch invoices');
        }
        const data = await response.json();
        setInvoices(data.invoices); // Assuming the backend returns an object with an 'invoices' array
      } catch (error) {
        console.error(error.message);
      }
    };

    fetchInvoices();
  }, []);

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="dashboard">
      <Navbar />
      <div className="content">
        <h1 className='header'>Invoicing</h1>
        <div className="button-container" style={{ marginBottom: '10px' }}>
          <button onClick={() => handleTabClick('pending')}>Pending Transactions</button>
          <button onClick={() => handleTabClick('all')}>All Transactions</button>
          <button onClick={() => handleTabClick('recent')}>Recent Payments</button>
        </div>
        <div className='table-container'>
          <table>
            <thead>
              <tr><th>ID</th><th>Name</th><th>Amount</th></tr>
            </thead>
            <tbody>
              {invoices.filter(invoice => {
                if (activeTab === 'all') return true;
                if (activeTab === 'pending') return invoice.status === 'Pending';
                if (activeTab === 'recent') return invoice.status === 'Paid'; // Adjust these checks based on your data structure
              }).map(invoice => (
                <tr key={invoice.id}>
                  <td>{invoice.id}</td>
                  <td>{invoice.name}</td>
                  <td>{invoice.amount}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Invoicing;
