import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Container, Grid, Typography, Button, Divider } from "@mui/material";
import Navbar from "../../../Components/Navbar/NavBar";
import { makeStyles } from "@material-ui/core/styles";
import SearchFilterSection from "../Search/search";
import CampaignDetailsUI from "./campaigndetailsUI"; // Ensure correct import path

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  searchSection: {
    // Adjust this class if additional styling is needed for the search section
  },
  campaignDetails: {
    padding: theme.spacing(2),
    textAlign: "center",
    color: theme.palette.text.secondary,
  },
}));

const AddCreators = () => {
  const classes = useStyles();
  const { campaignId } = useParams();
  const navigate = useNavigate();
  const [campaignDetails, setCampaignDetails] = useState(null);

  useEffect(() => {
    const fetchCampaignDetails = async () => {
      const url = `http://localhost:5000/api/campaigns/${campaignId}`;
      try {
        const response = await fetch(url);
        if (!response.ok) {
          throw new Error(`Failed to fetch campaign details, status: ${response.status}`);
        }
        const data = await response.json();
        setCampaignDetails(data);
      } catch (error) {
        console.error("Error fetching campaign details:", error);
      }
    };

    if (campaignId) {
      fetchCampaignDetails();
    }
  }, [campaignId]);

  const handleConfirmCreatorChanges = () => {
    navigate("/campaigns");
  };

  return (
    <Container>
      <Navbar />
      <Grid container spacing={3}>
        <Grid item xs={12} md={10} className={classes.searchSection}>
          {/* Adjust md value as needed to take more space */}
          <SearchFilterSection />
        </Grid>
        <Grid item xs={12} md={4} className={classes.campaignDetails}>
          {/* Adjust other side accordingly */}
          <CampaignDetailsUI campaignDetails={campaignDetails} />
          <Divider style={{ margin: "20px 0" }} />
          <Button variant="contained" color="primary" fullWidth onClick={handleConfirmCreatorChanges}>Confirm Creator Changes</Button>
        </Grid>
      </Grid>
    </Container>
  );
};

export default AddCreators;
