import React, { useState, useEffect } from 'react';
import './dashboard.css'; // Make sure to create and link this CSS for styling
import Navbar from '../../../Components/Navbar/NavBar';
import { Button, Select, MenuItem, Table, TableBody, TableCell, TableHead, TableRow } from '@mui/material';
import axios from 'axios';

const Dashboard = () => {
  const [dataView, setDataView] = useState('campaigns');
  const [campaigns, setCampaigns] = useState([]);
  const [payouts, setPayouts] = useState([]);
  const [invoices, setInvoices] = useState([]);
  const [statusFilter, setStatusFilter] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Assuming the endpoints are correct. Adjust as necessary.
        const campaignsRes = await axios.get('/api/campaigns');
        setCampaigns(campaignsRes.data);

        const payoutsRes = await axios.get('/api/payouts');
        setPayouts(payoutsRes.data);

        const invoicesRes = await axios.get('/api/invoices');
        setInvoices(invoicesRes.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    fetchData();
  }, []);

  const renderCampaignRows = () => campaigns
    .filter(campaign => statusFilter ? campaign.status === statusFilter : true)
    .map((campaign) => (
      <TableRow key={campaign.id}>
        <TableCell>{campaign.name}</TableCell>
        <TableCell>${campaign.budget}</TableCell>
        <TableCell>{campaign.creators.join(', ')}</TableCell>
        <TableCell>{campaign.status}</TableCell>
      </TableRow>
    ));

  const renderPayoutRows = () => payouts
    .filter(payout => statusFilter ? payout.status === statusFilter : true)
    .map((payout) => (
      <TableRow key={payout.id}>
        <TableCell>{payout.creatorName}</TableCell>
        <TableCell>${payout.amount}</TableCell>
        <TableCell>{payout.status}</TableCell>
      </TableRow>
    ));

  const renderInvoiceRows = () => invoices
    .filter(invoice => statusFilter ? invoice.status === statusFilter : true)
    .map((invoice) => (
      <TableRow key={invoice.id}>
        <TableCell>{invoice.id}</TableCell>
        <TableCell>${invoice.amount}</TableCell>
        <TableCell>{invoice.status}</TableCell>
      </TableRow>
    ));

  const renderTableData = () => {
    switch (dataView) {
      case 'campaigns': return renderCampaignRows();
      case 'payouts': return renderPayoutRows();
      case 'invoices': return renderInvoiceRows();
      default: return null;
    }
  };

  return (
    <div className="dashboard">
      <Navbar />
      <div className="content" style={{ display: 'flex', justifyContent: 'space-between', marginTop: '20px' }}>
        <div className="left-column" style={{ width: '75%' }}>
          <Select value={dataView} onChange={(e) => setDataView(e.target.value)} style={{ marginBottom: '20px' }}>
            <MenuItem value="campaigns">Campaigns</MenuItem>
            <MenuItem value="payouts">Payouts</MenuItem>
            <MenuItem value="invoices">Invoices</MenuItem>
          </Select>

          <Select value={statusFilter} onChange={(e) => setStatusFilter(e.target.value)} style={{ marginBottom: '20px' }}>
            <MenuItem value="">All Statuses</MenuItem>
            <MenuItem value="Launched">Launched</MenuItem>
            <MenuItem value="Pending">Pending</MenuItem>
            <MenuItem value="Completed">Completed</MenuItem>
            <MenuItem value="Paid">Paid</MenuItem>
            <MenuItem value="Queued">Queued</MenuItem>
          </Select>

          <Table>
            <TableHead>
              <TableRow>
                {dataView === 'campaigns' && <>
                  <TableCell>Campaign Name</TableCell>
                  <TableCell>Budget</TableCell>
                  <TableCell>Creators</TableCell>
                  <TableCell>Status</TableCell>
                </>}
                {dataView === 'payouts' && <>
                  <TableCell>Creator Name</TableCell>
                  <TableCell>Amount</TableCell>
                  <TableCell>Status</TableCell>
                </>}
                {dataView === 'invoices' && <>
                  <TableCell>Invoice ID</TableCell>
                  <TableCell>Amount</TableCell>
                  <TableCell>Status</TableCell>
                </>}
              </TableRow>
            </TableHead>
            <TableBody>
              {renderTableData()}
            </TableBody>
          </Table>
        </div>
  
        {/* Right Column */}
        <div className="right-column" style={{ flex: '0 0 25%', paddingLeft: '10px' }}>
          {/* Total Balance */}
          <div className="balance">
            <h2>Client Name Summary</h2>
          </div>
          <div className="balance" style={{ marginBottom: '10px' }}>
            <h2>Total Balance</h2>
            <div style={{ fontSize: '24px', fontWeight: 'bold' }}>$12,000</div>
          </div>
          <div className="balance" style={{ marginBottom: '10px' }}>
            <h3>Total Spent</h3>
            <div style={{ fontSize: '18px', fontWeight: 'bold' }}>$5,000</div>
          </div>
          {/* Add to Balance Button */}
          <Button variant="contained" color="primary" style={{ marginBottom: '20px' }}>Add to Balance</Button>
  
          {/* Placeholder for Transaction History */}
          <div className="transaction-history-placeholder" style={{ height: '300px', backgroundColor: '#f0f0f0', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            Transaction History Placeholder
          </div>
          <div className="transaction-history-placeholder" style={{ height: '300px', backgroundColor: '#f0f0f0', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            Campaign Recaps Summary
          </div>
        </div>
      </div>
    </div>
  );  
};

export default Dashboard;
