import React, { useState, useEffect } from "react";
import Navbar from "../../../Components/Navbar/NavBar";
import "./dedInvoicing.css";
import {
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Button,
} from "@material-ui/core";
import { StyledTableRow, StyledTableCell } from "../../../Utils/styledcell";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import { useMutation, useQuery } from "react-query";
import client from "../../../API";

const Invoicing = () => {
  const [activeTab, setActiveTab] = useState("all");
  const [statusFilter, setStatusFilter] = useState("");
  const [invoices, setInvoices] = useState([]);
  const [filteredInvoices, setFilteredInvoices] = useState([]);
  const [campaignNames, setCampaignNames] = useState({});
  const [accountBalance, setAccountBalance] = useState("Loading...");

  // Fetch company balance when component mounts
  const { data: companyData, isSuccess: isCompanyDataSuccess } = useQuery(
    "fetchCompany", 
    () => client.companies.list(),
    {
      onSuccess: (data) => {
        const balance = parseFloat(data?.[0]?.balance); // Ensure data and balance exist, and parse balance to float
        if (!isNaN(balance)) {
          setAccountBalance(`$${balance}`);
        } else {
          setAccountBalance("Error loading balance");
        }
      }
    }
  );
  useEffect(() => {
    if (isCompanyDataSuccess && companyData && companyData.length > 0) {
      // Assuming the first company is the relevant one, adjust as needed
      const companyBalance = companyData[0].balance;
      setAccountBalance(`$${companyBalance}`);
    }
  }, [isCompanyDataSuccess, companyData]);

  const { mutate: fetchInvoices } = useMutation(client.invoices.list, {
    onSuccess: async (data) => {
      setInvoices(data.invoices); // Adjust based on actual API response

      const uniqueCampaignIds = [...new Set(data.invoices.map(inv => inv.campaign_id))];
const campaignsResponse = await Promise.all(
  uniqueCampaignIds.map(id =>
    fetch(`http://localhost:5000/api/campaigns/${id}`)
      .then(res => res.json())
      .catch(err => {
        console.error(`Failed to fetch campaign with ID ${id}:`, err);
        return null; // Or some error handling logic
      })
  )
);
const campaignNamesMap = {};
campaignsResponse.forEach(campaign => {
  if (campaign) { // Ensure the campaign isn't null from a failed fetch
    campaignNamesMap[campaign.id] = campaign.name;
  }
});
setCampaignNames(campaignNamesMap);

    },
    onError: (error) => {
      console.error("Error fetching invoices:", error);
    },
  });

  useEffect(() => {
    // Fetch all invoices from the backend
    fetchInvoices();
  }, []);

  useEffect(() => {
    // Filter invoices whenever the invoices list or statusFilter changes
    setFilteredInvoices(
      invoices.filter(
        (invoice) => statusFilter === "" || invoice.status === statusFilter
      )
    );
  }, [invoices, statusFilter]);

  const handleStatusFilterChange = (event) => {
    setStatusFilter(event.target.value);
  };

  const formatAmount = (amount) => {
    // Format the number to a currency format
    return `$${parseFloat(amount)
      .replace(/\d(?=(\d{3})+\.)/g, "$&,")}`;
  };

  // Calculations for the summary
  const totalInvoices = invoices.length;
  const totalAmountDue = invoices.reduce(
    (acc, invoice) => acc + parseFloat(invoice.amount_due),
    0
  );
  const invoicesByStatus = invoices.reduce((acc, invoice) => {
    acc[invoice.status] = (acc[invoice.status] || 0) + 1;
    return acc;
  }, {});

  return (
    <div className="dashboard">
      <Navbar />
      <div className="invoice-container">
        <div className="left-column">
          <h1 className="header">Invoicing</h1>
          <div className="button-container" style={{ marginBottom: "10px" }}>
            {/* Added "Create Payment" button */}
            <Button
              variant="contained"
              color="primary"
              style={{ marginRight: "10px" }}
            >
              Create Payment
            </Button>
            <FormControl fullWidth>
              <InputLabel>Status Filter</InputLabel>
              <Select
                value={statusFilter}
                onChange={handleStatusFilterChange}
                displayEmpty
                inputProps={{ "aria-label": "Without label" }}
              >
                <MenuItem value="">
                  <em>All</em>
                </MenuItem>
                <MenuItem value="Sent">Sent</MenuItem>
                <MenuItem value="Paid">Paid</MenuItem>
                <MenuItem value="Failed">Failed</MenuItem>
                <MenuItem value="Advance">Advance</MenuItem>
              </Select>
            </FormControl>
          </div>
          <TableContainer component={Paper}>
            <Table sx={{ minWidth: 700 }} aria-label="customized table">
              <TableHead>
                <TableRow>
                  <StyledTableCell align="center">ID</StyledTableCell>
                  <StyledTableCell align="center">
                    Campaign Name
                  </StyledTableCell>
                  <StyledTableCell align="center">PO Number</StyledTableCell>
                  <StyledTableCell align="center">Amount Due</StyledTableCell>
                  <StyledTableCell align="center">Status</StyledTableCell>
                  <StyledTableCell align="center">Created At</StyledTableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {filteredInvoices.map((invoice) => (
                  <StyledTableRow key={invoice.id}>
                    <StyledTableCell align="center">
                      {invoice.id}
                    </StyledTableCell>
                    <StyledTableCell align="center">
                      {campaignNames[invoice.campaign_id] || "Creator Payout"}
                    </StyledTableCell>
                    <StyledTableCell align="center">
                      {invoice.po_number || "N/A"}
                    </StyledTableCell>
                    <StyledTableCell align="center">
                      {formatAmount(invoice.amount_due)}
                    </StyledTableCell>
                    <StyledTableCell align="center">
                      {invoice.status}
                    </StyledTableCell>
                    <StyledTableCell align="center">
                      {new Date(invoice.created_at).toLocaleDateString()}
                    </StyledTableCell>
                  </StyledTableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </div>
        <div className="right-column">
          {/* Account Balance section */}
          <h2>Account Balance</h2>
          <p>{accountBalance}</p>

          {/* Summary section */}
          <h2>Summary</h2>
          <p>Total Invoices: {totalInvoices}</p>
          <p>Total Amount Due: {formatAmount(totalAmountDue)}</p>
          <h3>Invoices by Status:</h3>
          {Object.entries(invoicesByStatus).map(([status, count]) => (
            <p key={status}>
              {status}: {count}
            </p>
          ))}

          {/* Payment History placeholder */}
          <h2>Payment History</h2>
          <p>Placeholder for payment history data...</p>
        </div>
      </div>
    </div>
  );
};

export default Invoicing;
