import React, { useState, useEffect } from 'react';
import Navbar from '../../Components/Navbar/NavBar';
import './blitzpay.css';
import PayCreatorDialog from './payCreator'; // Assuming PayCreatorDialog is in the same directory

const BlitzPay = () => {
  const [activeTab, setActiveTab] = useState('pending');
  const [isPayCreatorDialogOpen, setIsPayCreatorDialogOpen] = useState(false);
  const [payouts, setPayouts] = useState([]);
  const [selectedPayouts, setSelectedPayouts] = useState([]);
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');
  
  useEffect(() => {
    fetch('http://localhost:5000/api/payouts')
      .then(response => response.json())
      .then(data => setPayouts(data))
      .catch(error => console.error('Error fetching payouts:', error));
  }, []);

  const handleTabClick = (tab) => {
    setActiveTab(tab);
    if (tab === 'pay') setIsPayCreatorDialogOpen(true);
  };

  const handleDialogClose = () => setIsPayCreatorDialogOpen(false);

  const handleDialogSubmit = (submissionData) => {
    fetch('http://localhost:5000/api/payouts', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(submissionData),
    })
    .then(response => response.json())
    .then(data => {
      console.log('Success:', data);
      handleDialogClose();
      // Optionally update state or UI here based on response
    })
    .catch(error => console.error('Error:', error));
  };
  const handleCheckboxChange = (payoutId) => {
    const newSelection = selectedPayouts.includes(payoutId)
      ? selectedPayouts.filter(id => id !== payoutId)
      : [...selectedPayouts, payoutId];
    setSelectedPayouts(newSelection);
  };

  const handleDeleteSelected = () => {
    fetch('http://localhost:5000/api/payouts/delete', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ payoutIds: selectedPayouts }),
    })
    .then(response => response.json())
    .then(data => {
      setIsModalOpen(true);
      setModalMessage('Selected payouts have been successfully deleted.');
      // Refresh payouts list or remove deleted payouts from state
    })
    .catch(error => {
      setIsModalOpen(true);
      setModalMessage('Error deleting payouts: ' + error.message);
    });
  };
  // Calculators
  const creatorsPaid = payouts.length;
  const amountInEscrow = payouts.reduce((acc, curr) => {
    if (curr.status === 'Pending') {
      // Remove any currency symbols and commas from the amount
      // then convert it to a number before adding it to the accumulator.
      const sanitizedAmount = Number(curr.amount.toString().replace(/[$,]/g, ''));
      return acc + sanitizedAmount;
    } else {
      return acc;
    }
  }, 0);
    const blitzPaysUsed = payouts.filter(payout => payout.blitzpay).length;

  return (
    <div className="dashboard">
      <Navbar />
      <div className="content flex-row">
        <div className="left-column">
          <h1 className='header'>Blitz Pay</h1>
          <div className="button-container">
            <button className="btn" onClick={() => handleTabClick('pending')}>Pending Transactions</button>
            <button className="btn" onClick={() => handleTabClick('all')}>All Transactions</button>
            <button className="btn" onClick={() => handleTabClick('recent')}>Recent Payments</button>
            <button className="btn" onClick={() => handleTabClick('pay')}>Pay a Creator</button>
          </div>
          <div className='table-container'>
            <table className="styled-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Creator Name</th>
                  <th>Campaign Name</th>
                  <th>Payout Date</th>
                  <th>Payment Status</th>
                  <th>BlitzPay</th>
                  <th>Amount</th>
                </tr>
              </thead>
              <tbody>
                {payouts.map(payout => (
                  <tr key={payout.id}>
                    <td>{payout.creator_id}</td>
                    <td>{payout.campaignname ? payout.campaignname : 'Creator Payout'}</td>
                    <td>{payout.payout_date}</td>
                    <td>{payout.status}</td>
                    <td>{payout.blitzpay ? 'true' : 'false'}</td>
                    <td>${payout.amount}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
        <div className="right-column">
          <h2>Summary</h2>
          <p>Creators Paid: {creatorsPaid}</p>
          <p>Amount in Escrow: ${amountInEscrow}</p>
          <p>BlitzPays Used: {blitzPaysUsed}</p>
        </div>
      </div>
      {isPayCreatorDialogOpen && (
        <PayCreatorDialog
          open={isPayCreatorDialogOpen}
          onClose={handleDialogClose}
          onSubmit={handleDialogSubmit}
        />
      )}
    </div>
  );
};

export default BlitzPay;
